  dataSource,
  request,
  queryOptions,
  rowKey = 'id',
  loading: propLoading,
  bordered = false,
  striped = false,
  hover = true,
  size = 'default',
  title,
  subtitle,
  toolbar,
  footer,
  search = true,
  pagination = true,
  rowSelection = false,
  expandable,
  scroll,
  sticky,
  onRow,
  onHeaderRow,
  className,
  style,
  editable = false,
  exportable = true,
  resizable = false,
  reorderable = false,
  virtualScroll = false,
  emptyText = 'No data',
  errorText = 'Failed to load data',
}: ProTableProps<T>) {
  // State
  const [sorting, setSorting] = React.useState<SortingState>([]);
  const [columnFilters, setColumnFilters] = React.useState<ColumnFiltersState>([]);
  const [columnVisibility, setColumnVisibility] = React.useState<VisibilityState>({});
  const [rowSelectionState, setRowSelectionState] = React.useState<RowSelectionState>({});
  const [expanded, setExpanded] = React.useState<ExpandedState>({});
  const [globalFilter, setGlobalFilter] = React.useState('');
  const [{ pageIndex, pageSize }, setPagination] = React.useState<PaginationState>({
    pageIndex: 0,
    pageSize: typeof pagination === 'object' ? pagination.pageSize || 10 : 10,
  });

  // Fetch data
  const { data: queryData, isLoading, isError, refetch } = useQuery({
    queryKey: ['proTable', pageIndex, pageSize, sorting, columnFilters, globalFilter],
    queryFn: async () => {
