     1/**
     2 * Core PWA Manager
     3 * Orchestrates all PWA functionality and platform detection
     4 */
     5
     6import { EventEmitter } from 'events';
     7import type { 
     8  PWAConfig, 
     9  PlatformInfo, 
    10  PWAInstallState, 
    11  PWAMetrics,
    12  InstallPromptEvent,
    13  ServiceWorkerMessage,
    14  PWAUpdateInfo
    15} from '../types';
    16import { DeviceDetector } from '../detection/device-detector';
    17import { CapabilityChecker } from '../detection/capability-checker';
    18import { ManifestGenerator } from './manifest-generator';
    19import { ServiceWorkerManager } from './service-worker';
    20import { InstallPromptManager } from './install-prompt';
    21import { NativeBridge } from '../bridge/native-bridge';
    22import { OfflineManager } from '../offline/offline-manager';
    23import { PushManager } from '../notifications/push-manager';
    24import { PWAAnalytics } from '../analytics/pwa-analytics';
    25import { MobileAdapter } from '../platforms/mobile-adapter';
    26import { DesktopAdapter } from '../platforms/desktop-adapter';
    27import { MetaverseAdapter } from '../platforms/metaverse-adapter';
    28
    29export class PWAManager extends EventEmitter {
    30  private config: PWAConfig;
    31  private deviceDetector: DeviceDetector;
    32  private capabilityChecker: CapabilityChecker;
    33  private manifestGenerator: ManifestGenerator;
    34  private serviceWorker: ServiceWorkerManager;
    35  private installPrompt: InstallPromptManager;
    36  private nativeBridge: NativeBridge;
    37  private offlineManager: OfflineManager;
    38  private pushManager: PushManager;
    39  private analytics: PWAAnalytics;
    40  private platformAdapter: MobileAdapter | DesktopAdapter | MetaverseAdapter | null = null;
    41  
    42  private platformInfo: PlatformInfo | null = null;
    43  private installState: PWAInstallState = {
    44    canInstall: false,
    45    isInstalled: false,
    46    isStandalone: false,
    47    nativeAppAvailable: false
    48  };
    49  
    50  private metrics: PWAMetrics = {
    51    launchCount: 0,
    52    sessionDuration: 0,
    53    totalUsageTime: 0,
    54    offlineUsageTime: 0,
    55    dataUsage: { cached: 0, network: 0, total: 0 },
    56    performance: { loadTime: 0, renderTime: 0, interactionTime: 0 },
    57    engagement: { 
    58      pushOptIn: false, 
    59      notificationInteractions: 0, 
    60      shareCount: 0, 
    61      addToHomeScreen: false 
    62    }
    63  };
    64
    65  constructor(config: PWAConfig) {
    66    super();
    67    this.config = config;
    68    
    69    // Initialize core components
    70    this.deviceDetector = new DeviceDetector();
    71    this.capabilityChecker = new CapabilityChecker();
    72    this.manifestGenerator = new ManifestGenerator(config);
    73    this.serviceWorker = new ServiceWorkerManager(config);
    74    this.installPrompt = new InstallPromptManager(config);
    75    this.nativeBridge = new NativeBridge();
    76    this.offlineManager = new OfflineManager(config);
    77    this.pushManager = new PushManager(config);
    78    this.analytics = new PWAAnalytics(config);
    79  }
    80
    81  /**
    82   * Initialize the PWA
    83   */
    84  async initialize(): Promise<void> {
    85    try {
    86      // Detect platform and capabilities
    87      await this.detectPlatform();
    88      
    89      // Generate and inject manifest
    90      await this.setupManifest();
    91      
    92      // Register service worker
    93      await this.registerServiceWorker();
    94      
    95      // Setup platform-specific adapter
    96      await this.setupPlatformAdapter();
    97      
    98      // Initialize install prompt handling
    99      await this.setupInstallPrompt();
   100      
   101      // Setup offline functionality
   102      await this.setupOfflineSupport();
   103      
   104      // Initialize push notifications if enabled
   105      if (this.config.enableNotifications) {
   106        await this.setupPushNotifications();
   107      }
   108      
   109      // Start analytics tracking
   110      if (this.config.analytics.enabled) {
   111        await this.startAnalytics();
   112      }
   113      
   114      // Check for native app availability
   115      await this.checkNativeApp();
   116      
   117      // Handle app updates
   118      await this.checkForUpdates();
   119      
   120      // Emit ready event
   121      this.emit('ready', {
   122        platform: this.platformInfo,
   123        installState: this.installState,
   124        metrics: this.metrics
   125      });
   126      
   127      console.log(' Katalyst PWA initialized successfully');
   128    } catch (error) {
   129      console.error('Failed to initialize PWA:', error);
   130      this.emit('error', error);
   131    }
   132  }
   133
   134  /**
   135   * Detect platform and device capabilities
   136   */
   137  private async detectPlatform(): Promise<void> {
   138    this.platformInfo = await this.deviceDetector.detect();
   139    
   140    // Check if running as installed PWA
   141    const isStandalone = window.matchMedia('(display-mode: standalone)').matches ||
   142                        window.navigator.standalone ||
   143                        document.referrer.includes('android-app://');
   144    
   145    this.installState.isStandalone = isStandalone;
   146    this.installState.isInstalled = isStandalone;
   147    
   148    // Emit platform detected event
   149    this.emit('platform-detected', this.platformInfo);
   150  }
   151
   152  /**
   153   * Setup and inject manifest
   154   */
   155  private async setupManifest(): Promise<void> {
   156    const manifest = await this.manifestGenerator.generate(this.platformInfo!);
   157    
   158    // Create manifest blob and URL
   159    const manifestBlob = new Blob([JSON.stringify(manifest, null, 2)], {
   160      type: 'application/manifest+json'
   161    });
   162    const manifestUrl = URL.createObjectURL(manifestBlob);
   163    
   164    // Inject manifest link
   165    const link = document.createElement('link');
   166    link.rel = 'manifest';
   167    link.href = manifestUrl;
   168    document.head.appendChild(link);
   169    
   170    // Set theme color
   171    const themeColorMeta = document.createElement('meta');
   172    themeColorMeta.name = 'theme-color';
   173    themeColorMeta.content = this.config.themeColor;
   174    document.head.appendChild(themeColorMeta);
   175    
   176    // Set viewport for mobile
   177    if (!document.querySelector('meta[name="viewport"]')) {
   178      const viewportMeta = document.createElement('meta');
   179      viewportMeta.name = 'viewport';
   180      viewportMeta.content = 'width=device-width, initial-scale=1, maximum-scale=5, user-scalable=yes';
   181      document.head.appendChild(viewportMeta);
   182    }
   183    
   184    // Apple-specific meta tags
   185    if (this.platformInfo?.os === 'ios') {
   186      this.setupAppleMeta();
   187    }
   188  }
   189
   190  /**
   191   * Setup Apple-specific meta tags
   192   */
   193  private setupAppleMeta(): void {
   194    // Apple touch icon
   195    const appleTouchIcon = document.createElement('link');
   196    appleTouchIcon.rel = 'apple-touch-icon';
   197    appleTouchIcon.href = this.config.icons.find(i => i.sizes === '180x180')?.src || this.config.icons[0].src;
   198    document.head.appendChild(appleTouchIcon);
   199    
   200    // Apple mobile web app capable
   201    const appleWebApp = document.createElement('meta');
   202    appleWebApp.name = 'apple-mobile-web-app-capable';
   203    appleWebApp.content = 'yes';
   204    document.head.appendChild(appleWebApp);
   205    
   206    // Apple status bar style
   207    const appleStatusBar = document.createElement('meta');
   208    appleStatusBar.name = 'apple-mobile-web-app-status-bar-style';
   209    appleStatusBar.content = this.config.platforms?.mobile?.statusBarStyle || 'default';
   210    document.head.appendChild(appleStatusBar);
   211    
   212    // Apple title
   213    const appleTitle = document.createElement('meta');
   214    appleTitle.name = 'apple-mobile-web-app-title';
   215    appleTitle.content = this.config.shortName;
   216    document.head.appendChild(appleTitle);
   217  }
   218
   219  /**
   220   * Register service worker
   221   */
   222  private async registerServiceWorker(): Promise<void> {
   223    if (!('serviceWorker' in navigator)) {
   224      console.warn('Service Worker not supported');
   225      return;
   226    }
   227    
   228    try {
   229      const registration = await this.serviceWorker.register();
   230      
   231      // Listen for updates
   232      registration.addEventListener('updatefound', () => {
   233        this.handleServiceWorkerUpdate(registration);
   234      });
   235      
   236      // Listen for messages from service worker
   237      navigator.serviceWorker.addEventListener('message', (event) => {
   238        this.handleServiceWorkerMessage(event.data);
   239      });
   240      
   241      console.log('Service Worker registered successfully');
   242    } catch (error) {
   243      console.error('Service Worker registration failed:', error);
   244    }
   245  }
   246
   247  /**
   248   * Setup platform-specific adapter
   249   */
   250  private async setupPlatformAdapter(): Promise<void> {
   251    if (!this.platformInfo) return;
   252    
   253    switch (this.platformInfo.device) {
   254      case 'mobile':
   255      case 'tablet':
   256        this.platformAdapter = new MobileAdapter(this.config, this.platformInfo);
   257        break;
   258      case 'desktop':
   259        this.platformAdapter = new DesktopAdapter(this.config, this.platformInfo);
   260        break;
   261      case 'vr':
   262      case 'ar':
   263        this.platformAdapter = new MetaverseAdapter(this.config, this.platformInfo);
   264        break;
   265    }
   266    
   267    if (this.platformAdapter) {
   268      await this.platformAdapter.initialize();
   269      
   270      // Apply platform-specific optimizations
   271      await this.platformAdapter.optimize();
   272    }
   273  }
   274
   275  /**
   276   * Setup install prompt handling
   277   */
   278  private async setupInstallPrompt(): Promise<void> {
   279    // Listen for beforeinstallprompt event
   280    window.addEventListener('beforeinstallprompt', (event: Event) => {
   281      event.preventDefault();
   282      const promptEvent = event as InstallPromptEvent;
   283      
   284      this.installState.canInstall = true;
   285      this.installState.deferredPrompt = promptEvent;
   286      
   287      // Check if we should suggest native app instead
   288      if (this.config.suggestNativeApp && this.installState.nativeAppAvailable) {
   289        this.suggestNativeApp();
   290      } else {
   291        this.emit('install-available', this.installState);
   292      }
   293    });
   294    
   295    // Listen for app installed event
   296    window.addEventListener('appinstalled', () => {
   297      this.installState.isInstalled = true;
   298      this.installState.canInstall = false;
   299      this.metrics.engagement.addToHomeScreen = true;
   300      
   301      this.emit('app-installed');
   302      this.analytics.trackEvent('pwa_installed');
   303    });
   304    
   305    // Check if already installed
   306    if (window.matchMedia('(display-mode: standalone)').matches) {
   307      this.installState.isInstalled = true;
   308      this.installState.isStandalone = true;
   309    }
   310  }
   311
   312  /**
   313   * Setup offline support
   314   */
   315  private async setupOfflineSupport(): Promise<void> {
   316    await this.offlineManager.initialize();
   317    
   318    // Listen for online/offline events
   319    window.addEventListener('online', () => {
   320      this.emit('online');
   321      this.offlineManager.syncQueue();
   322    });
   323    
   324    window.addEventListener('offline', () => {
   325      this.emit('offline');
   326    });
   327  }
   328
   329  /**
   330   * Setup push notifications
   331   */
   332  private async setupPushNotifications(): Promise<void> {
   333    const permission = await this.pushManager.requestPermission();
   334    
   335    if (permission === 'granted') {
   336      const subscription = await this.pushManager.subscribe();
   337      this.metrics.engagement.pushOptIn = true;
   338      
   339      this.emit('push-subscribed', subscription);
   340    }
   341  }
   342
   343  /**
   344   * Start analytics tracking
   345   */
   346  private async startAnalytics(): Promise<void> {
   347    await this.analytics.initialize();
   348    
   349    // Track launch
   350    this.metrics.launchCount++;
   351    this.metrics.lastLaunchTime = Date.now();
   352    this.analytics.trackEvent('pwa_launch', {
   353      platform: this.platformInfo,
   354      installState: this.installState
   355    });
   356    
   357    // Track session
   358    const sessionStart = Date.now();
   359    window.addEventListener('beforeunload', () => {
   360      const sessionDuration = Date.now() - sessionStart;
   361      this.metrics.sessionDuration = sessionDuration;
   362      this.metrics.totalUsageTime += sessionDuration;
   363      
   364      this.analytics.trackEvent('pwa_session', {
   365        duration: sessionDuration,
   366        metrics: this.metrics
   367      });
   368    });
   369  }
   370
   371  /**
   372   * Check for native app availability
   373   */
   374  private async checkNativeApp(): Promise<void> {
   375    if (!this.config.detectDevice || !this.platformInfo) return;
   376    
   377    const nativeAppInfo = await this.nativeBridge.checkNativeApp(this.platformInfo);
   378    
   379    if (nativeAppInfo?.available) {
   380      this.installState.nativeAppAvailable = true;
   381      this.installState.nativeAppInfo = nativeAppInfo;
   382      
   383      // Determine suggested platform
   384      if (this.shouldSuggestNative()) {
   385        this.installState.suggestedPlatform = 'native';
   386        
   387        // Show native app prompt after delay
   388        if (this.config.forceNativePrompt) {
   389          setTimeout(() => {
   390            this.suggestNativeApp();
   391          }, this.config.nativeAppDelay || 5000);
   392        }
   393      } else {
   394        this.installState.suggestedPlatform = 'pwa';
   395      }
   396    }
   397  }
   398
   399  /**
   400   * Determine if native app should be suggested
   401   */
   402  private shouldSuggestNative(): boolean {
   403    if (!this.platformInfo || !this.installState.nativeAppInfo) return false;
   404    
   405    // Check platform-specific criteria
   406    const { device, os, capabilities } = this.platformInfo;
   407    const nativeFeatures = this.installState.nativeAppInfo.features || [];
   408    
   409    // Prefer native on mobile for better performance
   410    if (device === 'mobile' || device === 'tablet') {
   411      // Check if native app has features PWA can't provide
   412      if (nativeFeatures.includes('biometrics') && capabilities.biometrics !== 'none') {
   413        return true;
   414      }
   415      if (nativeFeatures.includes('ar') && capabilities.ar) {
   416        return true;
   417      }
   418      if (nativeFeatures.includes('background-location')) {
   419        return true;
   420      }
   421    }
   422    
   423    // Prefer native on desktop for better integration
   424    if (device === 'desktop') {
   425      if (nativeFeatures.includes('file-system')) {
   426        return true;
   427      }
   428      if (nativeFeatures.includes('system-tray')) {
   429        return true;
   430      }
   431    }
   432    
   433    // Prefer native for VR/AR
   434    if (device === 'vr' || device === 'ar') {
   435      return true;
   436    }
   437    
   438    return false;
   439  }
   440
   441  /**
   442   * Suggest native app installation
   443   */
   444  private suggestNativeApp(): void {
   445    if (!this.installState.nativeAppInfo) return;
   446    
   447    this.emit('suggest-native', {
   448      appInfo: this.installState.nativeAppInfo,
   449      platform: this.platformInfo,
   450      installInstructions: this.getInstallInstructions()
   451    });
   452  }
   453
   454  /**
   455   * Get platform-specific install instructions
   456   */
   457  private getInstallInstructions(): string[] {
   458    const instructions: string[] = [];
   459    const { os } = this.platformInfo!;
   460    const { nativeAppInfo } = this.installState;
   461    
   462    if (!nativeAppInfo) {
   463      // PWA install instructions
   464      if (os === 'ios') {
   465        instructions.push('Tap the Share button');
   466        instructions.push('Scroll down and tap "Add to Home Screen"');
   467        instructions.push('Tap "Add" to install');
   468      } else if (os === 'android') {
   469        instructions.push('Tap the menu button ()');
   470        instructions.push('Select "Install app" or "Add to Home Screen"');
   471        instructions.push('Tap "Install" to confirm');
   472      } else {
   473        instructions.push('Click the install button in the address bar');
   474        instructions.push('Click "Install" to add to your device');
   475      }
   476    } else {
   477      // Native app install instructions
   478      if (os === 'ios') {
   479        instructions.push('Tap "View in App Store"');
   480        instructions.push('Tap "Get" to download');
   481        instructions.push('Open the app after installation');
   482      } else if (os === 'android') {
   483        instructions.push('Tap "View in Play Store"');
   484        instructions.push('Tap "Install" to download');
   485        instructions.push('Tap "Open" after installation');
   486      } else if (os === 'windows') {
   487        instructions.push('Click "Get from Microsoft Store"');
   488        instructions.push('Click "Get" to install');
   489        instructions.push('Launch from Start menu');
   490      } else if (os === 'macos') {
   491        instructions.push('Click "View in Mac App Store"');
   492        instructions.push('Click "Get" to download');
   493        instructions.push('Open from Applications');
   494      }
   495    }
   496    
   497    this.installState.installInstructions = instructions;
   498    return instructions;
   499  }
   500
   501  /**
   502   * Check for app updates
   503   */
   504  private async checkForUpdates(): Promise<void> {
   505    // Check service worker for updates
   506    const swUpdate = await this.serviceWorker.checkForUpdate();
   507    
   508    if (swUpdate) {
   509      const updateInfo: PWAUpdateInfo = {
   510        available: true,
   511        version: this.config.version,
   512        critical: false,
   513        autoUpdate: true
   514      };
   515      
   516      this.emit('update-available', updateInfo);
   517    }
   518    
   519    // Check native app for updates if available
   520    if (this.installState.nativeAppInfo) {
   521      const nativeUpdate = await this.nativeBridge.checkForUpdate(
   522        this.installState.nativeAppInfo
   523      );
   524      
   525      if (nativeUpdate) {
   526        this.emit('native-update-available', nativeUpdate);
   527      }
   528    }
   529  }
   530
   531  /**
   532   * Handle service worker update
   533   */
   534  private handleServiceWorkerUpdate(registration: ServiceWorkerRegistration): void {
   535    const newWorker = registration.installing;
   536    
   537    if (newWorker) {
   538      newWorker.addEventListener('statechange', () => {
   539        if (newWorker.state === 'installed' && navigator.serviceWorker.controller) {
   540          // New service worker available
   541          this.emit('update-ready');
   542        }
   543      });
   544    }
   545  }
   546
   547  /**
   548   * Handle service worker messages
   549   */
   550  private handleServiceWorkerMessage(message: ServiceWorkerMessage): void {
   551    switch (message.type) {
   552      case 'cache-updated':
   553        this.metrics.dataUsage.cached = message.data.size;
   554        break;
   555      case 'network-request':
   556        this.metrics.dataUsage.network += message.data.size;
   557        break;
   558      case 'offline-ready':
   559        this.emit('offline-ready');
   560        break;
   561      case 'sync-complete':
   562        this.emit('sync-complete', message.data);
   563        break;
   564      default:
   565        this.emit('sw-message', message);
   566    }
   567  }
   568
   569  /**
   570   * Prompt for PWA installation
   571   */
   572  async promptInstall(): Promise<boolean> {
   573    if (!this.installState.canInstall || !this.installState.deferredPrompt) {
   574      return false;
   575    }
   576    
   577    try {
   578      await this.installState.deferredPrompt.prompt();
   579      const { outcome } = await this.installState.deferredPrompt.userChoice;
   580      
   581      if (outcome === 'accepted') {
   582        this.analytics.trackEvent('pwa_install_accepted');
   583        return true;
   584      } else {
   585        this.analytics.trackEvent('pwa_install_dismissed');
   586        return false;
   587      }
   588    } catch (error) {
   589      console.error('Install prompt failed:', error);
   590      return false;
   591    }
   592  }
   593
   594  /**
   595   * Open native app or store
   596   */
   597  async openNativeApp(): Promise<void> {
   598    if (!this.installState.nativeAppInfo) return;
   599    
   600    try {
   601      // Try deep link first
   602      await this.nativeBridge.openDeepLink(this.installState.nativeAppInfo.deepLink);
   603    } catch {
   604      // Fallback to store URL
   605      window.open(this.installState.nativeAppInfo.storeUrl, '_blank');
   606    }
   607    
   608    this.analytics.trackEvent('native_app_opened');
   609  }
   610
   611  /**
   612   * Get current PWA state
   613   */
   614  getState(): {
   615    platform: PlatformInfo | null;
   616    install: PWAInstallState;
   617    metrics: PWAMetrics;
   618  } {
   619    return {
   620      platform: this.platformInfo,
   621      install: this.installState,
   622      metrics: this.metrics
   623    };
   624  }
   625
   626  /**
   627   * Update PWA configuration
   628   */
   629  updateConfig(config: Partial<PWAConfig>): void {
   630    this.config = { ...this.config, ...config };
   631    this.emit('config-updated', this.config);
   632  }
   633
   634  /**
   635   * Destroy PWA manager
   636   */
   637  destroy(): void {
   638    this.serviceWorker.unregister();
   639    this.offlineManager.destroy();
   640    this.pushManager.unsubscribe();
   641    this.analytics.destroy();
   642    this.removeAllListeners();
   643  }
   644}

