   483  /**
   484   * Attach prompt event handlers
   485   */
   486  private attachPromptHandlers(onInstallPWA: () => void, onInstallNative: () => void): void {
   487    // Close button
   488    document.getElementById('katalyst-prompt-close')?.addEventListener('click', () => {
   489      this.hidePrompt();
   490    });
   491    
   492    // Install native button
   493    document.getElementById('katalyst-install-native')?.addEventListener('click', () => {
   494      onInstallNative();
   495      this.hidePrompt();
   496    });
   497    
   498    // Install PWA button
   499    document.getElementById('katalyst-install-pwa')?.addEventListener('click', () => {
   500      onInstallPWA();
   501      this.hidePrompt();
   502    });
   503  }
   504
   505  /**
   506   * Attach PWA prompt handlers
   507   */
   508  private attachPWAHandlers(onInstall: () => void, onDismiss: () => void): void {
   509    // Close button
   510    document.getElementById('katalyst-prompt-close')?.addEventListener('click', () => {
   511      onDismiss();
   512      this.hidePrompt();
   513    });
   514    
   515    // Install button
   516    document.getElementById('katalyst-install-pwa')?.addEventListener('click', () => {
   517      onInstall();
   518      this.hidePrompt();
   519    });
   520    
   521    // Later button
   522    document.getElementById('katalyst-prompt-later')?.addEventListener('click', () => {
   523      onDismiss();
   524      this.hidePrompt();
   525    });
   526  }
   527
   528  /**
   529   * Hide the prompt
   530   */
   531  hidePrompt(): void {
   532    if (!this.promptContainer) return;
   533    
   534    this.promptContainer.classList.remove('katalyst-prompt-visible');
   535    
   536    setTimeout(() => {
   537      if (this.promptContainer) {
   538        this.promptContainer.remove();
   539        this.promptContainer = null;
   540      }
   541      this.isPromptShowing = false;
   542    }, 300);
   543  }
   544
   545  /**
   546   * Format feature name
   547   */
   548  private formatFeature(feature: string): string {
   549    const featureMap: Record<string, string> = {
   550      'biometrics': ' Biometric Login',
   551      'ar': ' AR Features',
   552      'vr': ' VR Support',
   553      'background-location': ' Location Services',
   554      'file-system': ' File Access',
   555      'system-tray': ' System Integration',
   556      'push-notifications': ' Notifications',
   557      'offline': ' Offline Mode',
   558      'sync': ' Auto Sync',
   559      'share': ' Native Sharing'
   560    };
   561    
   562    return featureMap[feature] || feature;
   563  }
   564
   565  /**
   566   * Get native advantage text
   567   */
   568  private getNativeAdvantage(platform: PlatformInfo): string {
   569    const { device, os } = platform;
   570    
   571    if (device === 'mobile' || device === 'tablet') {
   572      if (os === 'ios') return 'Optimized for iOS';
   573      if (os === 'android') return 'Material Design';
   574      return 'Native performance';
   575    }
   576    
   577    if (device === 'desktop') {
   578      if (os === 'windows') return 'Windows integration';
   579      if (os === 'macos') return 'macOS native';
   580      return 'Desktop features';
   581    }
   582    
   583    return 'Enhanced features';
   584  }
   585
   586  /**
   587   * Get native install button text
   588   */
   589  private getNativeButtonText(os: PlatformOS): string {
   590    const buttonText: Record<string, string> = {
   591      'ios': 'Get from App Store',
   592      'android': 'Get from Play Store',
   593      'windows': 'Get from Microsoft Store',
   594      'macos': 'Get from Mac App Store',
   595      'linux': 'Download App'
   596    };
   597    
   598    return buttonText[os] || 'Download Native App';
   599  }
   600
   601  /**
   602   * Get PWA install button text
   603   */
   604  private getPWAButtonText(platform: PlatformInfo): string {
   605    if (platform.browser === 'safari' || platform.browser === 'firefox') {
   606      return 'View Instructions';
   607    }
   608    return 'Install Now';
   609  }
   610}

