     1/**
     2 * Device Detection Module
     3 * Comprehensive device and platform detection
     4 */
     5
     6import type { DeviceType, PlatformOS, BrowserType, PlatformInfo, DeviceCapabilities } from '../types';
     7
     8export class DeviceDetector {
     9  private userAgent: string;
    10  private platform: string;
    11  private vendor: string;
    12  
    13  constructor() {
    14    this.userAgent = navigator.userAgent.toLowerCase();
    15    this.platform = navigator.platform.toLowerCase();
    16    this.vendor = navigator.vendor?.toLowerCase() || '';
    17  }
    18
    19  /**
    20   * Detect comprehensive platform information
    21   */
    22  async detect(): Promise<PlatformInfo> {
    23    const device = this.detectDevice();
    24    const os = this.detectOS();
    25    const osVersion = this.detectOSVersion(os);
    26    const browser = this.detectBrowser();
    27    const browserVersion = this.detectBrowserVersion(browser);
    28    const isWebView = this.detectWebView();
    29    const isPWA = this.detectPWA();
    30    const isStandalone = this.detectStandalone();
    31    const capabilities = await this.detectCapabilities();
    32
    33    return {
    34      device,
    35      os,
    36      osVersion,
    37      browser,
    38      browserVersion,
    39      isWebView,
    40      isPWA,
    41      isStandalone,
    42      capabilities
    43    };
    44  }
    45
    46  /**
    47   * Detect device type
    48   */
    49  private detectDevice(): DeviceType {
    50    // VR/AR Detection
    51    if ('xr' in navigator || 'getVRDisplays' in navigator) {
    52      if (this.userAgent.includes('quest') || this.userAgent.includes('oculus')) {
    53        return 'vr';
    54      }
    55      if (this.userAgent.includes('hololens') || this.userAgent.includes('magic leap')) {
    56        return 'ar';
    57      }
    58    }
    59
    60    // TV Detection
    61    if (this.userAgent.includes('tv') || this.userAgent.includes('crkey') || 
    62        this.userAgent.includes('roku') || this.userAgent.includes('appletv')) {
    63      return 'tv';
    64    }
    65
    66    // Watch Detection
    67    if (this.userAgent.includes('watch')) {
    68      return 'watch';
    69    }
    70
    71    // Mobile/Tablet Detection
    72    const isMobile = /mobile|android|iphone|ipod|blackberry|opera mini|iemobile/i.test(this.userAgent);
    73    const isTablet = /tablet|ipad|playbook|silk|kindle|gt-p|gt-n|sch-i|sm-t|sm-p/i.test(this.userAgent);
    74    
    75    if (isTablet) {
    76      return 'tablet';
    77    }
    78    
    79    if (isMobile) {
    80      // Additional mobile checks
    81      const hasTouch = 'ontouchstart' in window || navigator.maxTouchPoints > 0;
    82      const screenWidth = window.screen.width;
    83      const screenHeight = window.screen.height;
    84      const minDimension = Math.min(screenWidth, screenHeight);
    85      
    86      // Tablets typically have min dimension > 600px
    87      if (hasTouch && minDimension > 600) {
    88        return 'tablet';
    89      }
    90      
    91      return 'mobile';
    92    }
    93
    94    // Desktop is default
    95    return 'desktop';
    96  }
    97
    98  /**
    99   * Detect operating system
   100   */
   101  private detectOS(): PlatformOS {
   102    // iOS Detection
   103    if (/iphone|ipad|ipod/.test(this.userAgent) || 
   104        (this.platform.includes('mac') && 'ontouchend' in document)) {
   105      return 'ios';
   106    }
   107
   108    // Android Detection
   109    if (/android/.test(this.userAgent)) {
   110      return 'android';
   111    }
   112
   113    // Windows Detection
   114    if (/windows|win32|win64/.test(this.platform) || /windows/.test(this.userAgent)) {
   115      return 'windows';
   116    }
   117
   118    // macOS Detection
   119    if (/macintosh|mac os x|macos/.test(this.platform) || /macintosh/.test(this.userAgent)) {
   120      return 'macos';
   121    }
   122
   123    // Linux Detection
   124    if (/linux/.test(this.platform) || /linux/.test(this.userAgent)) {
   125      // Chrome OS check
   126      if (/cros/.test(this.userAgent)) {
   127        return 'chromeos';
   128      }
   129      return 'linux';
   130    }
   131
   132    return 'unknown';
   133  }
   134
   135  /**
   136   * Detect OS version
   137   */
   138  private detectOSVersion(os: PlatformOS): string {
   139    let version = 'unknown';
   140
   141    switch (os) {
   142      case 'ios': {
   143        const match = this.userAgent.match(/os ([\d_]+)/);
   144        if (match) {
   145          version = match[1].replace(/_/g, '.');
   146        }
   147        break;
   148      }
   149      case 'android': {
   150        const match = this.userAgent.match(/android ([\d.]+)/);
   151        if (match) {
   152          version = match[1];
   153        }
   154        break;
   155      }
   156      case 'windows': {
   157        const match = this.userAgent.match(/windows nt ([\d.]+)/);
   158        if (match) {
   159          const ntVersion = match[1];
   160          // Map NT version to Windows version
   161          const versionMap: Record<string, string> = {
   162            '10.0': '10/11',
   163            '6.3': '8.1',
   164            '6.2': '8',
   165            '6.1': '7',
   166            '6.0': 'Vista',
   167            '5.1': 'XP'
   168          };
   169          version = versionMap[ntVersion] || ntVersion;
   170        }
   171        break;
   172      }
   173      case 'macos': {
   174        const match = this.userAgent.match(/mac os x ([\d_]+)/);
   175        if (match) {
   176          version = match[1].replace(/_/g, '.');
   177        }
   178        break;
   179      }
   180    }
   181
   182    return version;
   183  }
   184
   185  /**
   186   * Detect browser type
   187   */
   188  private detectBrowser(): BrowserType {
   189    // Edge Detection (must be before Chrome)
   190    if (/edg\//.test(this.userAgent)) {
   191      return 'edge';
   192    }
   193
   194    // Opera Detection (must be before Chrome)
   195    if (/opr\/|opera/.test(this.userAgent)) {
   196      return 'opera';
   197    }
   198
   199    // Samsung Browser Detection
   200    if (/samsungbrowser/.test(this.userAgent)) {
   201      return 'samsung';
   202    }
   203
   204    // Chrome Detection
   205    if (/chrome|chromium|crios/.test(this.userAgent) && this.vendor.includes('google')) {
   206      return 'chrome';
   207    }
   208
   209    // Firefox Detection
   210    if (/firefox|fxios/.test(this.userAgent)) {
   211      return 'firefox';
   212    }
   213
   214    // Safari Detection
   215    if (/safari/.test(this.userAgent) && this.vendor.includes('apple')) {
   216      return 'safari';
   217    }
   218
   219    return 'unknown';
   220  }
   221
   222  /**
   223   * Detect browser version
   224   */
   225  private detectBrowserVersion(browser: BrowserType): string {
   226    let version = 'unknown';
   227    let match: RegExpMatchArray | null = null;
   228
   229    switch (browser) {
   230      case 'chrome':
   231        match = this.userAgent.match(/(?:chrome|chromium|crios)\/([\d.]+)/);
   232        break;
   233      case 'firefox':
   234        match = this.userAgent.match(/(?:firefox|fxios)\/([\d.]+)/);
   235        break;
   236      case 'safari':
   237        match = this.userAgent.match(/version\/([\d.]+)/);
   238        break;
   239      case 'edge':
   240        match = this.userAgent.match(/edg\/([\d.]+)/);
   241        break;
   242      case 'opera':
