   311        height: window.screen.height * (window.devicePixelRatio || 1)
   312      },
   313      pixelDensity: window.devicePixelRatio || 1,
   314      colorGamut: this.detectColorGamut(),
   315      hdr: this.detectHDR(),
   316      refreshRate: await this.detectRefreshRate(),
   317      
   318      // Input
   319      keyboard: !('ontouchstart' in window) || window.innerWidth > 768,
   320      mouse: window.matchMedia('(pointer: fine)').matches,
   321      stylus: window.matchMedia('(pointer: fine) and (hover: none)').matches,
   322      gamepad: 'getGamepads' in navigator,
   323      
   324      // Sensors
