   326      gyroscope: 'Gyroscope' in window,
   327      magnetometer: 'Magnetometer' in window,
   328      ambientLight: 'AmbientLightSensor' in window,
   329      proximity: 'ProximitySensor' in window,
   330      
   331      // Connectivity
