   350      ar: 'xr' in navigator && await this.checkXRSupport('immersive-ar'),
   351      vr: 'xr' in navigator && await this.checkXRSupport('immersive-vr'),
   352      ml: 'ml' in navigator || 'neuralNetwork' in window
   353    };
   354
   355    return capabilities;
   356  }
   357
   358  /**
   359   * Detect color gamut support
   360   */
   361  private detectColorGamut(): 'srgb' | 'p3' | 'rec2020' {
   362    if (window.matchMedia('(color-gamut: rec2020)').matches) {
   363      return 'rec2020';
   364    }
   365    if (window.matchMedia('(color-gamut: p3)').matches) {
