   426  private detectBiometrics(): 'none' | 'fingerprint' | 'face' | 'iris' {
   427    // Check for WebAuthn support
   428    if ('credentials' in navigator && 'create' in navigator.credentials) {
   429      // iOS Face ID
   430      if (/iphone|ipad/.test(this.userAgent) && parseFloat(this.detectOSVersion('ios')) >= 11) {
   431        return 'face';
   432      }
   433      
   434      // Android with fingerprint
   435      if (/android/.test(this.userAgent)) {
   436        return 'fingerprint';
   437      }
   438      
   439      // Windows Hello
   440      if (/windows/.test(this.userAgent)) {
