/**
 * Deployment Manifest System
 * Comprehensive platform-specific deployment configurations
 */

import type { PlatformOS, DeviceType } from '../types';

// ============================================================================
// Deployment Types
// ============================================================================

export interface DeploymentManifest {
  version: string;
  lastUpdated: string;
  platforms: {
    android: AndroidDeployment;
    ios: IOSDeployment;
    windows: WindowsDeployment;
    macos: MacOSDeployment;
    linux: LinuxDeployment;
  };
  features: PlatformFeatures;
  requirements: PlatformRequirements;
  analytics: DeploymentAnalytics;
}

export interface AndroidDeployment {
  twa: {
    enabled: boolean;
    packageName: string;
    sha256Fingerprints: string[];
    launcherActivity: string;
    hostName: string;
    defaultUrl: string;
    assetStatements: string;
  };
  playStore: {
    url: string;
    appId: string;
    minVersion: string;
    instantApp?: boolean;
  };
  directDownload: {
    apkUrl: string;
    apkSize: number;
    versionCode: number;
    versionName: string;
    sha256: string;
  };
  amazonAppStore?: {
    url: string;
    asin: string;
  };
  features: string[];
  permissions: string[];
}

export interface IOSDeployment {
  appStore: {
    url: string;
    appId: string;
    bundleId: string;
    teamId: string;
    minIOSVersion: string;
  };
  testFlight?: {
    enabled: boolean;
    publicLink: string;
    betaGroupId: string;
  };
  appClip?: {
    enabled: boolean;
    url: string;
    invocationUrl: string;
    bundleId: string;
    minimumVersion: string;
  };
  enterprise?: {
    enabled: boolean;
    manifestUrl: string;
    bundleId: string;
    provisioningProfile: string;
  };
  features: string[];
  capabilities: string[];
}

export interface WindowsDeployment {
  store: {
    url: string;
    appId: string;
    packageFamilyName: string;
    minVersion: string;
  };
  directDownload: {
    msixUrl: string;
    msixSize: number;
    exeUrl: string;
    exeSize: number;
    version: string;
    sha256: string;
  };
  winget?: {
    enabled: boolean;
    packageId: string;
    moniker: string;
  };
  features: string[];
  protocols: string[];
}

export interface MacOSDeployment {
  appStore: {
    url: string;
    appId: string;
    bundleId: string;
    minMacOSVersion: string;
  };
  directDownload: {
    dmgUrl: string;
    dmgSize: number;
    pkgUrl: string;
    pkgSize: number;
    universalBinary: boolean;
    version: string;
    sha256: string;
  };
  homebrew?: {
    enabled: boolean;
    caskName: string;
    tapUrl: string;
  };
  features: string[];
  entitlements: string[];
}

export interface LinuxDeployment {
  snap?: {
    enabled: boolean;
    url: string;
    snapName: string;
    channel: 'stable' | 'candidate' | 'beta' | 'edge';
  };
  flatpak?: {
    enabled: boolean;
    url: string;
    appId: string;
    remote: string;
  };
  appImage?: {
    enabled: boolean;
    url: string;
    size: number;
    sha256: string;
    updateInformation: string;
  };
  deb?: {
    enabled: boolean;
    url: string;
    size: number;
    sha256: string;
    dependencies: string[];
  };
  rpm?: {
    enabled: boolean;
    url: string;
    size: number;
    sha256: string;
    dependencies: string[];
  };
  aur?: {
    enabled: boolean;
    packageName: string;
    gitUrl: string;
  };
