  realtimeMetrics?: boolean;
  dataRetention?: number;
  customMetrics?: string[];
}

export interface LoggingConfig {
  enabled: boolean;
  level: 'error' | 'warn' | 'info' | 'debug';
  destination?: 'console' | 's3' | 'elasticsearch' | 'custom';
  format?: 'json' | 'text' | 'combined';
}

// CDN Manager Types
export interface CDNManagerConfig {
  providers: CDNProvider[];
  fallbackStrategy: 'failover' | 'round-robin' | 'geo-based';
  healthCheck?: HealthCheckConfig;
  customDomain?: CustomDomainConfig;
}

export interface HealthCheckConfig {
  enabled: boolean;
  interval: number;
  timeout: number;
  healthyThreshold: number;
  unhealthyThreshold: number;
  path: string;
}

export interface CustomDomainConfig {
  domain: string;
  subdomain?: string;
  certificateId?: string;
  verificationMethod: 'dns' | 'http' | 'email';
  autoRenew?: boolean;
}

// Cache Purge Types
export interface PurgeRequest {
  type: 'all' | 'url' | 'tag' | 'prefix';
  targets?: string[];
  async?: boolean;
  callback?: (result: PurgeResult) => void;
}

export interface PurgeResult {
  success: boolean;
  purgedCount?: number;
  errors?: string[];
  timestamp: Date;
}

// Metrics Types
export interface CDNMetrics {
  requests: {
    total: number;
    cached: number;
    uncached: number;
    errors: number;
  };
  bandwidth: {
    total: number;
    cached: number;
    uncached: number;
  };
  performance: {
    averageResponseTime: number;
    p95ResponseTime: number;
    p99ResponseTime: number;
    cacheHitRatio: number;
  };
  geography: {
    [country: string]: {
      requests: number;
      bandwidth: number;
    };
  };
  threats: {
    blocked: number;
    challenged: number;
    allowed: number;
  };
}

// Response Types
export interface CDNResponse<T = any> {
  success: boolean;
  data?: T;
  error?: CDNError;
  metadata?: {
    provider: string;
    region?: string;
    cacheStatus?: 'HIT' | 'MISS' | 'BYPASS' | 'EXPIRED';
    responseTime?: number;
  };
}

export interface CDNError {
  code: string;
  message: string;
  details?: any;
  retryable?: boolean;
}

