          duration,
          result,
          timestamp: Date.now()
        }
      });
    });
  }

  /**
   * Log cache updates
   */
  private async logCacheUpdate(
    type: string,
    url: string,
    status?: number
  ): Promise<void> {
    const clients = await (self as any).clients.matchAll();
    clients.forEach((client: any) => {
      client.postMessage({
        type: 'CACHE_UPDATE',
        data: {
