  version: '1.0.0',
  cacheNamePrefix: 'katalyst-pwa',
  staticCacheName: 'static-v1',
  dynamicCacheName: 'dynamic-v1',
  apiCacheName: 'api-v1',
  precacheUrls: [
    '/',
    '/offline',
    '/manifest.json'
  ],
  offlineUrl: '/offline',
  enableBackgroundSync: true,
  enablePushNotifications: true,
  enablePerformanceTracking: true,
  skipWaiting: true
};

