/**
 * Monitoring and Analytics Types
 * Enterprise-grade monitoring, analytics, and observability types
 */

// Core Web Vitals and Performance Metrics
export interface WebVitalsMetrics {
  // Core Web Vitals
  lcp: number; // Largest Contentful Paint
  fid: number; // First Input Delay
  cls: number; // Cumulative Layout Shift
  inp: number; // Interaction to Next Paint (new metric replacing FID)

  // Other Performance Metrics
  fcp: number; // First Contentful Paint
  ttfb: number; // Time to First Byte
  fcpToLcp: number; // Time between FCP and LCP

  // Navigation Timing
  domContentLoaded: number;
  loadComplete: number;

  // Resource Timing
  resourceLoadTime: number;
  cacheHitRatio: number;

  // Custom Metrics
  timeToInteractive: number;
  speedIndex: number;
}

export interface PWAMetrics {
  // Installation
  installPromptShown: boolean;
  installAccepted: boolean;
  installTime?: number;

  // Usage
  standalone: boolean;
  displayMode: 'fullscreen' | 'standalone' | 'minimal-ui' | 'browser';
  orientation: 'portrait' | 'landscape';

  // Service Worker
  serviceWorkerStatus: 'installing' | 'installed' | 'activating' | 'activated' | 'redundant';
  cacheStrategy: 'cache-first' | 'network-first' | 'stale-while-revalidate';
  backgroundSyncEnabled: boolean;

  // Offline
  offlineUsage: boolean;
  offlineDuration?: number;
  queuedRequests: number;
}

export interface ErrorMetrics {
  jsErrors: JSError[];
  networkErrors: NetworkError[];
  cspViolations: CSPViolation[];
  unhandledRejections: UnhandledRejection[];
}

export interface JSError {
  message: string;
  filename: string;
  lineno: number;
  colno: number;
  stack?: string;
  timestamp: number;
  userAgent: string;
  url: string;
  userId?: string;
}

export interface NetworkError {
  url: string;
  method: string;
  status: number;
  statusText: string;
  responseTime: number;
  timestamp: number;
  retryCount?: number;
}

export interface CSPViolation {
  blockedURI: string;
  violatedDirective: string;
  originalPolicy: string;
  sourceFile: string;
  lineNumber: number;
  timestamp: number;
}

export interface UnhandledRejection {
  reason: string;
  stack?: string;
  timestamp: number;
  url: string;
}

// User Analytics
export interface UserSession {
  sessionId: string;
  userId?: string;
  startTime: number;
  endTime?: number;
  duration?: number;
  pageViews: number;
  events: UserEvent[];
  device: DeviceInfo;
  location: GeolocationInfo;
}

export interface UserEvent {
  type: 'page_view' | 'click' | 'scroll' | 'form_submit' | 'custom';
  name: string;
  properties: Record<string, any>;
  timestamp: number;
  element?: {
    tagName: string;
    className: string;
    id: string;
    innerText: string;
  };
}

export interface DeviceInfo {
  userAgent: string;
  platform: string;
  language: string;
  screenResolution: string;
  viewportSize: string;
  colorDepth: number;
  pixelRatio: number;
  touchSupport: boolean;
  connectionType?: string;
  batteryLevel?: number;
  memoryInfo?: {
    usedJSHeapSize: number;
    totalJSHeapSize: number;
