    };

    let success = 0;
    let failed = 0;

    // Simulate API call (replace with actual FCM v1 API call)
    for (const token of tokens) {
      try {
        // Mock API call
        await this.mockFCMSend(token, payload);
        success++;
      } catch (error) {
