     1import type { RSpackConfig } from '../types/index.ts';
     2
     3declare const process:
     4  | {
     5      env: {
     6        NODE_ENV?: string;
     7      };
     8    }
     9  | undefined;
    10
    11export interface RSpackPlugin {
    12  name: string;
    13  apply: (compiler: any) => void;
    14}
    15
    16export interface RSpackIntegrationOptions extends RSpackConfig {
    17  enableModuleFederation?: boolean;
    18  enableSwcHelpers?: boolean;
    19  enableWebWorkers?: boolean;
    20  enableWasm?: boolean;
    21  enableSourceMaps?: boolean;
    22  enableBundleAnalyzer?: boolean;
    23  enableProgressBar?: boolean;
    24  enableTypeChecking?: boolean;
    25  customPlugins?: RSpackPlugin[];
    26}
    27
    28export class RSpackIntegration {
    29  private config: RSpackIntegrationOptions;
    30  private initialized = false;
    31  private plugins: Map<string, RSpackPlugin> = new Map();
    32
    33  constructor(config: RSpackIntegrationOptions) {
    34    this.config = {
    35      enableSwcHelpers: true,
    36      enableSourceMaps: process?.env?.NODE_ENV !== 'production',
    37      enableProgressBar: true,
    38      ...config,
    39    };
    40  }
    41
    42  async initialize() {
    43    if (this.initialized) {
    44      return [];
    45    }
    46
    47    const integrations = await Promise.all([
    48      this.setupCorePlugins(),
    49      this.setupOptimizations(),
    50      this.setupDevelopmentServer(),
    51      this.setupProductionBuild(),
    52    ]);
    53
    54    this.initialized = true;
    55    return integrations.filter(Boolean);
    56  }
    57
    58  private async setupCorePlugins() {
    59    // Setup core RSpack plugins
    60    const plugins = [];
    61
    62    // Progress plugin
    63    if (this.config.enableProgressBar) {
    64      plugins.push({
    65        name: 'rspack-progress-plugin',
    66        config: {
    67          profile: true,
    68          activeModules: true,
    69        },
    70      });
    71    }
    72
    73    // Module Federation
    74    if (this.config.enableModuleFederation) {
    75      plugins.push({
    76        name: 'module-federation-plugin',
    77        config: {
    78          name: 'katalyst_rspack',
    79          filename: 'remoteEntry.js',
    80          exposes: {},
    81          remotes: {},
    82          shared: {
    83            react: { singleton: true, requiredVersion: '^19.0.0' },
    84            'react-dom': { singleton: true, requiredVersion: '^19.0.0' },
    85          },
    86        },
    87      });
    88    }
    89
    90    // Bundle Analyzer
    91    if (this.config.enableBundleAnalyzer) {
    92      plugins.push({
    93        name: 'bundle-analyzer-plugin',
    94        config: {
    95          analyzerMode: 'static',
    96          openAnalyzer: false,
    97          reportFilename: 'bundle-report.html',
    98        },
    99      });
   100    }
   101
   102    return { type: 'plugins', plugins };
   103  }
   104
   105  private async setupOptimizations() {
   106    const optimizations = {
   107      splitChunks: {
   108        chunks: 'all',
   109        minSize: 20000,
   110        minRemainingSize: 0,
   111        minChunks: 1,
   112        maxAsyncRequests: 30,
   113        maxInitialRequests: 30,
   114        cacheGroups: {
   115          defaultVendors: {
   116            test: /[\\/]node_modules[\\/]/,
   117            priority: -10,
   118            reuseExistingChunk: true,
   119          },
   120          default: {
   121            minChunks: 2,
   122            priority: -20,
   123            reuseExistingChunk: true,
   124          },
   125          react: {
   126            test: /[\\/]node_modules[\\/](react|react-dom|scheduler)[\\/]/,
   127            name: 'react',
   128            chunks: 'all',
   129            priority: 20,
   130          },
   131          katalyst: {
   132            test: /[\\/]shared[\\/]src[\\/]/,
   133            name: 'katalyst-shared',
   134            chunks: 'all',
   135            priority: 15,
   136          },
   137        },
   138      },
   139      runtimeChunk: {
   140        name: 'runtime',
   141      },
   142      moduleIds: 'deterministic',
   143      minimize: process?.env?.NODE_ENV === 'production',
   144      usedExports: true,
   145      sideEffects: false,
   146    };
   147
   148    return { type: 'optimizations', config: optimizations };
   149  }
   150
   151  private async setupDevelopmentServer() {
   152    if (process?.env?.NODE_ENV === 'production') {
   153      return null;
   154    }
   155
   156    return {
   157      type: 'devServer',
   158      config: {
   159        hot: true,
   160        liveReload: true,
   161        historyApiFallback: true,
   162        compress: true,
   163        headers: {
   164          'Access-Control-Allow-Origin': '*',
   165          'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, PATCH, OPTIONS',
   166          'Access-Control-Allow-Headers': 'X-Requested-With, content-type, Authorization',
   167        },
   168        client: {
   169          overlay: {
   170            errors: true,
   171            warnings: false,
   172          },
   173          progress: true,
   174        },
   175      },
   176    };
   177  }
   178
   179  private async setupProductionBuild() {
   180    if (process?.env?.NODE_ENV !== 'production') {
   181      return null;
   182    }
   183
   184    return {
   185      type: 'production',
   186      config: {
   187        output: {
   188          hashFunction: 'xxhash64',
   189          hashDigestLength: 8,
   190          crossOriginLoading: 'anonymous',
   191        },
   192        optimization: {
   193          realContentHash: true,
   194          mergeDuplicateChunks: true,
   195          removeAvailableModules: true,
   196        },
   197      },
   198    };
   199  }
   200
   201  generateConfig(variant: 'core' | 'remix' | 'nextjs') {
   202    const baseConfig = {
   203      mode:
   204        typeof process !== 'undefined' && process?.env?.NODE_ENV === 'production'
   205          ? 'production'
   206          : 'development',
   207      entry: this.getEntryPoint(variant),
   208      output: {
   209        path: './dist',
   210        filename: '[name].[contenthash:8].js',
   211        chunkFilename: '[name].[contenthash:8].chunk.js',
   212        publicPath: '/',
   213        clean: true,
   214        cssFilename: 'css/[name].[contenthash:8].css',
   215        cssChunkFilename: 'css/[name].[contenthash:8].chunk.css',
   216        assetModuleFilename: 'assets/[name].[hash][ext]',
   217      },
   218      resolve: {
   219        extensions: ['.ts', '.tsx', '.js', '.jsx', '.json', '.node', '.wasm'],
   220        alias: {
   221          '@': './src',
   222          '@/components': './src/components',
   223          '@/hooks': './src/hooks',
   224          '@/utils': './src/utils',
   225          '@/stores': './src/stores',
   226          '@katalyst/multithreading': './shared/src/native',
   227          '@katalyst/shared': './shared/src',
   228        },
   229        fallback: this.config.enableWebWorkers
   230          ? {
   231              worker_threads: false,
   232              fs: false,
   233              path: false,
   234            }
   235          : undefined,
   236      },
   237      module: {
   238        rules: this.getModuleRules(variant),
   239        parser: this.config.enableWasm
   240          ? {
   241              javascript: {
   242                wasm: true,
   243              },
   244            }
   245          : undefined,
   246      },
   247      plugins: this.getPlugins(variant),
   248      optimization: this.config.optimization || {},
   249      performance: this.config.performance || {},
   250      experiments: this.getExperiments(),
   251      devtool: this.getDevtool(),
   252      cache: this.getCacheConfig(),
   253      stats: this.getStatsConfig(),
   254    };
   255
   256    return baseConfig;
   257  }
   258
   259  private getModuleRules(variant: 'core' | 'remix' | 'nextjs') {
   260    const rules = [
   261      {
   262        test: /\.(ts|tsx)$/,
   263        exclude: /node_modules/,
   264        use: [
   265          {
   266            loader: 'builtin:swc-loader',
   267            options: {
   268              jsc: {
   269                parser: {
   270                  syntax: 'typescript',
   271                  tsx: true,
   272                  decorators: true,
   273                  dynamicImport: true,
   274                },
   275                transform: {
   276                  react: {
   277                    runtime: 'automatic',
   278                    development: process?.env?.NODE_ENV !== 'production',
   279                    refresh: process?.env?.NODE_ENV !== 'production',
   280                  },
   281                  optimizer: {
   282                    globals: {
   283                      vars: {
   284                        __DEV__: process?.env?.NODE_ENV !== 'production',
   285                      },
   286                    },
   287                  },
   288                },
   289                target: 'es2022',
   290                externalHelpers: this.config.enableSwcHelpers,
   291              },
   292              module: {
   293                type: 'es6',
   294              },
   295            },
   296          },
   297        ],
   298      },
   299      {
   300        test: /\.css$/,
   301        use: [
   302          'style-loader',
   303          {
   304            loader: 'css-loader',
   305            options: {
   306              modules: {
   307                auto: true,
   308                localIdentName:
   309                  process?.env?.NODE_ENV === 'production'
   310                    ? '[hash:base64:8]'
   311                    : '[path][name]__[local]--[hash:base64:5]',
   312              },
   313            },
   314          },
   315          'postcss-loader',
   316        ],
   317      },
   318      {
   319        test: /\.(png|jpg|jpeg|gif|svg|webp|avif)$/,
   320        type: 'asset',
   321        generator: {
   322          filename: 'images/[name].[hash:8][ext]',
   323        },
   324        parser: {
   325          dataUrlCondition: {
   326            maxSize: 8 * 1024, // 8kb
   327          },
   328        },
   329      },
   330      {
   331        test: /\.(woff|woff2|eot|ttf|otf)$/,
   332        type: 'asset/resource',
   333        generator: {
   334          filename: 'fonts/[name].[hash:8][ext]',
   335        },
   336      },
   337      {
   338        test: /\.node$/,
   339        use: 'node-loader',
   340      },
   341    ];
   342
   343    // Add WebAssembly support
   344    if (this.config.enableWasm) {
   345      rules.push({
   346        test: /\.wasm$/,
   347        type: 'webassembly/async',
   348      });
   349    }
   350
   351    // Add Web Worker support
   352    if (this.config.enableWebWorkers) {
   353      rules.push({
   354        test: /\.worker\.(ts|js)$/,
   355        use: [
   356          {
   357            loader: 'worker-loader',
   358            options: {
   359              esModule: true,
   360              filename: '[name].[contenthash:8].worker.js',
   361            },
   362          },
   363          {
   364            loader: 'builtin:swc-loader',
   365            options: {
   366              jsc: {
   367                parser: {
   368                  syntax: 'typescript',
   369                  tsx: false,
   370                },
   371                target: 'es2022',
   372              },
   373            },
   374          },
   375        ],
   376      });
   377    }
   378
   379    return rules;
   380  }
   381
   382  private getExperiments() {
   383    const experiments: any = {
   384      asyncWebAssembly: this.config.enableWasm,
   385      topLevelAwait: true,
   386      outputModule: true,
   387      css: true,
   388    };
   389
   390    if (this.config.enableModuleFederation) {
   391      experiments.lazyCompilation = {
   392        entries: false,
   393        imports: true,
   394      };
   395    }
   396
   397    return experiments;
   398  }
   399
   400  private getDevtool() {
   401    if (!this.config.enableSourceMaps) {
   402      return false;
   403    }
   404
   405    return process?.env?.NODE_ENV === 'production' ? 'source-map' : 'eval-cheap-module-source-map';
   406  }
