   408  private getCacheConfig() {
   409    return {
   410      type: 'filesystem' as const,
   411      cacheDirectory: '.rspack-cache',
   412      compression: 'gzip',
   413      profile: true,
   414      buildDependencies: {
   415        config: ['./rspack.config.ts', './package.json'],
   416      },
   417    };
   418  }
   419
   420  private getStatsConfig() {
   421    return {
   422      preset: 'normal',
   423      colors: true,
   424      errorDetails: true,
   425      children: false,
   426      modules: false,
   427    };
   428  }
   429
   430  private getEntryPoint(variant: 'core' | 'remix' | 'nextjs') {
   431    switch (variant) {
   432      case 'core':
   433        return './src/main.tsx';
   434      case 'remix':
   435        return './app/entry.client.tsx';
   436      case 'nextjs':
   437        return './src/app/page.tsx';
   438      default:
   439        return './src/main.tsx';
   440    }
   441  }
   442
   443  private getPlugins(variant: 'core' | 'remix' | 'nextjs') {
   444    const plugins: RSpackPlugin[] = [];
   445
   446    // HTML Plugin
   447    plugins.push({
   448      name: 'HtmlRspackPlugin',
   449      apply: (compiler) => {
   450        // This would be replaced with actual RSpack HTML plugin in real implementation
   451        compiler.hooks.emit.tapAsync('HtmlRspackPlugin', (compilation: any, callback: any) => {
   452          const html = this.generateHtmlTemplate(variant);
   453          compilation.assets['index.html'] = {
   454            source: () => html,
   455            size: () => html.length,
   456          };
   457          callback();
   458        });
   459      },
   460    });
   461
   462    // Define Plugin for global constants
   463    plugins.push({
   464      name: 'DefinePlugin',
   465      apply: (compiler) => {
   466        const definitions = {
   467          'process.env.NODE_ENV': JSON.stringify(process?.env?.NODE_ENV || 'development'),
   468          __VARIANT__: JSON.stringify(variant),
   469          __KATALYST_VERSION__: JSON.stringify('1.0.0'),
   470          __RSPACK_ENABLED__: JSON.stringify(true),
   471        };
   472
   473        compiler.hooks.compilation.tap('DefinePlugin', (compilation: any) => {
   474          compilation.hooks.optimizeChunkModules.tap('DefinePlugin', () => {
   475            // Define globals
   476            Object.entries(definitions).forEach(([key, value]) => {
   477              compilation.defineVariable(key, value);
   478            });
   479          });
   480        });
   481      },
   482    });
   483
   484    // React Refresh Plugin for development
   485    if (process?.env?.NODE_ENV !== 'production' && this.config.plugins?.includes('react')) {
   486      plugins.push({
   487        name: 'ReactRefreshRspackPlugin',
   488        apply: (compiler) => {
   489          compiler.hooks.compilation.tap('ReactRefreshRspackPlugin', (compilation: any) => {
   490            // Enable React Fast Refresh
   491            compilation.hooks.buildModule.tap('ReactRefreshRspackPlugin', (module: any) => {
   492              if (module.resource && /\.(jsx?|tsx?)$/.test(module.resource)) {
   493                module.hotUpdate = true;
   494              }
   495            });
   496          });
   497        },
   498      });
   499    }
   500
   501    // SVGR Plugin
   502    if (this.config.plugins?.includes('svgr')) {
   503      plugins.push({
   504        name: 'SvgrRspackPlugin',
   505        apply: (compiler) => {
   506          compiler.hooks.normalModuleFactory.tap('SvgrRspackPlugin', (factory: any) => {
   507            factory.hooks.createModule.tap('SvgrRspackPlugin', (data: any) => {
   508              if (data.resource && data.resource.endsWith('.svg')) {
   509                data.type = 'javascript/auto';
   510                data.generator = {
   511                  type: 'asset/resource',
   512                };
   513              }
   514            });
   515          });
   516        },
   517      });
   518    }
   519
   520    // Type Check Plugin
   521    if (this.config.enableTypeChecking || this.config.plugins?.includes('type-check')) {
   522      plugins.push({
   523        name: 'TypeCheckRspackPlugin',
   524        apply: (compiler) => {
   525          let typescriptCheckProcess: any = null;
   526
   527          compiler.hooks.watchRun.tapAsync('TypeCheckRspackPlugin', (_: any, callback: any) => {
   528            if (!typescriptCheckProcess) {
   529              const { spawn } = require('child_process');
   530              typescriptCheckProcess = spawn('tsc', ['--noEmit', '--watch'], {
   531                stdio: 'inherit',
   532                shell: true,
   533              });
   534            }
   535            callback();
   536          });
   537
   538          compiler.hooks.done.tap('TypeCheckRspackPlugin', () => {
   539            if (typescriptCheckProcess) {
   540              console.log('[TypeCheck] TypeScript checking in progress...');
   541            }
   542          });
   543        },
   544      });
   545    }
   546
   547    // Progress Plugin
   548    if (this.config.enableProgressBar) {
   549      plugins.push({
   550        name: 'ProgressPlugin',
   551        apply: (compiler) => {
   552          let progress = 0;
   553          compiler.hooks.compilation.tap('ProgressPlugin', (compilation: any) => {
   554            compilation.hooks.buildModule.tap('ProgressPlugin', () => {
   555              progress += 1;
   556              if (progress % 10 === 0) {
   557                console.log(`[RSpack] Building... ${progress} modules processed`);
   558              }
   559            });
   560          });
   561        },
   562      });
   563    }
   564
   565    // Bundle Analyzer Plugin
   566    if (this.config.enableBundleAnalyzer) {
   567      plugins.push({
   568        name: 'BundleAnalyzerPlugin',
   569        apply: (compiler) => {
   570          compiler.hooks.emit.tapAsync(
   571            'BundleAnalyzerPlugin',
   572            (compilation: any, callback: any) => {
   573              const stats = compilation.getStats().toJson({
   574                chunks: true,
   575                modules: true,
   576                assets: true,
   577              });
   578
   579              const report = this.generateBundleReport(stats);
   580              compilation.assets['bundle-report.html'] = {
   581                source: () => report,
   582                size: () => report.length,
   583              };
   584              callback();
   585            }
   586          );
   587        },
   588      });
   589    }
   590
   591    // Add custom plugins
   592    if (this.config.customPlugins) {
   593      plugins.push(...this.config.customPlugins);
   594    }
   595
   596    // Store plugins for later use
   597    plugins.forEach((plugin) => {
   598      this.plugins.set(plugin.name, plugin);
   599    });
   600
   601    return plugins;
   602  }
   603
   604  private generateHtmlTemplate(variant: 'core' | 'remix' | 'nextjs'): string {
   605    const titles = {
   606      core: 'Katalyst Core - High Performance React Framework',
   607      remix: 'Katalyst Admin - Remix Powered Dashboard',
   608      nextjs: 'Katalyst Marketing - Next.js Website',
   609    };
   610
   611    return `<!DOCTYPE html>
   612<html lang="en">
   613<head>
   614    <meta charset="UTF-8">
   615    <meta name="viewport" content="width=device-width, initial-scale=1.0">
   616    <meta name="description" content="Katalyst Framework - ${variant} variant">
   617    <title>${titles[variant]}</title>
   618    <link rel="preconnect" href="https://fonts.googleapis.com">
   619    <link rel="dns-prefetch" href="https://fonts.googleapis.com">
   620</head>
   621<body>
   622    <div id="root"></div>
   623    <noscript>You need to enable JavaScript to run this application.</noscript>
   624</body>
   625</html>`;
   626  }
   627
   628  private generateBundleReport(stats: any): string {
   629    const modules = stats.modules || [];
   630    const chunks = stats.chunks || [];
   631    const assets = stats.assets || [];
   632
   633    const totalSize = assets.reduce((sum: number, asset: any) => sum + asset.size, 0);
   634    const formatSize = (bytes: number) => {
   635      const kb = bytes / 1024;
   636      return kb > 1024 ? `${(kb / 1024).toFixed(2)} MB` : `${kb.toFixed(2)} KB`;
   637    };
   638
   639    return `<!DOCTYPE html>
   640<html lang="en">
   641<head>
   642    <meta charset="UTF-8">
   643    <title>RSpack Bundle Analysis Report</title>
   644    <style>
   645        body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; padding: 20px; }
   646        h1, h2 { color: #333; }
   647        table { border-collapse: collapse; width: 100%; margin: 20px 0; }
   648        th, td { border: 1px solid #ddd; padding: 8px; text-align: left; }
   649        th { background-color: #f2f2f2; font-weight: bold; }
   650        tr:nth-child(even) { background-color: #f9f9f9; }
   651        .size { text-align: right; }
   652        .summary { background: #e3f2fd; padding: 15px; border-radius: 5px; margin: 20px 0; }
   653    </style>
   654</head>
   655<body>
   656    <h1>RSpack Bundle Analysis Report</h1>
   657    
   658    <div class="summary">
   659        <h2>Summary</h2>
   660        <p><strong>Total Bundle Size:</strong> ${formatSize(totalSize)}</p>
   661        <p><strong>Number of Chunks:</strong> ${chunks.length}</p>
   662        <p><strong>Number of Modules:</strong> ${modules.length}</p>
   663        <p><strong>Number of Assets:</strong> ${assets.length}</p>
   664    </div>
   665
   666    <h2>Assets</h2>
   667    <table>
   668        <thead>
   669            <tr>
   670                <th>Name</th>
   671                <th class="size">Size</th>
   672                <th>Type</th>
   673            </tr>
   674        </thead>
   675        <tbody>
   676            ${assets
   677              .map(
   678                (asset: any) => `
   679                <tr>
   680                    <td>${asset.name}</td>
   681                    <td class="size">${formatSize(asset.size)}</td>
   682                    <td>${asset.name.split('.').pop()}</td>
   683                </tr>
   684            `
   685              )
   686              .join('')}
   687        </tbody>
   688    </table>
   689
   690    <h2>Chunks</h2>
   691    <table>
   692        <thead>
   693            <tr>
   694                <th>ID</th>
   695                <th>Names</th>
   696                <th class="size">Size</th>
   697                <th>Modules</th>
   698            </tr>
   699        </thead>
   700        <tbody>
   701            ${chunks
   702              .map(
   703                (chunk: any) => `
   704                <tr>
   705                    <td>${chunk.id}</td>
   706                    <td>${chunk.names.join(', ')}</td>
   707                    <td class="size">${formatSize(chunk.size)}</td>
   708                    <td>${chunk.modules ? chunk.modules.length : 0}</td>
   709                </tr>
   710            `
   711              )
   712              .join('')}
   713        </tbody>
   714    </table>
   715</body>
   716</html>`;
   717  }
   718
   719  // API Methods for external use
   720  getPlugin(name: string): RSpackPlugin | undefined {
   721    return this.plugins.get(name);
   722  }
   723
   724  getAllPlugins(): RSpackPlugin[] {
   725    return Array.from(this.plugins.values());
   726  }
   727
   728  addPlugin(plugin: RSpackPlugin) {
   729    this.plugins.set(plugin.name, plugin);
   730  }
   731
   732  removePlugin(name: string) {
   733    this.plugins.delete(name);
   734  }
