#!/usr/bin/env node

const http = require('http');
const { WebSocketServer } = require('ws');

// Simple MCP server that provides basic tools
class SimpleMCPServer {
    constructor() {
        this.tools = new Map();
        this.setupTools();
    }

    setupTools() {
        // ByteDriver-like memory tool
        this.tools.set('memory:store', {
            name: 'memory:store',
            description: 'Store a key-value pair in memory',
            inputSchema: {
                type: 'object',
                properties: {
                    key: { type: 'string' },
                    value: { type: 'string' },
                    ttl: { type: 'number', optional: true }
                },
                required: ['key', 'value']
            }
        });

        this.tools.set('memory:retrieve', {
            name: 'memory:retrieve',
            description: 'Retrieve a value by key from memory',
            inputSchema: {
                type: 'object',
                properties: {
                    key: { type: 'string' }
                },
                required: ['key']
            }
        });

        this.tools.set('github:list_repos', {
            name: 'github:list_repos',
            description: 'List GitHub repositories (demo)',
            inputSchema: {
                type: 'object',
                properties: {
                    org: { type: 'string', optional: true }
                }
            }
        });
    }

    async handleToolCall(tool, args) {
        switch (tool) {
            case 'memory:store':
                // Simple in-memory storage for demo
                this.storage = this.storage || new Map();
                this.storage.set(args.key, args.value);
                return { success: true, message: `Stored ${args.key}` };

            case 'memory:retrieve':
                this.storage = this.storage || new Map();
                const value = this.storage.get(args.key);
                return { key: args.key, value: value || null };

            case 'github:list_repos':
                // Mock GitHub response
                return {
                    repositories: [
                        { name: 'example-repo', description: 'Demo repository' },
                        { name: 'another-repo', description: 'Another demo repo' }
                    ]
                };

            default:
                throw new Error(`Unknown tool: ${tool}`);
        }
    }

    handleMessage(message) {
        try {
            const data = JSON.parse(message);

            if (data.method === 'initialize') {
                return JSON.stringify({
                    jsonrpc: '2.0',
                    id: data.id,
                    result: {
                        protocolVersion: '2024-11-05',
                        capabilities: {
                            tools: { enabled: true },
                            resources: { enabled: false },
                            prompts: { enabled: false }
                        },
                        serverInfo: {
                            name: 'Simple MCP Server',
                            version: '1.0.0'
                        }
                    }
                });
            }

            if (data.method === 'tools/list') {
                return JSON.stringify({
                    jsonrpc: '2.0',
                    id: data.id,
                    result: {
                        tools: Array.from(this.tools.values())
                    }
                });
            }

            if (data.method === 'tools/call') {
                const { name, arguments: args } = data.params;
                return this.handleToolCall(name, args || {}).then(result => {
                    return JSON.stringify({
                        jsonrpc: '2.0',
                        id: data.id,
                        result: {
                            content: [{
                                type: 'text',
                                text: JSON.stringify(result, null, 2)
                            }]
                        }
                    });
                }).catch(error => {
                    return JSON.stringify({
                        jsonrpc: '2.0',
                        id: data.id,
                        error: {
                            code: -32603,
                            message: error.message
                        }
                    });
                });
            }

            return JSON.stringify({
                jsonrpc: '2.0',
                id: data.id,
                error: {
                    code: -32601,
                    message: 'Method not found'
                }
            });

        } catch (error) {
            return JSON.stringify({
                jsonrpc: '2.0',
                id: null,
                error: {
                    code: -32700,
                    message: 'Parse error'
                }
            });
        }
    }

    start(port = 8080) {
        const server = http.createServer((req, res) => {
            if (req.url === '/health') {
                res.writeHead(200, { 'Content-Type': 'application/json' });
                res.end(JSON.stringify({ status: 'healthy' }));
            } else if (req.url === '/') {
                res.writeHead(200, { 'Content-Type': 'application/json' });
                res.end(JSON.stringify({ 
                    name: 'Simple MCP Server',
                    version: '1.0.0',
                    endpoints: [
                        'WebSocket: ws://localhost:8080/mcp',
                        'Health: http://localhost:8080/health'
                    ]
                }));
            } else {
                res.writeHead(404);
                res.end('Not found');
            }
        });

        const wss = new WebSocketServer({ 
            server,
            path: '/mcp'
        });

        wss.on('connection', (ws) => {
            console.log('New MCP client connected');

            ws.on('message', async (message) => {
                try {
                    const response = await this.handleMessage(message.toString());
                    ws.send(response);
                } catch (error) {
                    console.error('Error handling message:', error);
                    ws.send(JSON.stringify({
                        jsonrpc: '2.0',
                        id: null,
                        error: {
                            code: -32603,
                            message: 'Internal error'
                        }
                    }));
                }
            });

            ws.on('close', () => {
                console.log('MCP client disconnected');
            });
        });

        server.listen(port, '0.0.0.0', () => {
            console.log(`Simple MCP Server running on port ${port}`);
            console.log(`WebSocket endpoint: ws://localhost:${port}/mcp`);
            console.log(`Health endpoint: http://localhost:${port}/health`);
        });
    }
}

if (require.main === module) {
    const server = new SimpleMCPServer();
    const port = process.env.PORT || 8080;
    server.start(port);
}

module.exports = SimpleMCPServer;

