use anyhow::Result;
use revm::{
    Evm, InMemoryDB,
    primitives::{Address, Bytes, CreateScheme, TransactTo, U256},
};
use serde::{Deserialize, Serialize};

pub mod compat;
pub mod compat_clean;

// Re-export clean EVM types
pub use compat_clean::{
    APEChainAddress, TransactionId, ContractCall, AuthData, Amount,
    Encode, Decode, utils
};

// Keep legacy exports for backward compatibility (deprecate later)
#[deprecated(note = "Use clean EVM types instead")]
pub use compat::{
    ANONYMOUS, APEChainAuthTypes, APEChainContractCall, APEChainInterface,
    APEChainXid, CandidType, Nat, Principal,
};

#[derive(Debug)]
pub struct APEChainEVM {
    evm: Evm<'static, (), InMemoryDB>,
    state: APEChainState,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct APEChainState {
    pub block_number: u64,
    pub timestamp: u64,
    pub chain_id: u64,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Transaction {
    pub from: Address,
    pub to: Option<Address>,
    pub value: U256,
    pub data: Bytes,
    pub gas_limit: u64,
    pub gas_price: U256,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ExecutionResult {
    pub success: bool,
    pub gas_used: u64,
    pub output: Bytes,
    pub logs: Vec<String>,
}

impl APEChainEVM {
    pub fn new() -> Self {
        let evm = Evm::builder().with_db(InMemoryDB::default()).build();

        Self {
            evm,
            state: APEChainState {
                block_number: 0,
                timestamp: 0,
                chain_id: 1337,
            },
        }
    }

    pub async fn execute_transaction(&mut self, tx: Transaction) -> Result<ExecutionResult> {
        use revm::primitives::{BlockEnv, CfgEnv, TxEnv};

        let mut tx_env = TxEnv::default();
        tx_env.caller = tx.from;
        tx_env.gas_limit = tx.gas_limit;
        tx_env.gas_price = tx.gas_price;
        tx_env.transact_to = match tx.to {
            Some(to) => TransactTo::Call(to),
            None => TransactTo::Create(CreateScheme::Create),
        };
        tx_env.value = tx.value;
        tx_env.data = tx.data;

        let mut block_env = BlockEnv::default();
        block_env.number = U256::from(self.state.block_number);
        block_env.timestamp = U256::from(self.state.timestamp);

        let cfg_env = CfgEnv::default();

        // Update the EVM environment
        *self.evm.tx_mut() = tx_env;
        *self.evm.block_mut() = block_env;
        *self.evm.cfg_mut() = cfg_env;

        match self.evm.transact() {
            Ok(result) => {
                let execution_result = ExecutionResult {
                    success: result.result.is_success(),
                    gas_used: result.result.gas_used(),
                    output: result
                        .result
                        .output()
                        .map(|o| o.clone())
                        .unwrap_or_default(),
                    logs: vec![format!("Transaction executed successfully")],
                };

                self.state.block_number += 1;
                self.state.timestamp = std::time::SystemTime::now()
                    .duration_since(std::time::UNIX_EPOCH)
                    .unwrap()
                    .as_secs();

                Ok(execution_result)
            }
            Err(e) => {
                let execution_result = ExecutionResult {
                    success: false,
                    gas_used: 0,
                    output: Bytes::new(),
                    logs: vec![format!("Transaction failed: {:?}", e)],
                };
                Ok(execution_result)
            }
        }
    }

    pub fn get_state(&self) -> &APEChainState {
        &self.state
    }

    pub async fn deploy_contract(
        &mut self,
        bytecode: Bytes,
        constructor_args: Bytes,
    ) -> Result<Address> {
        let tx = Transaction {
            from: Address::ZERO,
            to: None,
            value: U256::ZERO,
            data: [bytecode, constructor_args].concat().into(),
            gas_limit: 1_000_000,
            gas_price: U256::from(20_000_000_000u64),
        };

        let result = self.execute_transaction(tx).await?;
        if result.success {
            Ok(Address::from_slice(&result.output[12..32]))
        } else {
            Err(anyhow::anyhow!("Contract deployment failed"))
        }
    }
}

impl Default for APEChainEVM {
    fn default() -> Self {
        Self::new()
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[tokio::test]
    async fn test_apechain_evm_creation() {
        let evm = APEChainEVM::new();
        assert_eq!(evm.state.chain_id, 1337);
        assert_eq!(evm.state.block_number, 0);
    }

    #[tokio::test]
    async fn test_transaction_execution() {
        let mut evm = APEChainEVM::new();

        let tx = Transaction {
            from: Address::from_slice(&[1u8; 20]),
            to: Some(Address::from_slice(&[2u8; 20])),
            value: U256::from(1000),
            data: Bytes::new(),
            gas_limit: 21000,
            gas_price: U256::from(20_000_000_000u64),
        };

        let result = evm.execute_transaction(tx).await;
        assert!(result.is_ok());

        let execution_result = result.unwrap();
        assert_eq!(evm.state.block_number, 1);
    }
}

