    pub key: Pubkey,
    pub is_signer: bool,
    pub is_writable: bool,
    pub lamports: u64,
    pub data: Vec<u8>,
    pub owner: Pubkey,
    pub executable: bool,
    pub rent_epoch: u64,
}

impl AccountInfo {
    /// Create account info with security validation
    pub fn new_validated(
        key: Pubkey,
        lamports: u64,
        data: Vec<u8>,
        owner: Pubkey,
        is_signer: bool,
        is_writable: bool,
    ) -> Result<Self, AccountError> {
        // Validate data size limits
        if data.len() > MAX_ACCOUNT_DATA_SIZE {
            return Err(AccountError::DataTooLarge);
        }

        // Validate key format
        if key == Pubkey::default() && !data.is_empty() {
            return Err(AccountError::InvalidKey);
        }

        Ok(Self {
            key,
            is_signer,
            is_writable,
            lamports,
            data,
            owner,
            executable: false,
            rent_epoch: 0,
        })
    }
}
