    message_queue: AsyncMessageQueue,
    /// State synchronization manager
    state_sync: StateSynchronizer,
}

impl GenServerBridge {
    pub fn new(config: BridgeConfig) -> Result<Self, BridgeError> {
        Ok(Self {
            process_registry: ProcessRegistry::connect(config.registry_endpoint)?,
            message_codec: MessageCodec::new_with_validation(),
            message_queue: AsyncMessageQueue::new(config.queue_size),
            state_sync: StateSynchronizer::new(config.sync_config),
        })
    }

    /// Send asynchronous message to GenServer process
    pub async fn send_async_message(&mut self, message: GenServerMessage) -> Result<(), BridgeError> {
        // Step 1: Validate message format
        self.message_codec.validate_message(&message)?;

        // Step 2: Serialize message for GenServer
        let serialized = self.message_codec.serialize_for_genserver(&message)?;

        // Step 3: Lookup target GenServer process
        let process_info = self.process_registry
            .find_process(&message.target_process_name)
            .await?
            .ok_or(BridgeError::ProcessNotFound)?;

        // Step 4: Queue message for async delivery
        self.message_queue.enqueue(QueuedMessage {
            process_info,
            message: serialized,
            priority: message.priority,
            timestamp: Instant::now(),
        }).await?;

        Ok(())
    }

    /// Receive and process messages from GenServer
    pub async fn handle_genserver_message(&mut self, raw_message: &[u8]) -> Result<SVMResponse, BridgeError> {
        // Step 1: Deserialize GenServer message
        let genserver_msg = self.message_codec.deserialize_from_genserver(raw_message)?;

        // Step 2: Process message based on type
        match genserver_msg.message_type {
            MessageType::StateUpdate => {
                self.handle_state_update(genserver_msg.payload).await
            },
            MessageType::TransactionRequest => {
                self.handle_transaction_request(genserver_msg.payload).await
            },
            MessageType::AccountQuery => {
                self.handle_account_query(genserver_msg.payload).await
            },
            MessageType::HealthCheck => {
                Ok(SVMResponse::HealthCheck { status: "healthy".to_string() })
            },
        }
    }

    /// Handle state synchronization with DragonflyDB
    async fn handle_state_update(&mut self, payload: MessagePayload) -> Result<SVMResponse, BridgeError> {
        let state_update: StateUpdate = self.message_codec.decode_state_update(payload)?;

        // Update SVM account state
        self.state_sync.apply_state_update(state_update).await?;

        Ok(SVMResponse::StateUpdateConfirmed)
    }
}

/// Message types for GenServer communication
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum MessageType {
    StateUpdate,
    TransactionRequest,
    AccountQuery,
    HealthCheck,
}

