            .await?;

        // Step 5: Translate WASM result back to SVM types
        let svm_result = self.function_translator.translate_result(&result)?;

        // Step 6: Apply any state changes back to SVM
        self.apply_wasm_state_changes(svm_result.state_changes, svm_context).await?;

        Ok(svm_result)
    }

