}

impl GasIncludedProcessor {
    pub fn new(config: GasConfig) -> Result<Self, GasError> {
        Ok(Self {
            gas_calculator: GasPriceCalculator::new_secure(config.calculator_config)?,
            weth_pool: WETHPoolManager::new(config.pool_config)?,
            fee_processor: FeeProcessor::new(config.fee_config)?,
            security_monitor: EconomicSecurityMonitor::new(config.security_config)?,
        })
    }

    /// Process transaction with automatic gas inclusion
    pub async fn process_transaction_with_gas(
        &mut self,
        transaction: SVMTransaction,
        user_weth_balance: WETHBalance,
    ) -> Result<TransactionResult, GasError> {
        // Step 1: Calculate gas requirement
        let gas_estimate = self.estimate_transaction_gas(&transaction).await?;

        // Step 2: Check economic security conditions
        self.security_monitor.validate_transaction(&transaction, gas_estimate).await?;

        // Step 3: Calculate WETH cost for gas
        let current_gas_price = self.gas_calculator.get_current_price().await?;
        let weth_cost = self.calculate_weth_cost(gas_estimate, current_gas_price)?;

        // Step 4: Validate user has sufficient WETH balance
        if user_weth_balance.amount < weth_cost {
            return Err(GasError::InsufficientBalance {
                required: weth_cost,
                available: user_weth_balance.amount,
            });
        }

        // Step 5: Reserve WETH for gas payment
        let gas_reservation = self.weth_pool.reserve_for_gas(
            user_weth_balance.address,
            weth_cost,
        ).await?;

        // Step 6: Execute SVM transaction
        let execution_result = match self.execute_svm_transaction(transaction).await {
            Ok(result) => result,
            Err(e) => {
                // Release gas reservation on failure
                self.weth_pool.release_reservation(gas_reservation).await?;
                return Err(GasError::ExecutionFailed(e));
            }
        };

        // Step 7: Calculate actual gas used and process payment
        let actual_gas_used = execution_result.gas_consumed;
        let actual_weth_cost = self.calculate_weth_cost(actual_gas_used, current_gas_price)?;

        // Step 8: Process final gas payment
        self.fee_processor.process_gas_payment(
            gas_reservation,
            actual_weth_cost,
            execution_result.fee_recipient,
        ).await?;

        Ok(TransactionResult {
            execution_result,
            gas_paid: actual_weth_cost,
            gas_price_used: current_gas_price,
            remaining_balance: user_weth_balance.amount - actual_weth_cost,
        })
    }

    /// Estimate gas consumption for SVM transaction
    async fn estimate_transaction_gas(&self, transaction: &SVMTransaction) -> Result<GasAmount, GasError> {
        // Simulate transaction execution without state changes
        let simulation_result = self.simulate_transaction(transaction).await?;

        // Add safety margin for gas estimation
        let base_gas = simulation_result.gas_used;
        let safety_margin = (base_gas * 110) / 100; // 10% safety margin

        Ok(GasAmount(safety_margin))
    }
}

