    /// Execute cross-chain transaction from SVM context
    pub async fn execute_cross_chain_transaction(
        &mut self,
        transaction: CrossChainTransaction,
        svm_context: &SVMContext,
    ) -> Result<CrossChainResult, BridgeError> {
        // Step 1: Validate cross-chain transaction
        self.validate_cross_chain_transaction(&transaction).await?;

        // Step 2: Prepare transaction for target chain
        let prepared_tx = match transaction.target_chain {
            ChainType::Polygon => {
                self.prepare_polygon_transaction(&transaction, svm_context).await?
            },
            ChainType::Ethereum => {
                self.prepare_ethereum_transaction(&transaction, svm_context).await?
            },
            ChainType::Polyhedra => {
                self.prepare_polyhedra_zk_transaction(&transaction, svm_context).await?
            },
        };

        // Step 3: Execute bridge transaction with security controls
        let bridge_result = self.execute_bridge_transaction(prepared_tx).await?;

        // Step 4: Wait for confirmation and finality
        let confirmation = self.wait_for_confirmation(
            bridge_result.tx_hash,
            transaction.target_chain,
            transaction.required_confirmations,
        ).await?;

        // Step 5: Update SVM state with cross-chain result
        self.update_svm_state(confirmation, svm_context).await?;

        Ok(CrossChainResult {
            source_tx_hash: transaction.source_tx_hash,
            target_tx_hash: bridge_result.tx_hash,
            confirmation,
            final_state: bridge_result.final_state,
        })
    }

    /// Prepare Polygon transaction with gas optimization
    async fn prepare_polygon_transaction(
        &self,
        transaction: &CrossChainTransaction,
        svm_context: &SVMContext,
    ) -> Result<PreparedTransaction, BridgeError> {
        // Step 1: Convert SVM account data to Polygon format
        let polygon_accounts = self.convert_svm_to_polygon_accounts(
            &transaction.accounts,
            svm_context,
        )?;

        // Step 2: Calculate optimal gas price for Polygon
        let gas_price = self.polygon_bridge.get_optimal_gas_price().await?;

        // Step 3: Prepare transaction data
        let tx_data = self.polygon_bridge.prepare_transaction_data(
            &transaction.instruction_data,
            &polygon_accounts,
        )?;

        // Step 4: Sign transaction with cross-chain keys
        let signed_tx = self.polygon_bridge.sign_transaction(
            tx_data,
            gas_price,
            transaction.signer_key,
        ).await?;

        Ok(PreparedTransaction::Polygon(signed_tx))
    }

    /// Handle incoming cross-chain messages
    pub async fn handle_incoming_message(
        &mut self,
        message: IncomingCrossChainMessage,
    ) -> Result<MessageResult, BridgeError> {
        // Step 1: Verify message authenticity
        self.verify_message_authenticity(&message).await?;

        // Step 2: Decode message for SVM processing
        let svm_instruction = self.decode_cross_chain_message(&message)?;

        // Step 3: Execute SVM instruction
        let execution_result = self.execute_svm_instruction(svm_instruction).await?;

        // Step 4: Prepare response message if needed
        let response = if message.requires_response {
            Some(self.prepare_response_message(execution_result, &message).await?)
        } else {
            None
        };

        Ok(MessageResult {
            execution_result,
            response_message: response,
        })
    }
