//! GenServer Integration Bridge for SVM-Elixir Communication
//!
//! This module provides a secure, high-performance bridge between Elixir GenServer
//! processes and the SVM runtime, enabling seamless integration with the existing
//! APEOs architecture while maintaining memory safety and formal verification.

use crate::svm_runtime::{SVMRuntimeCore, SVMInstruction, AccountInfo, InstructionResult, SVMError, Pubkey};
use serde::{Serialize, Deserialize};
use tokio::sync::{mpsc, oneshot, RwLock, Mutex};
use std::sync::Arc;
use std::collections::HashMap;
use std::time::{Duration, Instant};
use uuid::Uuid;
use tracing::{info, warn, error, debug, instrument};

/// Maximum number of concurrent GenServer requests
const MAX_CONCURRENT_REQUESTS: usize = 1000;

/// Request timeout duration
const REQUEST_TIMEOUT: Duration = Duration::from_secs(30);

/// Bridge message types for communication with Elixir GenServer
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum BridgeMessage {
    /// Execute SVM instruction
    ExecuteInstruction {
        request_id: String,
        instruction: SerializableSVMInstruction,
        accounts: Vec<SerializableAccountInfo>,
    },
    /// Query account state
    QueryAccount {
        request_id: String,
        pubkey: String,
    },
    /// Health check request
    HealthCheck {
        request_id: String,
    },
    /// Batch execution request
    BatchExecute {
        request_id: String,
        instructions: Vec<SerializableSVMInstruction>,
        accounts: Vec<Vec<SerializableAccountInfo>>,
    },
}

/// Bridge response types for GenServer communication
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum BridgeResponse {
    /// Instruction execution result
    ExecutionResult {
        request_id: String,
        result: Result<SerializableInstructionResult, String>,
        execution_time_ms: u64,
        gas_used: u64,
    },
    /// Account query result
    AccountData {
        request_id: String,
        account: Option<SerializableAccountInfo>,
    },
    /// Health check response
    HealthStatus {
        request_id: String,
        status: HealthInfo,
    },
    /// Batch execution result
    BatchResult {
        request_id: String,
        results: Vec<Result<SerializableInstructionResult, String>>,
        total_gas_used: u64,
        execution_time_ms: u64,
    },
    /// Error response
    Error {
        request_id: String,
        error: String,
        error_code: u32,
    },
}

/// Serializable version of SVMInstruction for JSON communication
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SerializableSVMInstruction {
    pub program_id: String,
    pub accounts: Vec<String>,
    pub data: Vec<u8>,
}

/// Serializable version of AccountInfo for JSON communication
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SerializableAccountInfo {
    pub pubkey: String,
    pub lamports: u64,
    pub data: Vec<u8>,
    pub owner: String,
    pub executable: bool,
    pub rent_epoch: u64,
}

/// Serializable version of InstructionResult for JSON communication
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SerializableInstructionResult {
    pub success: bool,
    pub logs: Vec<String>,
    pub compute_units_consumed: u64,
    pub accounts_modified: Vec<String>,
}

/// Health information for the SVM runtime
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct HealthInfo {
    pub active_requests: usize,
    pub total_requests_processed: u64,
    pub average_execution_time_ms: f64,
    pub error_rate: f64,
    pub memory_usage_mb: u64,
    pub uptime_seconds: u64,
}

/// Pending request tracking
#[derive(Debug)]
struct PendingRequest {
    sender: oneshot::Sender<BridgeResponse>,
    timestamp: Instant,
}

/// GenServer Bridge for SVM-Elixir communication
pub struct GenServerBridge {
    /// SVM runtime core instance
    svm_runtime: Arc<Mutex<SVMRuntimeCore>>,

    /// Pending requests map
    pending_requests: Arc<RwLock<HashMap<String, PendingRequest>>>,

    /// Request statistics
    stats: Arc<Mutex<BridgeStats>>,

    /// Message receiver channel
    message_receiver: Option<mpsc::Receiver<BridgeMessage>>,

    /// Response sender channel
    response_sender: mpsc::Sender<BridgeResponse>,

    /// Bridge startup time
    start_time: Instant,
}

/// Bridge statistics tracking
#[derive(Debug, Default)]
struct BridgeStats {
    total_requests: u64,
    successful_requests: u64,
    failed_requests: u64,
    total_execution_time: Duration,
    peak_concurrent_requests: usize,
}

impl GenServerBridge {
    /// Create new GenServer bridge
    pub fn new(svm_runtime: SVMRuntimeCore) -> (Self, mpsc::Sender<BridgeMessage>, mpsc::Receiver<BridgeResponse>) {
        let (message_sender, message_receiver) = mpsc::channel(MAX_CONCURRENT_REQUESTS);
        let (response_sender, response_receiver) = mpsc::channel(MAX_CONCURRENT_REQUESTS);

        let bridge = GenServerBridge {
            svm_runtime: Arc::new(Mutex::new(svm_runtime)),
            pending_requests: Arc::new(RwLock::new(HashMap::new())),
            stats: Arc::new(Mutex::new(BridgeStats::default())),
            message_receiver: Some(message_receiver),
            response_sender,
            start_time: Instant::now(),
        };

        (bridge, message_sender, response_receiver)
    }

    /// Start the bridge event loop
    #[instrument(skip(self), name = "genserver_bridge_start")]
    pub async fn start(&mut self) -> Result<(), Box<dyn std::error::Error>> {
        info!("Starting GenServer Bridge for SVM-Elixir communication");

        let mut message_receiver = self.message_receiver
            .take()
            .ok_or("Bridge already started")?;

        // Start request timeout cleanup task
        self.start_cleanup_task().await;

        // Main message processing loop
        while let Some(message) = message_receiver.recv().await {
            self.handle_message(message).await;
        }

        info!("GenServer Bridge stopped");
        Ok(())
    }

    /// Handle incoming GenServer message
    #[instrument(skip(self, message), name = "handle_bridge_message")]
    async fn handle_message(&self, message: BridgeMessage) {
        let start_time = Instant::now();

        let response = match message {
            BridgeMessage::ExecuteInstruction { request_id, instruction, accounts } => {
                self.handle_execute_instruction(request_id.clone(), instruction, accounts).await
            },
            BridgeMessage::QueryAccount { request_id, pubkey } => {
                self.handle_query_account(request_id.clone(), pubkey).await
            },
            BridgeMessage::HealthCheck { request_id } => {
                self.handle_health_check(request_id.clone()).await
            },
            BridgeMessage::BatchExecute { request_id, instructions, accounts } => {
                self.handle_batch_execute(request_id.clone(), instructions, accounts).await
            },
        };

        // Update statistics
        let execution_time = start_time.elapsed();
        self.update_stats(execution_time, response.is_success()).await;

        // Send response
        if let Err(e) = self.response_sender.send(response).await {
            error!("Failed to send bridge response: {}", e);
        }
    }

    /// Handle SVM instruction execution request
    async fn handle_execute_instruction(
        &self,
        request_id: String,
        instruction: SerializableSVMInstruction,
        accounts: Vec<SerializableAccountInfo>,
    ) -> BridgeResponse {
        let start_time = Instant::now();

        // Convert serializable types to SVM types
        let svm_instruction = match self.convert_to_svm_instruction(instruction) {
            Ok(instr) => instr,
            Err(e) => {
                return BridgeResponse::Error {
                    request_id,
                    error: format!("Invalid instruction format: {}", e),
                    error_code: 400,
                };
            }
        };

        let svm_accounts = match self.convert_to_svm_accounts(accounts) {
            Ok(accts) => accts,
            Err(e) => {
                return BridgeResponse::Error {
                    request_id,
                    error: format!("Invalid accounts format: {}", e),
                    error_code: 400,
                };
            }
        };

        // Execute instruction
        let mut runtime = self.svm_runtime.lock().await;
        let result = runtime.execute_instruction(svm_instruction, svm_accounts).await;

        let execution_time = start_time.elapsed().as_millis() as u64;

        match result {
            Ok(instruction_result) => {
                let serializable_result = self.convert_from_instruction_result(instruction_result);
                BridgeResponse::ExecutionResult {
                    request_id,
                    result: Ok(serializable_result.0),
                    execution_time_ms: execution_time,
                    gas_used: serializable_result.1,
                }
            },
            Err(e) => {
                BridgeResponse::Error {
                    request_id,
                    error: format!("Execution failed: {}", e),
                    error_code: 500,
                }
            }
        }
    }

    /// Handle account query request
    async fn handle_query_account(&self, request_id: String, pubkey: String) -> BridgeResponse {
        let pubkey = match self.parse_pubkey(&pubkey) {
            Ok(pk) => pk,
            Err(e) => {
                return BridgeResponse::Error {
                    request_id,
                    error: format!("Invalid pubkey format: {}", e),
                    error_code: 400,
                };
            }
        };

        let runtime = self.svm_runtime.lock().await;
        let account = runtime.get_account(&pubkey).await;

        let serializable_account = account.map(|acc| self.convert_from_account_info(acc));

        BridgeResponse::AccountData {
            request_id,
            account: serializable_account,
        }
    }

    /// Handle health check request
    async fn handle_health_check(&self, request_id: String) -> BridgeResponse {
        let stats = self.stats.lock().await;
        let pending_count = self.pending_requests.read().await.len();

        let health = HealthInfo {
            active_requests: pending_count,
            total_requests_processed: stats.total_requests,
            average_execution_time_ms: if stats.total_requests > 0 {
                stats.total_execution_time.as_millis() as f64 / stats.total_requests as f64
            } else {
                0.0
            },
            error_rate: if stats.total_requests > 0 {
                stats.failed_requests as f64 / stats.total_requests as f64
            } else {
                0.0
            },
            memory_usage_mb: self.get_memory_usage(),
            uptime_seconds: self.start_time.elapsed().as_secs(),
        };

        BridgeResponse::HealthStatus {
            request_id,
            status: health,
        }
    }

    /// Handle batch execution request
    async fn handle_batch_execute(
        &self,
        request_id: String,
        instructions: Vec<SerializableSVMInstruction>,
        accounts: Vec<Vec<SerializableAccountInfo>>,
    ) -> BridgeResponse {
        let start_time = Instant::now();
        let mut results = Vec::new();
        let mut total_gas = 0u64;

        if instructions.len() != accounts.len() {
            return BridgeResponse::Error {
                request_id,
                error: "Instructions and accounts length mismatch".to_string(),
                error_code: 400,
            };
        }

        let mut runtime = self.svm_runtime.lock().await;

        for (instruction, account_set) in instructions.into_iter().zip(accounts.into_iter()) {
            let svm_instruction = match self.convert_to_svm_instruction(instruction) {
                Ok(instr) => instr,
                Err(e) => {
                    results.push(Err(format!("Invalid instruction: {}", e)));
                    continue;
                }
            };

            let svm_accounts = match self.convert_to_svm_accounts(account_set) {
                Ok(accts) => accts,
                Err(e) => {
                    results.push(Err(format!("Invalid accounts: {}", e)));
                    continue;
                }
            };

            match runtime.execute_instruction(svm_instruction, svm_accounts).await {
                Ok(instruction_result) => {
                    let (serializable_result, gas_used) = self.convert_from_instruction_result(instruction_result);
                    total_gas += gas_used;
                    results.push(Ok(serializable_result));
                },
                Err(e) => {
                    results.push(Err(format!("Execution failed: {}", e)));
                }
            }
        }

        let execution_time = start_time.elapsed().as_millis() as u64;

        BridgeResponse::BatchResult {
            request_id,
            results,
            total_gas_used: total_gas,
            execution_time_ms: execution_time,
        }
    }

    /// Convert serializable instruction to SVM instruction
    fn convert_to_svm_instruction(&self, instruction: SerializableSVMInstruction) -> Result<SVMInstruction, String> {
        let program_id = self.parse_pubkey(&instruction.program_id)?;
        let account_metas = instruction.accounts
            .into_iter()
            .map(|acc| self.parse_pubkey(&acc))
            .collect::<Result<Vec<_>, _>>()?;

        Ok(SVMInstruction {
            program_id,
            accounts: account_metas,
            data: instruction.data,
        })
    }

    /// Convert serializable accounts to SVM accounts
    fn convert_to_svm_accounts(&self, accounts: Vec<SerializableAccountInfo>) -> Result<Vec<AccountInfo>, String> {
        accounts
            .into_iter()
            .map(|acc| {
                Ok(AccountInfo {
                    pubkey: self.parse_pubkey(&acc.pubkey)?,
                    lamports: acc.lamports,
                    data: acc.data,
                    owner: self.parse_pubkey(&acc.owner)?,
                    executable: acc.executable,
                    rent_epoch: acc.rent_epoch,
                })
            })
            .collect()
    }

    /// Convert instruction result to serializable format
    fn convert_from_instruction_result(&self, result: InstructionResult) -> (SerializableInstructionResult, u64) {
        let gas_used = result.compute_units_consumed;
        let serializable = SerializableInstructionResult {
            success: result.success,
            logs: result.logs,
            compute_units_consumed: result.compute_units_consumed,
            accounts_modified: result.accounts_modified
                .into_iter()
                .map(|pk| pk.to_string())
                .collect(),
        };
        (serializable, gas_used)
    }

    /// Convert account info to serializable format
    fn convert_from_account_info(&self, account: AccountInfo) -> SerializableAccountInfo {
        SerializableAccountInfo {
            pubkey: account.pubkey.to_string(),
            lamports: account.lamports,
            data: account.data,
            owner: account.owner.to_string(),
            executable: account.executable,
            rent_epoch: account.rent_epoch,
        }
    }

    /// Parse string to Pubkey
    fn parse_pubkey(&self, s: &str) -> Result<Pubkey, String> {
        s.parse().map_err(|e| format!("Invalid pubkey: {}", e))
    }

    /// Start cleanup task for expired requests
    async fn start_cleanup_task(&self) {
        let pending_requests = Arc::clone(&self.pending_requests);

        tokio::spawn(async move {
            let mut interval = tokio::time::interval(Duration::from_secs(10));

            loop {
                interval.tick().await;

                let mut requests = pending_requests.write().await;
                let now = Instant::now();

                requests.retain(|_id, request| {
                    if now.duration_since(request.timestamp) > REQUEST_TIMEOUT {
                        // Send timeout response
                        let _ = request.sender.send(BridgeResponse::Error {
                            request_id: "timeout".to_string(),
                            error: "Request timeout".to_string(),
                            error_code: 408,
                        });
                        false
                    } else {
                        true
                    }
                });
            }
        });
    }

    /// Update bridge statistics
    async fn update_stats(&self, execution_time: Duration, success: bool) {
        let mut stats = self.stats.lock().await;
        stats.total_requests += 1;
        stats.total_execution_time += execution_time;

        if success {
            stats.successful_requests += 1;
        } else {
            stats.failed_requests += 1;
        }

        let current_pending = self.pending_requests.read().await.len();
        if current_pending > stats.peak_concurrent_requests {
            stats.peak_concurrent_requests = current_pending;
        }
    }

    /// Get current memory usage (placeholder implementation)
    fn get_memory_usage(&self) -> u64 {
        // In a real implementation, this would get actual memory usage
        // For now, return a placeholder value
        100 // MB
    }
}

impl BridgeResponse {
    /// Check if response indicates success
    fn is_success(&self) -> bool {
        match self {
            BridgeResponse::Error { .. } => false,
            _ => true,
        }
    }
}

/// Elixir GenServer integration utilities
pub mod elixir_integration {
    use super::*;
    use serde_json;

    /// Convert bridge message from JSON string (from Elixir)
    pub fn from_elixir_json(json: &str) -> Result<BridgeMessage, serde_json::Error> {
        serde_json::from_str(json)
    }

    /// Convert bridge response to JSON string (to Elixir)
    pub fn to_elixir_json(response: &BridgeResponse) -> Result<String, serde_json::Error> {
        serde_json::to_string(response)
    }

    /// Create a health check message
    pub fn create_health_check() -> BridgeMessage {
        BridgeMessage::HealthCheck {
            request_id: Uuid::new_v4().to_string(),
        }
    }

    /// Create an instruction execution message
    pub fn create_execute_instruction(
        program_id: &str,
        accounts: Vec<&str>,
        data: Vec<u8>,
        account_infos: Vec<SerializableAccountInfo>,
    ) -> BridgeMessage {
        BridgeMessage::ExecuteInstruction {
            request_id: Uuid::new_v4().to_string(),
            instruction: SerializableSVMInstruction {
                program_id: program_id.to_string(),
                accounts: accounts.into_iter().map(|s| s.to_string()).collect(),
                data,
            },
            accounts: account_infos,
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use tokio::time::timeout;

    #[tokio::test]
    async fn test_bridge_creation() {
        let svm_runtime = SVMRuntimeCore::new(Default::default()).await.unwrap();
        let (mut bridge, _sender, _receiver) = GenServerBridge::new(svm_runtime);

        assert!(bridge.message_receiver.is_some());
    }

    #[tokio::test]
    async fn test_health_check() {
        let svm_runtime = SVMRuntimeCore::new(Default::default()).await.unwrap();
        let bridge = GenServerBridge::new(svm_runtime).0;

        let response = bridge.handle_health_check("test-123".to_string()).await;

        match response {
            BridgeResponse::HealthStatus { request_id, .. } => {
                assert_eq!(request_id, "test-123");
            },
            _ => panic!("Expected health status response"),
        }
    }

    #[tokio::test]
    async fn test_serialization() {
        let message = BridgeMessage::HealthCheck {
            request_id: "test-456".to_string(),
        };

        let json = serde_json::to_string(&message).unwrap();
        let deserialized: BridgeMessage = serde_json::from_str(&json).unwrap();

        match deserialized {
            BridgeMessage::HealthCheck { request_id } => {
                assert_eq!(request_id, "test-456");
            },
            _ => panic!("Deserialization failed"),
        }
    }
}

