    @moduledoc """
    Result of SVM instruction execution.
    """

    defstruct [
      :request_id,
      :result,
      :execution_time_ms,
      :gas_used
    ]

    @type t :: %__MODULE__{
      request_id: String.t(),
      result: {:ok, map()} | {:error, String.t()},
      execution_time_ms: non_neg_integer(),
      gas_used: non_neg_integer()
    }
  end

  defmodule AccountData do
    @moduledoc """
    Account query response.
    """

    defstruct [
      :request_id,
      :account
    ]

    @type t :: %__MODULE__{
      request_id: String.t(),
      account: APEOs.SVMBridge.AccountInfo.t() | nil
    }
  end

  defmodule HealthStatus do
    @moduledoc """
    Health check response.
    """

    defstruct [
      :request_id,
      :status
    ]

    @type t :: %__MODULE__{
      request_id: String.t(),
      status: map()
    }
  end

  defmodule BatchResult do
    @moduledoc """
    Batch execution response.
    """

    defstruct [
      :request_id,
      :results,
      :total_gas_used,
      :execution_time_ms
    ]

    @type t :: %__MODULE__{
      request_id: String.t(),
      results: list(),
      total_gas_used: non_neg_integer(),
      execution_time_ms: non_neg_integer()
    }
  end

  defmodule Error do
    @moduledoc """
    Error response.
    """

    defstruct [
      :request_id,
      :error,
      :error_code
    ]

    @type t :: %__MODULE__{
      request_id: String.t(),
      error: String.t(),
      error_code: non_neg_integer()
    }
  end
end

