//! SVM Bridge Binary
//!
//! This binary serves as the bridge process between Elixir GenServer
//! and the SVM runtime, handling JSON communication over stdin/stdout.

use std::io::{self, BufRead, BufReader, Write};
use std::sync::Arc;
use tokio::sync::Mutex;
use tracing::{info, error, debug};
use clap::Parser;

use apechain_cdk::genserver_bridge::{
    GenServerBridge, BridgeMessage, BridgeResponse, elixir_integration
};
use apechain_cdk::svm_runtime::{SVMRuntimeCore, SVMRuntimeConfig};

#[derive(Parser, Debug)]
#[command(name = "svm_bridge")]
#[command(about = "SVM Runtime Bridge for GenServer Communication")]
struct Args {
    /// Enable JSON communication mode for GenServer integration
    #[arg(long)]
    json_mode: bool,

    /// Configuration file path
    #[arg(short, long, default_value = "svm_config.toml")]
    config: String,

    /// Log level
    #[arg(short, long, default_value = "info")]
    log_level: String,
}

#[tokio::main]
async fn main() -> Result<(), Box<dyn std::error::Error>> {
    let args = Args::parse();

    // Initialize tracing
    tracing_subscriber::fmt()
        .with_env_filter(&args.log_level)
        .init();

    info!("Starting SVM Bridge with JSON mode: {}", args.json_mode);

    if args.json_mode {
        run_json_mode().await
    } else {
        run_interactive_mode().await
    }
}

/// Run in JSON mode for GenServer communication
async fn run_json_mode() -> Result<(), Box<dyn std::error::Error>> {
    // Initialize SVM runtime
    let config = SVMRuntimeConfig::default();
    let svm_runtime = SVMRuntimeCore::new(config).await?;

    // Create bridge
    let (mut bridge, message_sender, mut response_receiver) = GenServerBridge::new(svm_runtime);

    // Start bridge in background
    let bridge_handle = tokio::spawn(async move {
        if let Err(e) = bridge.start().await {
            error!("Bridge error: {}", e);
        }
    });

    // Handle responses (write to stdout)
    let response_handle = tokio::spawn(async move {
        while let Some(response) = response_receiver.recv().await {
            match elixir_integration::to_elixir_json(&response) {
                Ok(json) => {
                    if let Err(e) = write_response(&json).await {
                        error!("Failed to write response: {}", e);
                        break;
                    }
                },
                Err(e) => {
                    error!("Failed to serialize response: {}", e);
                }
            }
        }
    });

    // Handle input (read from stdin)
    let stdin = io::stdin();
    let reader = BufReader::new(stdin);
    let mut lines = reader.lines();

    while let Some(line) = lines.next() {
        let line = line?;
        debug!("Received line: {}", line);

        match elixir_integration::from_elixir_json(&line) {
            Ok(message) => {
                if let Err(e) = message_sender.send(message).await {
                    error!("Failed to send message to bridge: {}", e);
                    break;
                }
            },
            Err(e) => {
                error!("Failed to parse JSON message: {} - Line: {}", e, line);

                // Send error response
                let error_response = BridgeResponse::Error {
                    request_id: "unknown".to_string(),
                    error: format!("Invalid JSON: {}", e),
                    error_code: 400,
                };

                if let Ok(json) = elixir_integration::to_elixir_json(&error_response) {
                    let _ = write_response(&json).await;
                }
            }
        }
    }

    info!("Stdin closed, shutting down bridge");

    // Wait for background tasks to complete
    let _ = tokio::join!(bridge_handle, response_handle);

    Ok(())
}

/// Run in interactive mode for testing and debugging
async fn run_interactive_mode() -> Result<(), Box<dyn std::error::Error>> {
    println!("SVM Bridge Interactive Mode");
    println!("Type 'help' for available commands");

    // Initialize SVM runtime
    let config = SVMRuntimeConfig::default();
    let svm_runtime = Arc::new(Mutex::new(SVMRuntimeCore::new(config).await?));

    let stdin = io::stdin();
    let mut input = String::new();

    loop {
        print!("> ");
        io::stdout().flush()?;
        input.clear();
        stdin.read_line(&mut input)?;

        let cmd = input.trim();
        if cmd.is_empty() {
            continue;
        }

        match cmd {
            "help" => print_help(),
            "health" => {
                let health_msg = elixir_integration::create_health_check();
                println!("Health check message: {}", 
                    serde_json::to_string_pretty(&health_msg)?);
            },
            "quit" | "exit" => {
                println!("Goodbye!");
                break;
            },
            "stats" => {
                println!("Runtime statistics would be displayed here");
            },
            _ if cmd.starts_with("exec ") => {
                let json_part = &cmd[5..];
                match serde_json::from_str::<serde_json::Value>(json_part) {
                    Ok(value) => {
                        println!("Would execute: {}", serde_json::to_string_pretty(&value)?);
                    },
                    Err(e) => {
                        println!("Invalid JSON: {}", e);
                    }
                }
            },
            _ => {
                println!("Unknown command: {}. Type 'help' for available commands.", cmd);
            }
        }
    }

    Ok(())
}

/// Write response to stdout with proper formatting for GenServer
async fn write_response(json: &str) -> Result<(), Box<dyn std::error::Error>> {
    let mut stdout = tokio::io::stdout();
    stdout.write_all(json.as_bytes()).await?;
    stdout.write_all(b"\n").await?;
    stdout.flush().await?;
    Ok(())
}

fn print_help() {
    println!("Available commands:");
    println!("  help          - Show this help message");
    println!("  health        - Create a health check message");
    println!("  stats         - Show runtime statistics");
    println!("  exec <json>   - Execute instruction from JSON");
    println!("  quit/exit     - Exit the bridge");
}

#[cfg(test)]
mod tests {
    use super::*;

    #[tokio::test]
    async fn test_json_serialization() {
        let health_msg = elixir_integration::create_health_check();
        let json = elixir_integration::to_elixir_json(&BridgeMessage::HealthCheck {
            request_id: "test".to_string()
        }).unwrap();

        assert!(json.contains("HealthCheck"));
        assert!(json.contains("test"));
    }
}

