//! Hardware Security Module (HSM) Integration
//!
//! This module provides secure private key storage and signing operations
//! using Hardware Security Modules to protect against key extraction attacks
//! and ensure tamper-resistant cryptographic operations for the Solana L1 integration.

use std::collections::HashMap;
use std::sync::Arc;
use tokio::sync::{RwLock, Mutex};
use serde::{Serialize, Deserialize};
use tracing::{info, warn, error, instrument};
use thiserror::Error;
use uuid::Uuid;
use chrono::{DateTime, Utc};

use crate::svm_runtime::Pubkey;

/// HSM-specific errors
#[derive(Error, Debug)]
pub enum HSMError {
    #[error("HSM device not available: {0}")]
    DeviceNotAvailable(String),

    #[error("Key not found: {0}")]
    KeyNotFound(String),

    #[error("Signing operation failed: {0}")]
    SigningFailed(String),

    #[error("Key generation failed: {0}")]
    KeyGenerationFailed(String),

    #[error("HSM authentication failed: {0}")]
    AuthenticationFailed(String),

    #[error("Invalid signature format: {0}")]
    InvalidSignature(String),

    #[error("HSM configuration error: {0}")]
    ConfigurationError(String),

    #[error("Security policy violation: {0}")]
    PolicyViolation(String),
}

/// HSM device configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct HSMConfig {
    /// HSM device identifier
    pub device_id: String,

    /// Authentication credentials
    pub auth_token: String,

    /// Maximum concurrent operations
    pub max_concurrent_ops: usize,

    /// Operation timeout in seconds
    pub operation_timeout: u64,

    /// Key rotation interval in hours
    pub key_rotation_interval: u64,

    /// Enable audit logging
    pub audit_logging: bool,

    /// Emergency shutdown triggers
    pub emergency_triggers: Vec<String>,

    /// Backup HSM configurations
    pub backup_hsms: Vec<HSMBackupConfig>,
}

/// Backup HSM configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct HSMBackupConfig {
    pub device_id: String,
    pub auth_token: String,
    pub priority: u8,
}

/// Key metadata stored in HSM
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct KeyMetadata {
    /// Unique key identifier
    pub key_id: String,

    /// Public key
    pub public_key: Pubkey,

    /// Key purpose (signing, validation, etc.)
    pub purpose: KeyPurpose,

    /// Creation timestamp
    pub created_at: DateTime<Utc>,

    /// Last rotation timestamp
    pub last_rotated: Option<DateTime<Utc>>,

    /// Usage counter
    pub usage_count: u64,

    /// Maximum usage limit
    pub usage_limit: Option<u64>,

    /// Key status
    pub status: KeyStatus,

    /// Associated permissions
    pub permissions: Vec<KeyPermission>,
}

/// Key purpose enumeration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum KeyPurpose {
    /// Transaction signing
    TransactionSigning,

    /// Multi-signature validation
    MultiSigValidation,

    /// Bridge operation signing
    BridgeSigning,

    /// Emergency operations
    EmergencyOps,

    /// Key rotation
    KeyRotation,

    /// Audit and compliance
    AuditSigning,
}

/// Key status enumeration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum KeyStatus {
    /// Active and available for use
    Active,

    /// Scheduled for rotation
    PendingRotation,

    /// Compromised or suspected compromise
    Compromised,

    /// Retired and no longer in use
    Retired,

    /// Emergency locked
    EmergencyLocked,
}

/// Key permission enumeration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum KeyPermission {
    /// Can sign transactions
    SignTransactions,

    /// Can validate signatures
    ValidateSignatures,

    /// Can rotate keys
    RotateKeys,

    /// Emergency shutdown capability
    EmergencyShutdown,

    /// Audit access
    AuditAccess,
}

/// Signature with HSM provenance
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct HSMSignature {
    /// The actual signature bytes
    pub signature: Vec<u8>,

    /// Key ID used for signing
    pub key_id: String,

    /// HSM device ID
    pub device_id: String,

    /// Signing timestamp
    pub timestamp: DateTime<Utc>,

    /// Signature algorithm used
    pub algorithm: SignatureAlgorithm,

    /// Audit trail reference
    pub audit_ref: String,
}

/// Supported signature algorithms
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum SignatureAlgorithm {
    Ed25519,
    Secp256k1,
    Secp256r1,
}

/// HSM operation audit log entry
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct HSMAuditEntry {
    /// Unique audit entry ID
    pub audit_id: String,

    /// Operation type
    pub operation: HSMOperation,

    /// Key ID involved
    pub key_id: Option<String>,

    /// Operation result
    pub result: OperationResult,

    /// Timestamp
    pub timestamp: DateTime<Utc>,

    /// HSM device ID
    pub device_id: String,

    /// Additional context
    pub context: HashMap<String, String>,
}

/// HSM operation types
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum HSMOperation {
    KeyGeneration,
    Signing,
    KeyRotation,
    KeyDeletion,
    Authentication,
    EmergencyShutdown,
    ConfigurationChange,
}

/// Operation result
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum OperationResult {
    Success,
    Failed(String),
    SecurityViolation(String),
}

/// HSM Manager for secure key operations
pub struct HSMManager {
    /// HSM configuration
    config: HSMConfig,

    /// Active keys metadata
    keys: Arc<RwLock<HashMap<String, KeyMetadata>>>,

    /// Operation semaphore for concurrency control
    operation_semaphore: Arc<tokio::sync::Semaphore>,

    /// Audit log
    audit_log: Arc<Mutex<Vec<HSMAuditEntry>>>,

    /// Emergency shutdown flag
    emergency_shutdown: Arc<tokio::sync::RwLock<bool>>,

    /// HSM device connection pool
    device_pool: Arc<Mutex<HSMDevicePool>>,
}

/// HSM device pool for connection management
struct HSMDevicePool {
    /// Primary HSM connection
    primary: Option<HSMDevice>,

    /// Backup HSM connections
    backups: Vec<HSMDevice>,

    /// Connection health status
    health_status: HashMap<String, bool>,
}

/// HSM device abstraction
struct HSMDevice {
    /// Device identifier
    device_id: String,

    /// Connection handle (mock for now - would be actual HSM SDK connection)
    connection_handle: String,

    /// Device status
    status: DeviceStatus,

    /// Last health check
    last_health_check: DateTime<Utc>,
}

/// Device status enumeration
#[derive(Debug, Clone)]
enum DeviceStatus {
    Connected,
    Disconnected,
    Error(String),
    Maintenance,
}

impl HSMManager {
    /// Create new HSM manager with configuration
    pub async fn new(config: HSMConfig) -> Result<Self, HSMError> {
        info!("Initializing HSM Manager with device: {}", config.device_id);

        // Validate configuration
        Self::validate_config(&config)?;

        // Create operation semaphore for concurrency control
        let operation_semaphore = Arc::new(
            tokio::sync::Semaphore::new(config.max_concurrent_ops)
        );

        // Initialize device pool
        let device_pool = Arc::new(Mutex::new(HSMDevicePool {
            primary: None,
            backups: Vec::new(),
            health_status: HashMap::new(),
        }));

        let manager = HSMManager {
            config,
            keys: Arc::new(RwLock::new(HashMap::new())),
            operation_semaphore,
            audit_log: Arc::new(Mutex::new(Vec::new())),
            emergency_shutdown: Arc::new(tokio::sync::RwLock::new(false)),
            device_pool,
        };

        // Initialize HSM connections
        manager.initialize_connections().await?;

        // Start background tasks
        manager.start_background_tasks().await;

        info!("HSM Manager initialized successfully");
        Ok(manager)
    }

    /// Generate a new key pair in HSM
    #[instrument(skip(self), fields(purpose = ?purpose))]
    pub async fn generate_key(&self, purpose: KeyPurpose) -> Result<KeyMetadata, HSMError> {
        // Check emergency shutdown
        if *self.emergency_shutdown.read().await {
            return Err(HSMError::PolicyViolation("System in emergency shutdown".to_string()));
        }

        // Acquire operation permit
        let _permit = self.operation_semaphore
            .acquire()
            .await
            .map_err(|e| HSMError::DeviceNotAvailable(e.to_string()))?;

        let key_id = Uuid::new_v4().to_string();
        info!("Generating new key with ID: {}", key_id);

        // Generate key in HSM (mock implementation - would use actual HSM SDK)
        let (public_key, _private_key_handle) = self.hsm_generate_keypair(&key_id).await?;

        // Create key metadata
        let metadata = KeyMetadata {
            key_id: key_id.clone(),
            public_key,
            purpose: purpose.clone(),
            created_at: Utc::now(),
            last_rotated: None,
            usage_count: 0,
            usage_limit: self.get_usage_limit(&purpose),
            status: KeyStatus::Active,
            permissions: self.get_default_permissions(&purpose),
        };

        // Store metadata
        self.keys.write().await.insert(key_id.clone(), metadata.clone());

        // Log audit entry
        self.log_audit_entry(HSMAuditEntry {
            audit_id: Uuid::new_v4().to_string(),
            operation: HSMOperation::KeyGeneration,
            key_id: Some(key_id),
            result: OperationResult::Success,
            timestamp: Utc::now(),
            device_id: self.config.device_id.clone(),
            context: [("purpose".to_string(), format!("{:?}", purpose))]
                .iter().cloned().collect(),
        }).await;

        info!("Key generation completed successfully");
        Ok(metadata)
    }

    /// Sign data using specified key
    #[instrument(skip(self, data), fields(key_id = %key_id, data_len = data.len()))]
    pub async fn sign(&self, key_id: &str, data: &[u8]) -> Result<HSMSignature, HSMError> {
        // Check emergency shutdown
        if *self.emergency_shutdown.read().await {
            return Err(HSMError::PolicyViolation("System in emergency shutdown".to_string()));
        }

        // Acquire operation permit
        let _permit = self.operation_semaphore
            .acquire()
            .await
            .map_err(|e| HSMError::DeviceNotAvailable(e.to_string()))?;

        // Get key metadata
        let mut keys = self.keys.write().await;
        let metadata = keys.get_mut(key_id)
            .ok_or_else(|| HSMError::KeyNotFound(key_id.to_string()))?;

        // Check key status
        match metadata.status {
            KeyStatus::Active => {},
            KeyStatus::Compromised => {
                return Err(HSMError::PolicyViolation(
                    format!("Key {} is compromised", key_id)
                ));
            },
            KeyStatus::Retired => {
                return Err(HSMError::PolicyViolation(
                    format!("Key {} is retired", key_id)
                ));
            },
            KeyStatus::EmergencyLocked => {
                return Err(HSMError::PolicyViolation(
                    format!("Key {} is emergency locked", key_id)
                ));
            },
            KeyStatus::PendingRotation => {
                warn!("Using key {} that is pending rotation", key_id);
            },
        }

        // Check usage limits
        if let Some(limit) = metadata.usage_limit {
            if metadata.usage_count >= limit {
                return Err(HSMError::PolicyViolation(
                    format!("Key {} has exceeded usage limit", key_id)
                ));
            }
        }

        // Perform HSM signing operation (mock implementation)
        let signature_bytes = self.hsm_sign(key_id, data).await?;

        // Update usage counter
        metadata.usage_count += 1;

        // Create signature with provenance
        let signature = HSMSignature {
            signature: signature_bytes,
            key_id: key_id.to_string(),
            device_id: self.config.device_id.clone(),
            timestamp: Utc::now(),
            algorithm: SignatureAlgorithm::Ed25519, // Default for Solana
            audit_ref: Uuid::new_v4().to_string(),
        };

        // Log audit entry
        self.log_audit_entry(HSMAuditEntry {
            audit_id: signature.audit_ref.clone(),
            operation: HSMOperation::Signing,
            key_id: Some(key_id.to_string()),
            result: OperationResult::Success,
            timestamp: signature.timestamp,
            device_id: self.config.device_id.clone(),
            context: [
                ("data_hash".to_string(), hex::encode(data)),
                ("algorithm".to_string(), format!("{:?}", signature.algorithm)),
            ].iter().cloned().collect(),
        }).await;

        info!("Signing operation completed successfully");
        Ok(signature)
    }

    /// Rotate a key (generate new key and mark old as retired)
    #[instrument(skip(self), fields(key_id = %key_id))]
    pub async fn rotate_key(&self, key_id: &str) -> Result<KeyMetadata, HSMError> {
        info!("Starting key rotation for key: {}", key_id);

        // Get current key metadata
        let keys = self.keys.read().await;
        let current_metadata = keys.get(key_id)
            .ok_or_else(|| HSMError::KeyNotFound(key_id.to_string()))?
            .clone();
        drop(keys);

        // Generate new key with same purpose
        let new_metadata = self.generate_key(current_metadata.purpose.clone()).await?;

        // Mark old key as retired
        let mut keys = self.keys.write().await;
        if let Some(old_key) = keys.get_mut(key_id) {
            old_key.status = KeyStatus::Retired;
        }

        // Log audit entry
        self.log_audit_entry(HSMAuditEntry {
            audit_id: Uuid::new_v4().to_string(),
            operation: HSMOperation::KeyRotation,
            key_id: Some(key_id.to_string()),
            result: OperationResult::Success,
            timestamp: Utc::now(),
            device_id: self.config.device_id.clone(),
            context: [
                ("old_key_id".to_string(), key_id.to_string()),
                ("new_key_id".to_string(), new_metadata.key_id.clone()),
            ].iter().cloned().collect(),
        }).await;

        info!("Key rotation completed successfully");
        Ok(new_metadata)
    }

    /// Emergency shutdown of HSM operations
    #[instrument(skip(self))]
    pub async fn emergency_shutdown(&self, reason: &str) -> Result<(), HSMError> {
        error!("EMERGENCY SHUTDOWN TRIGGERED: {}", reason);

        // Set emergency shutdown flag
        *self.emergency_shutdown.write().await = true;

        // Lock all active keys
        let mut keys = self.keys.write().await;
        for (_, metadata) in keys.iter_mut() {
            if metadata.status == KeyStatus::Active {
                metadata.status = KeyStatus::EmergencyLocked;
            }
        }

        // Log audit entry
        self.log_audit_entry(HSMAuditEntry {
            audit_id: Uuid::new_v4().to_string(),
            operation: HSMOperation::EmergencyShutdown,
            key_id: None,
            result: OperationResult::Success,
            timestamp: Utc::now(),
            device_id: self.config.device_id.clone(),
            context: [("reason".to_string(), reason.to_string())]
                .iter().cloned().collect(),
        }).await;

        // Additional emergency procedures would go here
        // (disconnect HSM, alert security team, etc.)

        error!("Emergency shutdown completed");
        Ok(())
    }

    /// Get key metadata
    pub async fn get_key_metadata(&self, key_id: &str) -> Result<KeyMetadata, HSMError> {
        let keys = self.keys.read().await;
        keys.get(key_id)
            .cloned()
            .ok_or_else(|| HSMError::KeyNotFound(key_id.to_string()))
    }

    /// List all active keys
    pub async fn list_active_keys(&self) -> Vec<KeyMetadata> {
        let keys = self.keys.read().await;
        keys.values()
            .filter(|metadata| metadata.status == KeyStatus::Active)
            .cloned()
            .collect()
    }

    /// Get audit log entries
    pub async fn get_audit_log(&self, limit: Option<usize>) -> Vec<HSMAuditEntry> {
        let audit_log = self.audit_log.lock().await;
        match limit {
            Some(n) => audit_log.iter().rev().take(n).cloned().collect(),
            None => audit_log.iter().rev().cloned().collect(),
        }
    }

    /// HSM health check
    pub async fn health_check(&self) -> Result<HSMHealthStatus, HSMError> {
        let device_pool = self.device_pool.lock().await;

        let status = HSMHealthStatus {
            primary_device_healthy: device_pool.primary.is_some(),
            backup_devices_healthy: device_pool.backups.len(),
            total_keys: self.keys.read().await.len(),
            active_keys: self.keys.read().await
                .values()
                .filter(|k| k.status == KeyStatus::Active)
                .count(),
            emergency_shutdown_active: *self.emergency_shutdown.read().await,
            last_operation: Utc::now(), // Would track actual last operation
        };

        Ok(status)
    }

    // Private helper methods

    fn validate_config(config: &HSMConfig) -> Result<(), HSMError> {
        if config.device_id.is_empty() {
            return Err(HSMError::ConfigurationError("Device ID cannot be empty".to_string()));
        }

        if config.max_concurrent_ops == 0 {
            return Err(HSMError::ConfigurationError("Max concurrent operations must be > 0".to_string()));
        }

        if config.operation_timeout == 0 {
            return Err(HSMError::ConfigurationError("Operation timeout must be > 0".to_string()));
        }

        Ok(())
    }

    async fn initialize_connections(&self) -> Result<(), HSMError> {
        // Mock HSM connection initialization
        // In real implementation, this would establish connections to actual HSM devices
        info!("Initializing HSM connections");

        let mut device_pool = self.device_pool.lock().await;

        // Initialize primary device
        device_pool.primary = Some(HSMDevice {
            device_id: self.config.device_id.clone(),
            connection_handle: "primary_connection".to_string(),
            status: DeviceStatus::Connected,
            last_health_check: Utc::now(),
        });

        // Initialize backup devices
        for backup_config in &self.config.backup_hsms {
            device_pool.backups.push(HSMDevice {
                device_id: backup_config.device_id.clone(),
                connection_handle: format!("backup_{}", backup_config.device_id),
                status: DeviceStatus::Connected,
                last_health_check: Utc::now(),
            });
        }

        info!("HSM connections initialized successfully");
        Ok(())
    }

    async fn start_background_tasks(&self) {
        // Start key rotation monitor
        let keys_clone = Arc::clone(&self.keys);
        let config_clone = self.config.clone();
        tokio::spawn(async move {
            Self::key_rotation_monitor(keys_clone, config_clone).await;
        });

        // Start health monitor
        let device_pool_clone = Arc::clone(&self.device_pool);
        tokio::spawn(async move {
            Self::health_monitor(device_pool_clone).await;
        });
    }

    async fn key_rotation_monitor(
        keys: Arc<RwLock<HashMap<String, KeyMetadata>>>,
        config: HSMConfig,
    ) {
        let mut interval = tokio::time::interval(
            tokio::time::Duration::from_secs(3600) // Check every hour
        );

        loop {
            interval.tick().await;

            let rotation_threshold = chrono::Duration::hours(config.key_rotation_interval as i64);
            let now = Utc::now();

            let keys_to_rotate: Vec<String> = {
                let keys_read = keys.read().await;
                keys_read
                    .iter()
                    .filter_map(|(key_id, metadata)| {
                        if metadata.status == KeyStatus::Active {
                            let key_age = now - metadata.created_at;
                            if key_age > rotation_threshold {
                                Some(key_id.clone())
                            } else {
                                None
                            }
                        } else {
                            None
                        }
                    })
                    .collect()
            };

            // Mark keys for rotation
            if !keys_to_rotate.is_empty() {
                let mut keys_write = keys.write().await;
                for key_id in keys_to_rotate {
                    if let Some(metadata) = keys_write.get_mut(&key_id) {
                        metadata.status = KeyStatus::PendingRotation;
                    }
                }
                info!("Marked {} keys for rotation", keys_write.len());
            }
        }
    }

    async fn health_monitor(device_pool: Arc<Mutex<HSMDevicePool>>) {
        let mut interval = tokio::time::interval(
            tokio::time::Duration::from_secs(60) // Check every minute
        );

        loop {
            interval.tick().await;

            let mut pool = device_pool.lock().await;

            // Check primary device health
            if let Some(ref mut device) = pool.primary {
                device.last_health_check = Utc::now();
                // In real implementation, perform actual health check
                pool.health_status.insert(device.device_id.clone(), true);
            }

            // Check backup device health
            for device in &mut pool.backups {
                device.last_health_check = Utc::now();
                pool.health_status.insert(device.device_id.clone(), true);
            }
        }
    }

    async fn hsm_generate_keypair(&self, key_id: &str) -> Result<(Pubkey, String), HSMError> {
        // Mock implementation - in real HSM, this would generate actual keypair
        // and return public key + private key handle

        // Simulate HSM operation delay
        tokio::time::sleep(tokio::time::Duration::from_millis(100)).await;

        // Generate mock public key (in real implementation, this comes from HSM)
        let public_key_bytes = [0u8; 32]; // Mock - would be actual public key
        let public_key = Pubkey::new_from_array(public_key_bytes);

        let private_key_handle = format!("hsm_key_handle_{}", key_id);

        info!("Generated keypair in HSM: {}", key_id);
        Ok((public_key, private_key_handle))
    }

    async fn hsm_sign(&self, key_id: &str, data: &[u8]) -> Result<Vec<u8>, HSMError> {
        // Mock implementation - in real HSM, this would perform actual signing

        // Simulate HSM signing delay
        tokio::time::sleep(tokio::time::Duration::from_millis(50)).await;

        // Mock signature (in real implementation, this comes from HSM)
        let signature = vec![0u8; 64]; // Mock 64-byte signature

        info!("Signed data with HSM key: {}", key_id);
        Ok(signature)
    }

    async fn log_audit_entry(&self, entry: HSMAuditEntry) {
        let mut audit_log = self.audit_log.lock().await;
        audit_log.push(entry);

        // Keep only last 10000 entries to prevent memory bloat
        if audit_log.len() > 10000 {
            audit_log.drain(0..1000);
        }
    }

    fn get_usage_limit(&self, purpose: &KeyPurpose) -> Option<u64> {
        match purpose {
            KeyPurpose::TransactionSigning => Some(1000000), // 1M transactions
            KeyPurpose::BridgeSigning => Some(100000),       // 100K bridge operations
            KeyPurpose::EmergencyOps => Some(10),            // 10 emergency operations
            _ => None, // No limit for other purposes
        }
    }

    fn get_default_permissions(&self, purpose: &KeyPurpose) -> Vec<KeyPermission> {
        match purpose {
            KeyPurpose::TransactionSigning => vec![
                KeyPermission::SignTransactions,
                KeyPermission::ValidateSignatures,
            ],
            KeyPurpose::MultiSigValidation => vec![
                KeyPermission::ValidateSignatures,
            ],
            KeyPurpose::BridgeSigning => vec![
                KeyPermission::SignTransactions,
                KeyPermission::ValidateSignatures,
            ],
            KeyPurpose::EmergencyOps => vec![
                KeyPermission::SignTransactions,
                KeyPermission::EmergencyShutdown,
            ],
            KeyPurpose::KeyRotation => vec![
                KeyPermission::RotateKeys,
                KeyPermission::SignTransactions,
            ],
            KeyPurpose::AuditSigning => vec![
                KeyPermission::AuditAccess,
                KeyPermission::SignTransactions,
            ],
        }
    }
}

/// HSM health status
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct HSMHealthStatus {
    pub primary_device_healthy: bool,
    pub backup_devices_healthy: usize,
    pub total_keys: usize,
    pub active_keys: usize,
    pub emergency_shutdown_active: bool,
    pub last_operation: DateTime<Utc>,
}

impl Default for HSMConfig {
    fn default() -> Self {
        HSMConfig {
            device_id: "default_hsm".to_string(),
            auth_token: "default_token".to_string(),
            max_concurrent_ops: 100,
            operation_timeout: 30,
            key_rotation_interval: 24 * 7, // Weekly rotation
            audit_logging: true,
            emergency_triggers: vec![
                "multiple_failed_authentications".to_string(),
                "suspicious_key_usage_pattern".to_string(),
                "device_tampering_detected".to_string(),
            ],
            backup_hsms: vec![],
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[tokio::test]
    async fn test_hsm_manager_initialization() {
        let config = HSMConfig::default();
        let manager = HSMManager::new(config).await.unwrap();

        let health = manager.health_check().await.unwrap();
        assert!(health.primary_device_healthy);
        assert!(!health.emergency_shutdown_active);
    }

    #[tokio::test]
    async fn test_key_generation() {
        let config = HSMConfig::default();
        let manager = HSMManager::new(config).await.unwrap();

        let metadata = manager
            .generate_key(KeyPurpose::TransactionSigning)
            .await
            .unwrap();

        assert_eq!(metadata.status, KeyStatus::Active);
        assert_eq!(metadata.purpose, KeyPurpose::TransactionSigning);
        assert!(metadata.usage_count == 0);
    }

    #[tokio::test]
    async fn test_signing_operation() {
        let config = HSMConfig::default();
        let manager = HSMManager::new(config).await.unwrap();

        let metadata = manager
            .generate_key(KeyPurpose::TransactionSigning)
            .await
            .unwrap();

        let data = b"test message to sign";
        let signature = manager
            .sign(&metadata.key_id, data)
            .await
            .unwrap();

        assert_eq!(signature.key_id, metadata.key_id);
        assert!(!signature.signature.is_empty());
        assert_eq!(signature.algorithm, SignatureAlgorithm::Ed25519);
    }

    #[tokio::test]
    async fn test_emergency_shutdown() {
        let config = HSMConfig::default();
        let manager = HSMManager::new(config).await.unwrap();

        // Generate a key
        let metadata = manager
            .generate_key(KeyPurpose::TransactionSigning)
            .await
            .unwrap();

        // Trigger emergency shutdown
        manager
            .emergency_shutdown("Test emergency")
            .await
            .unwrap();

        // Verify shutdown state
        let health = manager.health_check().await.unwrap();
        assert!(health.emergency_shutdown_active);

        // Verify signing is blocked
        let result = manager
            .sign(&metadata.key_id, b"test data")
            .await;

        assert!(matches!(result, Err(HSMError::PolicyViolation(_))));
    }

    #[tokio::test]
    async fn test_key_rotation() {
        let config = HSMConfig::default();
        let manager = HSMManager::new(config).await.unwrap();

        // Generate original key
        let original_metadata = manager
            .generate_key(KeyPurpose::TransactionSigning)
            .await
            .unwrap();

        // Rotate the key
        let new_metadata = manager
            .rotate_key(&original_metadata.key_id)
            .await
            .unwrap();

        // Verify new key is different
        assert_ne!(new_metadata.key_id, original_metadata.key_id);
        assert_eq!(new_metadata.purpose, original_metadata.purpose);

        // Verify original key is retired
        let original_updated = manager
            .get_key_metadata(&original_metadata.key_id)
            .await
            .unwrap();
        assert_eq!(original_updated.status, KeyStatus::Retired);
    }

    #[tokio::test]
    async fn test_audit_logging() {
        let config = HSMConfig::default();
        let manager = HSMManager::new(config).await.unwrap();

        // Perform some operations
        let metadata = manager
            .generate_key(KeyPurpose::TransactionSigning)
            .await
            .unwrap();

        manager
            .sign(&metadata.key_id, b"test data")
            .await
            .unwrap();

        // Check audit log
        let audit_entries = manager.get_audit_log(None).await;
        assert!(audit_entries.len() >= 2); // At least key generation + signing

        // Verify audit entry structure
        let key_gen_entry = audit_entries
            .iter()
            .find(|entry| matches!(entry.operation, HSMOperation::KeyGeneration))
            .unwrap();

        assert_eq!(key_gen_entry.result, OperationResult::Success);
        assert!(key_gen_entry.key_id.is_some());
    }
}

