//! Multi-Signature Validation System
//!
//! This module implements a secure multi-signature validation system for bridge operations,
//! requiring consensus from multiple independent validators to prevent single points of failure
//! and unauthorized transactions.

use std::collections::{HashMap, BTreeMap};
use std::sync::Arc;
use tokio::sync::{RwLock, Mutex};
use serde::{Serialize, Deserialize};
use tracing::{info, warn, error, instrument};
use thiserror::Error;
use uuid::Uuid;
use chrono::{DateTime, Utc, Duration};

use crate::svm_runtime::Pubkey;
use crate::security::hsm_integration::{HSMManager, HSMSignature, KeyPurpose};

/// Multi-signature validation errors
#[derive(Error, Debug)]
pub enum MultiSigError {
    #[error("Insufficient signatures: required {required}, got {received}")]
    InsufficientSignatures { required: usize, received: usize },

    #[error("Invalid signature from validator {validator}: {reason}")]
    InvalidSignature { validator: String, reason: String },

    #[error("Unknown validator: {validator}")]
    UnknownValidator { validator: String },

    #[error("Validator not authorized: {validator}")]
    UnauthorizedValidator { validator: String },

    #[error("Transaction expired: {transaction_id}")]
    TransactionExpired { transaction_id: String },

    #[error("Duplicate signature from validator: {validator}")]
    DuplicateSignature { validator: String },

    #[error("Validator consensus threshold not met")]
    ConsensusThresholdNotMet,

    #[error("HSM operation failed: {0}")]
    HSMOperationFailed(String),

    #[error("Configuration error: {0}")]
    ConfigurationError(String),

    #[error("Validation timeout: {0}")]
    ValidationTimeout(String),
}

/// Multi-signature configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MultiSigConfig {
    /// Minimum required signatures for validation
    pub min_signatures: usize,

    /// Total number of validators
    pub total_validators: usize,

    /// Transaction validation timeout in seconds
    pub validation_timeout: u64,

    /// Maximum pending validations
    pub max_pending_validations: usize,

    /// Validator key rotation interval in hours
    pub validator_key_rotation_interval: u64,

    /// Enable fraud detection
    pub fraud_detection_enabled: bool,

    /// Emergency threshold (unanimous consent required)
    pub emergency_threshold: usize,

    /// Geographic distribution requirements
    pub geographic_distribution: Vec<String>,
}

/// Validator information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ValidatorInfo {
    /// Unique validator identifier
    pub validator_id: String,

    /// Validator public key
    pub public_key: Pubkey,

    /// Validator name/organization
    pub name: String,

    /// Geographic location
    pub location: String,

    /// Validator weight in consensus (1-10)
    pub weight: u8,

    /// Authorization status
    pub authorized: bool,

    /// Last activity timestamp
    pub last_activity: DateTime<Utc>,

    /// Reputation score (0-100)
    pub reputation: u8,

    /// Contact information for emergency
    pub emergency_contact: String,
}

/// Signature validation result
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SignatureValidation {
    /// Validator ID
    pub validator_id: String,

    /// Signature bytes
    pub signature: Vec<u8>,

    /// Validation timestamp
    pub timestamp: DateTime<Utc>,

    /// Signature validity
    pub is_valid: bool,

    /// Validation message
    pub message: String,

    /// Validator weight
    pub weight: u8,
}

/// Pending validation tracking
#[derive(Debug, Clone)]
struct PendingValidation {
    /// Transaction ID being validated
    pub transaction_id: String,

    /// Transaction data hash
    pub data_hash: Vec<u8>,

    /// Required signatures count
    pub required_signatures: usize,

    /// Received signatures
    pub signatures: HashMap<String, SignatureValidation>,

    /// Creation timestamp
    pub created_at: DateTime<Utc>,

    /// Expiry timestamp
    pub expires_at: DateTime<Utc>,

    /// Transaction priority
    pub priority: ValidationPriority,

    /// Validator responses received
    pub validator_responses: BTreeMap<String, ValidatorResponse>,
}

/// Validation priority levels
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
pub enum ValidationPriority {
    /// Low priority transaction
    Low,

    /// Standard priority transaction
    Standard,

    /// High priority transaction
    High,

    /// Emergency transaction (requires unanimous consent)
    Emergency,
}

/// Validator response to validation request
#[derive(Debug, Clone)]
struct ValidatorResponse {
    /// Response timestamp
    pub timestamp: DateTime<Utc>,

    /// Signature provided
    pub signature: Option<Vec<u8>>,

    /// Response status
    pub status: ResponseStatus,

    /// Response message
    pub message: String,
}

/// Response status from validator
#[derive(Debug, Clone)]
enum ResponseStatus {
    /// Signature provided and valid
    Approved,

    /// Signature rejected
    Rejected,

    /// Validator abstained from voting
    Abstained,

    /// No response received (timeout)
    NoResponse,

    /// Error in validation process
    Error(String),
}

/// Validation result summary
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ValidationResult {
    /// Transaction ID
    pub transaction_id: String,

    /// Validation successful
    pub success: bool,

    /// Required signatures
    pub required_signatures: usize,

    /// Received valid signatures
    pub received_signatures: usize,

    /// Total validator weight
    pub total_weight: u32,

    /// Validator signatures
    pub signatures: Vec<SignatureValidation>,

    /// Validation completion time
    pub completion_time: DateTime<Utc>,

    /// Validation duration in milliseconds
    pub validation_duration_ms: u64,

    /// Any errors encountered
    pub errors: Vec<String>,

    /// Geographic distribution achieved
    pub geographic_distribution: Vec<String>,
}

/// Multi-signature validator
pub struct MultiSigValidator {
    /// Configuration
    config: MultiSigConfig,

    /// Registered validators
    validators: Arc<RwLock<HashMap<String, ValidatorInfo>>>,

    /// Pending validations
    pending_validations: Arc<Mutex<HashMap<String, PendingValidation>>>,

    /// HSM manager for signature operations
    hsm_manager: Arc<HSMManager>,

    /// Validation statistics
    stats: Arc<Mutex<ValidationStats>>,

    /// Fraud detection system
    fraud_detector: Arc<Mutex<FraudDetector>>,
}

/// Validation statistics
#[derive(Debug, Default)]
struct ValidationStats {
    /// Total validations processed
    total_validations: u64,

    /// Successful validations
    successful_validations: u64,

    /// Failed validations
    failed_validations: u64,

    /// Average validation time
    average_validation_time_ms: f64,

    /// Validator performance
    validator_performance: HashMap<String, ValidatorPerformance>,
}

/// Individual validator performance metrics
#[derive(Debug, Default)]
struct ValidatorPerformance {
    /// Total responses provided
    total_responses: u64,

    /// Valid signatures provided
    valid_signatures: u64,

    /// Average response time
    average_response_time_ms: f64,

    /// Availability percentage
    availability_percentage: f64,
}

/// Fraud detection system
#[derive(Debug, Default)]
struct FraudDetector {
    /// Suspicious activity patterns
    suspicious_patterns: Vec<SuspiciousPattern>,

    /// Validator behavior tracking
    validator_behavior: HashMap<String, ValidatorBehaviorTracker>,
}

/// Suspicious activity pattern
#[derive(Debug)]
struct SuspiciousPattern {
    /// Pattern description
    pub description: String,

    /// Pattern detection threshold
    pub threshold: f64,

    /// Current score
    pub current_score: f64,

    /// Last detected
    pub last_detected: DateTime<Utc>,
}

/// Validator behavior tracker
#[derive(Debug, Default)]
struct ValidatorBehaviorTracker {
    /// Recent response times
    pub recent_response_times: Vec<u64>,

    /// Signature consistency score
    pub signature_consistency: f64,

    /// Geographic location consistency
    pub location_consistency: f64,

    /// Anomaly score
    pub anomaly_score: f64,
}

impl MultiSigValidator {
    /// Create new multi-signature validator
    pub async fn new(
        config: MultiSigConfig,
        hsm_manager: Arc<HSMManager>,
    ) -> Result<Self, MultiSigError> {
        info!("Initializing Multi-Signature Validator");

        // Validate configuration
        Self::validate_config(&config)?;

        let validator = MultiSigValidator {
            config,
            validators: Arc::new(RwLock::new(HashMap::new())),
            pending_validations: Arc::new(Mutex::new(HashMap::new())),
            hsm_manager,
            stats: Arc::new(Mutex::new(ValidationStats::default())),
            fraud_detector: Arc::new(Mutex::new(FraudDetector::default())),
        };

        // Start background tasks
        validator.start_background_tasks().await;

        info!("Multi-Signature Validator initialized successfully");
        Ok(validator)
    }

    /// Register a new validator
    #[instrument(skip(self), fields(validator_id = %validator_info.validator_id))]
    pub async fn register_validator(
        &self,
        validator_info: ValidatorInfo,
    ) -> Result<(), MultiSigError> {
        info!("Registering validator: {}", validator_info.validator_id);

        // Validate validator info
        self.validate_validator_info(&validator_info).await?;

        // Add to validator list
        let mut validators = self.validators.write().await;
        validators.insert(validator_info.validator_id.clone(), validator_info.clone());

        // Initialize performance tracking
        let mut stats = self.stats.lock().await;
        stats.validator_performance.insert(
            validator_info.validator_id.clone(),
            ValidatorPerformance::default(),
        );

        info!("Validator registered successfully: {}", validator_info.validator_id);
        Ok(())
    }

    /// Request multi-signature validation for a transaction
    #[instrument(skip(self, data), fields(transaction_id = %transaction_id, data_len = data.len()))]
    pub async fn request_validation(
        &self,
        transaction_id: String,
        data: &[u8],
        priority: ValidationPriority,
    ) -> Result<String, MultiSigError> {
        info!("Requesting validation for transaction: {}", transaction_id);

        // Check if validation already exists
        {
            let pending = self.pending_validations.lock().await;
            if pending.contains_key(&transaction_id) {
                return Err(MultiSigError::ConfigurationError(
                    format!("Validation already in progress for transaction: {}", transaction_id)
                ));
            }
        }

        // Check pending validation limit
        {
            let pending = self.pending_validations.lock().await;
            if pending.len() >= self.config.max_pending_validations {
                return Err(MultiSigError::ConfigurationError(
                    "Maximum pending validations reached".to_string()
                ));
            }
        }

        // Determine required signatures based on priority
        let required_signatures = self.get_required_signatures(&priority).await;

        // Create validation request
        let validation = PendingValidation {
            transaction_id: transaction_id.clone(),
            data_hash: self.hash_data(data),
            required_signatures,
            signatures: HashMap::new(),
            created_at: Utc::now(),
            expires_at: Utc::now() + Duration::seconds(self.config.validation_timeout as i64),
            priority,
            validator_responses: BTreeMap::new(),
        };

        // Store pending validation
        {
            let mut pending = self.pending_validations.lock().await;
            pending.insert(transaction_id.clone(), validation);
        }

        // Send validation requests to all active validators
        self.send_validation_requests(&transaction_id, data).await?;

        info!("Validation request created successfully: {}", transaction_id);
        Ok(transaction_id)
    }

    /// Submit validator signature for a pending validation
    #[instrument(skip(self, signature), fields(validator_id = %validator_id, transaction_id = %transaction_id))]
    pub async fn submit_signature(
        &self,
        transaction_id: &str,
        validator_id: &str,
        signature: Vec<u8>,
    ) -> Result<ValidationResult, MultiSigError> {
        info!("Receiving signature from validator: {} for transaction: {}", validator_id, transaction_id);

        // Get validator info
        let validator_info = {
            let validators = self.validators.read().await;
            validators.get(validator_id)
                .ok_or_else(|| MultiSigError::UnknownValidator {
                    validator: validator_id.to_string()
                })?
                .clone()
        };

        // Check authorization
        if !validator_info.authorized {
            return Err(MultiSigError::UnauthorizedValidator {
                validator: validator_id.to_string()
            });
        }

        // Get pending validation
        let mut validation_complete = false;
        let mut validation_result = None;

        {
            let mut pending = self.pending_validations.lock().await;
            if let Some(validation) = pending.get_mut(transaction_id) {
                // Check expiry
                if Utc::now() > validation.expires_at {
                    return Err(MultiSigError::TransactionExpired {
                        transaction_id: transaction_id.to_string()
                    });
                }

                // Check for duplicate signature
                if validation.signatures.contains_key(validator_id) {
                    return Err(MultiSigError::DuplicateSignature {
                        validator: validator_id.to_string()
                    });
                }

                // Verify signature
                let signature_validation = self.verify_signature(
                    &validator_info,
                    &validation.data_hash,
                    &signature,
                ).await?;

                // Store signature
                validation.signatures.insert(
                    validator_id.to_string(),
                    signature_validation.clone()
                );

                // Update validator response
                validation.validator_responses.insert(
                    validator_id.to_string(),
                    ValidatorResponse {
                        timestamp: Utc::now(),
                        signature: Some(signature),
                        status: if signature_validation.is_valid {
                            ResponseStatus::Approved
                        } else {
                            ResponseStatus::Rejected
                        },
                        message: signature_validation.message.clone(),
                    }
                );

                // Check if validation is complete
                let valid_signatures = validation.signatures
                    .values()
                    .filter(|sig| sig.is_valid)
                    .count();

                if valid_signatures >= validation.required_signatures {
                    validation_complete = true;
                    validation_result = Some(self.create_validation_result(validation).await);
                }
            } else {
                return Err(MultiSigError::ConfigurationError(
                    format!("No pending validation found for transaction: {}", transaction_id)
                ));
            }
        }

        // If validation is complete, remove from pending and return result
        if validation_complete {
            let mut pending = self.pending_validations.lock().await;
            pending.remove(transaction_id);

            let result = validation_result.unwrap();

            // Update statistics
            self.update_validation_stats(&result, validator_id).await;

            info!("Validation completed for transaction: {}", transaction_id);
            Ok(result)
        } else {
            // Return interim result showing current status
            let pending = self.pending_validations.lock().await;
            let validation = pending.get(transaction_id).unwrap();
            let interim_result = self.create_validation_result(validation).await;
            Ok(interim_result)
        }
    }

    /// Get current validation status
    pub async fn get_validation_status(
        &self,
        transaction_id: &str,
    ) -> Result<ValidationResult, MultiSigError> {
        let pending = self.pending_validations.lock().await;
        let validation = pending.get(transaction_id)
            .ok_or_else(|| MultiSigError::ConfigurationError(
                format!("No validation found for transaction: {}", transaction_id)
            ))?;

        Ok(self.create_validation_result(validation).await)
    }

    /// Cancel a pending validation
    pub async fn cancel_validation(&self, transaction_id: &str) -> Result<(), MultiSigError> {
        info!("Cancelling validation for transaction: {}", transaction_id);

        let mut pending = self.pending_validations.lock().await;
        pending.remove(transaction_id)
            .ok_or_else(|| MultiSigError::ConfigurationError(
                format!("No pending validation found for transaction: {}", transaction_id)
            ))?;

        info!("Validation cancelled successfully: {}", transaction_id);
        Ok(())
    }

    /// Get list of registered validators
    pub async fn get_validators(&self) -> Vec<ValidatorInfo> {
        let validators = self.validators.read().await;
        validators.values().cloned().collect()
    }

    /// Get validation statistics
    pub async fn get_validation_stats(&self) -> ValidationStats {
        self.stats.lock().await.clone()
    }

    /// Update validator authorization status
    pub async fn update_validator_authorization(
        &self,
        validator_id: &str,
        authorized: bool,
    ) -> Result<(), MultiSigError> {
        let mut validators = self.validators.write().await;
        let validator = validators.get_mut(validator_id)
            .ok_or_else(|| MultiSigError::UnknownValidator {
                validator: validator_id.to_string()
            })?;

        validator.authorized = authorized;
        info!("Updated validator authorization: {} -> {}", validator_id, authorized);
        Ok(())
    }

    /// Emergency shutdown of validation system
    pub async fn emergency_shutdown(&self, reason: &str) -> Result<(), MultiSigError> {
        error!("MULTI-SIG EMERGENCY SHUTDOWN: {}", reason);

        // Clear all pending validations
        let mut pending = self.pending_validations.lock().await;
        pending.clear();

        // Deauthorize all validators
        let mut validators = self.validators.write().await;
        for validator in validators.values_mut() {
            validator.authorized = false;
        }

        // Trigger HSM emergency shutdown
        self.hsm_manager
            .emergency_shutdown(&format!("MultiSig emergency: {}", reason))
            .await
            .map_err(|e| MultiSigError::HSMOperationFailed(e.to_string()))?;

        error!("Multi-sig emergency shutdown completed");
        Ok(())
    }

    // Private helper methods

    fn validate_config(config: &MultiSigConfig) -> Result<(), MultiSigError> {
        if config.min_signatures == 0 {
            return Err(MultiSigError::ConfigurationError(
                "Minimum signatures must be greater than 0".to_string()
            ));
        }

        if config.min_signatures > config.total_validators {
            return Err(MultiSigError::ConfigurationError(
                "Minimum signatures cannot exceed total validators".to_string()
            ));
        }

        if config.validation_timeout == 0 {
            return Err(MultiSigError::ConfigurationError(
                "Validation timeout must be greater than 0".to_string()
            ));
        }

        Ok(())
    }

    async fn validate_validator_info(&self, validator_info: &ValidatorInfo) -> Result<(), MultiSigError> {
        if validator_info.validator_id.is_empty() {
            return Err(MultiSigError::ConfigurationError(
                "Validator ID cannot be empty".to_string()
            ));
        }

        if validator_info.name.is_empty() {
            return Err(MultiSigError::ConfigurationError(
                "Validator name cannot be empty".to_string()
            ));
        }

        if validator_info.weight == 0 || validator_info.weight > 10 {
            return Err(MultiSigError::ConfigurationError(
                "Validator weight must be between 1 and 10".to_string()
            ));
        }

        Ok(())
    }

    async fn get_required_signatures(&self, priority: &ValidationPriority) -> usize {
        match priority {
            ValidationPriority::Low => self.config.min_signatures,
            ValidationPriority::Standard => self.config.min_signatures,
            ValidationPriority::High => std::cmp::max(
                self.config.min_signatures,
                (self.config.total_validators * 2) / 3 // 2/3 majority
            ),
            ValidationPriority::Emergency => self.config.emergency_threshold,
        }
    }

    fn hash_data(&self, data: &[u8]) -> Vec<u8> {
        // Simple hash implementation - in production, use SHA-256 or Blake3
        use std::collections::hash_map::DefaultHasher;
        use std::hash::{Hash, Hasher};

        let mut hasher = DefaultHasher::new();
        data.hash(&mut hasher);
        hasher.finish().to_le_bytes().to_vec()
    }

    async fn send_validation_requests(&self, transaction_id: &str, data: &[u8]) -> Result<(), MultiSigError> {
        // In real implementation, this would send requests to validator nodes
        // For now, we'll just log the request
        info!("Sending validation requests for transaction: {}", transaction_id);

        let validators = self.validators.read().await;
        for validator in validators.values() {
            if validator.authorized {
                info!("Sending validation request to validator: {}", validator.validator_id);
                // Here you would implement actual network communication to validators
            }
        }

        Ok(())
    }

    async fn verify_signature(
        &self,
        validator_info: &ValidatorInfo,
        data_hash: &[u8],
        signature: &[u8],
    ) -> Result<SignatureValidation, MultiSigError> {
        // Mock signature verification - in real implementation, use actual cryptography
        let is_valid = signature.len() == 64; // Basic validation

        Ok(SignatureValidation {
            validator_id: validator_info.validator_id.clone(),
            signature: signature.to_vec(),
            timestamp: Utc::now(),
            is_valid,
            message: if is_valid {
                "Signature verified successfully".to_string()
            } else {
                "Invalid signature format".to_string()
            },
            weight: validator_info.weight,
        })
    }

    async fn create_validation_result(&self, validation: &PendingValidation) -> ValidationResult {
        let valid_signatures = validation.signatures
            .values()
            .filter(|sig| sig.is_valid)
            .collect::<Vec<_>>();

        let total_weight: u32 = valid_signatures
            .iter()
            .map(|sig| sig.weight as u32)
            .sum();

        let geographic_distribution: Vec<String> = {
            let validators = self.validators.read().await;
            valid_signatures
                .iter()
                .filter_map(|sig| {
                    validators.get(&sig.validator_id)
                        .map(|v| v.location.clone())
                })
                .collect()
        };

        ValidationResult {
            transaction_id: validation.transaction_id.clone(),
            success: valid_signatures.len() >= validation.required_signatures,
            required_signatures: validation.required_signatures,
            received_signatures: valid_signatures.len(),
            total_weight,
            signatures: validation.signatures.values().cloned().collect(),
            completion_time: Utc::now(),
            validation_duration_ms: (Utc::now() - validation.created_at).num_milliseconds() as u64,
            errors: Vec::new(), // Would collect actual errors
            geographic_distribution,
        }
    }

    async fn update_validation_stats(&self, result: &ValidationResult, validator_id: &str) {
        let mut stats = self.stats.lock().await;

        stats.total_validations += 1;
        if result.success {
            stats.successful_validations += 1;
        } else {
            stats.failed_validations += 1;
        }

        // Update average validation time
        let total_validations = stats.total_validations as f64;
        let current_avg = stats.average_validation_time_ms;
        let new_time = result.validation_duration_ms as f64;
        stats.average_validation_time_ms = 
            (current_avg * (total_validations - 1.0) + new_time) / total_validations;

        // Update validator performance
        if let Some(performance) = stats.validator_performance.get_mut(validator_id) {
            performance.total_responses += 1;
            if result.signatures.iter().any(|s| s.validator_id == validator_id && s.is_valid) {
                performance.valid_signatures += 1;
            }
        }
    }

    async fn start_background_tasks(&self) {
        // Start validation timeout monitor
        let pending_validations = Arc::clone(&self.pending_validations);
        tokio::spawn(async move {
            Self::validation_timeout_monitor(pending_validations).await;
        });

        // Start validator health monitor
        let validators = Arc::clone(&self.validators);
        tokio::spawn(async move {
            Self::validator_health_monitor(validators).await;
        });

        // Start fraud detection monitor
        let fraud_detector = Arc::clone(&self.fraud_detector);
        let stats = Arc::clone(&self.stats);
        tokio::spawn(async move {
            Self::fraud_detection_monitor(fraud_detector, stats).await;
        });
    }

    async fn validation_timeout_monitor(
        pending_validations: Arc<Mutex<HashMap<String, PendingValidation>>>,
    ) {
        let mut interval = tokio::time::interval(
            tokio::time::Duration::from_secs(10) // Check every 10 seconds
        );

        loop {
            interval.tick().await;

            let now = Utc::now();
            let mut to_remove = Vec::new();

            {
                let pending = pending_validations.lock().await;
                for (tx_id, validation) in pending.iter() {
                    if now > validation.expires_at {
                        to_remove.push(tx_id.clone());
                    }
                }
            }

            if !to_remove.is_empty() {
                let mut pending = pending_validations.lock().await;
                for tx_id in to_remove {
                    pending.remove(&tx_id);
                    warn!("Validation timed out for transaction: {}", tx_id);
                }
            }
        }
    }

    async fn validator_health_monitor(
        validators: Arc<RwLock<HashMap<String, ValidatorInfo>>>,
    ) {
        let mut interval = tokio::time::interval(
            tokio::time::Duration::from_secs(300) // Check every 5 minutes
        );

        loop {
            interval.tick().await;

            let mut validators = validators.write().await;
            let now = Utc::now();

            for (validator_id, validator) in validators.iter_mut() {
                // Check if validator has been inactive for too long
                let inactive_duration = now - validator.last_activity;
                if inactive_duration > Duration::hours(1) {
                    warn!("Validator {} has been inactive for {} minutes", 
                          validator_id, inactive_duration.num_minutes());

                    // Reduce reputation for inactive validators
                    if validator.reputation > 10 {
                        validator.reputation -= 1;
                    }
                }
            }
        }
    }

    async fn fraud_detection_monitor(
        _fraud_detector: Arc<Mutex<FraudDetector>>,
        _stats: Arc<Mutex<ValidationStats>>,
    ) {
        let mut interval = tokio::time::interval(
            tokio::time::Duration::from_secs(60) // Check every minute
        );

        loop {
            interval.tick().await;

            // Implement fraud detection algorithms
            // - Unusual response time patterns
            // - Geographic inconsistencies
            // - Signature pattern anomalies
            // - Validator collusion detection

            // For now, just placeholder
        }
    }
}

impl Default for MultiSigConfig {
    fn default() -> Self {
        MultiSigConfig {
            min_signatures: 3,
            total_validators: 5,
            validation_timeout: 300, // 5 minutes
            max_pending_validations: 100,
            validator_key_rotation_interval: 24 * 7, // Weekly
            fraud_detection_enabled: true,
            emergency_threshold: 5, // Unanimous
            geographic_distribution: vec![
                "US-East".to_string(),
                "EU-West".to_string(),
                "Asia-Pacific".to_string(),
            ],
        }
    }
}

