//! Transaction Time-Delay Security System
//!
//! This module implements configurable time delays for high-value transactions,
//! providing a security window for detecting and preventing unauthorized operations
//! before they are executed on the blockchain.

use std::collections::{HashMap, BTreeMap};
use std::sync::Arc;
use tokio::sync::{RwLock, Mutex};
use serde::{Serialize, Deserialize};
use tracing::{info, warn, error, instrument};
use thiserror::Error;
use uuid::Uuid;
use chrono::{DateTime, Utc, Duration};

use crate::svm_runtime::Pubkey;

/// Time delay security errors
#[derive(Error, Debug)]
pub enum DelayError {
    #[error("Transaction not found: {transaction_id}")]
    TransactionNotFound { transaction_id: String },

    #[error("Transaction already executed: {transaction_id}")]
    TransactionAlreadyExecuted { transaction_id: String },

    #[error("Transaction cancelled: {transaction_id}")]
    TransactionCancelled { transaction_id: String },

    #[error("Transaction expired: {transaction_id}")]
    TransactionExpired { transaction_id: String },

    #[error("Insufficient authorization for immediate execution")]
    InsufficientAuthorization,

    #[error("Emergency pause active - all transactions delayed")]
    EmergencyPauseActive,

    #[error("Invalid delay configuration: {0}")]
    InvalidDelayConfiguration(String),

    #[error("Security threshold exceeded: {0}")]
    SecurityThresholdExceeded(String),

    #[error("Transaction value exceeds maximum allowed: {value} > {maximum}")]
    ValueExceedsMaximum { value: u64, maximum: u64 },

    #[error("Configuration error: {0}")]
    ConfigurationError(String),
}

/// Time delay configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DelayConfig {
    /// Default delay for standard transactions (seconds)
    pub default_delay_seconds: u64,

    /// High-value transaction threshold (in lamports)
    pub high_value_threshold: u64,

    /// High-value transaction delay (seconds)
    pub high_value_delay_seconds: u64,

    /// Emergency transaction threshold (immediate execution)
    pub emergency_value_threshold: u64,

    /// Maximum transaction value allowed
    pub maximum_transaction_value: u64,

    /// Delay for cross-chain transactions (seconds)
    pub cross_chain_delay_seconds: u64,

    /// Maximum concurrent delayed transactions
    pub max_concurrent_delayed_transactions: usize,

    /// Authorization levels for early execution
    pub early_execution_authorization: Vec<AuthorizationLevel>,

    /// Emergency pause capability
    pub emergency_pause_enabled: bool,

    /// Automatic cancellation after delay period multiplier
    pub auto_cancel_multiplier: f64, // Cancel after delay_time * multiplier
}

/// Authorization levels for early execution
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AuthorizationLevel {
    /// Authorization level name
    pub name: String,

    /// Required signatures for this level
    pub required_signatures: usize,

    /// Authorized public keys
    pub authorized_keys: Vec<Pubkey>,

    /// Maximum value that can be authorized (lamports)
    pub max_authorized_value: u64,

    /// Delay reduction percentage (0-100)
    pub delay_reduction_percent: u8,
}

/// Transaction priority levels
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq, Eq, PartialOrd, Ord)]
pub enum TransactionPriority {
    /// Low priority - maximum delay
    Low,

    /// Standard priority - default delay
    Standard,

    /// High priority - reduced delay
    High,

    /// Critical priority - minimal delay
    Critical,

    /// Emergency priority - immediate execution (requires special auth)
    Emergency,
}

/// Transaction delay status
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
pub enum DelayStatus {
    /// Transaction is pending execution (in delay period)
    Pending,

    /// Transaction is ready for execution (delay period passed)
    Ready,

    /// Transaction has been executed
    Executed,

    /// Transaction has been cancelled
    Cancelled,

    /// Transaction has expired (auto-cancelled)
    Expired,

    /// Transaction is paused due to emergency
    EmergencyPaused,
}

/// Delayed transaction information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct DelayedTransaction {
    /// Unique transaction identifier
    pub transaction_id: String,

    /// Original transaction data
    pub transaction_data: Vec<u8>,

    /// Transaction value in lamports
    pub value: u64,

    /// Transaction type
    pub transaction_type: TransactionType,

    /// Priority level
    pub priority: TransactionPriority,

    /// Current status
    pub status: DelayStatus,

    /// Creation timestamp
    pub created_at: DateTime<Utc>,

    /// Scheduled execution time
    pub scheduled_execution: DateTime<Utc>,

    /// Actual execution time (if executed)
    pub executed_at: Option<DateTime<Utc>>,

    /// Submitter information
    pub submitter: TransactionSubmitter,

    /// Early execution authorizations received
    pub early_authorizations: Vec<EarlyExecutionAuthorization>,

    /// Security flags
    pub security_flags: Vec<SecurityFlag>,

    /// Associated metadata
    pub metadata: HashMap<String, String>,
}

/// Transaction type enumeration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum TransactionType {
    /// Standard transfer
    Transfer,

    /// Cross-chain bridge operation
    CrossChainBridge,

    /// Smart contract execution
    ContractExecution,

    /// Account creation
    AccountCreation,

    /// Token operation
    TokenOperation,

    /// Emergency operation
    EmergencyOperation,

    /// System administration
    SystemAdmin,
}

/// Transaction submitter information
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct TransactionSubmitter {
    /// Submitter public key
    pub public_key: Pubkey,

    /// Submitter identifier
    pub identifier: String,

    /// Submission timestamp
    pub submitted_at: DateTime<Utc>,

    /// Client information
    pub client_info: Option<String>,

    /// IP address (if available)
    pub ip_address: Option<String>,
}

/// Early execution authorization
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct EarlyExecutionAuthorization {
    /// Authorization ID
    pub authorization_id: String,

    /// Authorizer public key
    pub authorizer_key: Pubkey,

    /// Authorization level used
    pub authorization_level: String,

    /// Authorization timestamp
    pub authorized_at: DateTime<Utc>,

    /// Authorization signature
    pub signature: Vec<u8>,

    /// Authorization message
    pub message: String,
}

/// Security flag for enhanced monitoring
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SecurityFlag {
    /// Flag type
    pub flag_type: SecurityFlagType,

    /// Flag severity
    pub severity: SecuritySeverity,

    /// Flag description
    pub description: String,

    /// Detection timestamp
    pub detected_at: DateTime<Utc>,

    /// Flag source
    pub source: String,
}

/// Security flag types
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum SecurityFlagType {
    /// High value transaction
    HighValue,

    /// Unusual activity pattern
    UnusualActivity,

    /// Known risk indicator
    KnownRisk,

    /// Suspicious timing
    SuspiciousTiming,

    /// Geographic anomaly
    GeographicAnomaly,

    /// Rate limiting exceeded
    RateLimitExceeded,
}

/// Security severity levels
#[derive(Debug, Clone, Serialize, Deserialize, PartialEq, Eq, PartialOrd, Ord)]
pub enum SecuritySeverity {
    /// Low severity - informational
    Low,

    /// Medium severity - enhanced monitoring
    Medium,

    /// High severity - extended delay
    High,

    /// Critical severity - manual review required
    Critical,
}

/// Time delay manager
pub struct TimeDelayManager {
    /// Configuration
    config: DelayConfig,

    /// Delayed transactions
    delayed_transactions: Arc<RwLock<HashMap<String, DelayedTransaction>>>,

    /// Emergency pause state
    emergency_paused: Arc<RwLock<bool>>,

    /// Statistics tracking
    stats: Arc<Mutex<DelayStats>>,

    /// Security monitoring
    security_monitor: Arc<Mutex<SecurityMonitor>>,
}

/// Delay statistics
#[derive(Debug, Default)]
struct DelayStats {
    /// Total transactions processed
    total_transactions: u64,

    /// Transactions executed after delay
    delayed_executions: u64,

    /// Early executions authorized
    early_executions: u64,

    /// Cancelled transactions
    cancelled_transactions: u64,

    /// Average delay time (seconds)
    average_delay_time: f64,

    /// Security flags generated
    security_flags_generated: u64,

    /// Emergency pauses triggered
    emergency_pauses: u64,
}

/// Security monitoring system
#[derive(Debug, Default)]
struct SecurityMonitor {
    /// Recent transaction patterns
    recent_patterns: BTreeMap<DateTime<Utc>, TransactionPattern>,

    /// Suspicious activity indicators
    suspicious_indicators: Vec<SuspiciousActivity>,

    /// Rate limiting counters
    rate_limits: HashMap<String, RateLimitCounter>,
}

/// Transaction pattern for analysis
#[derive(Debug)]
struct TransactionPattern {
    /// Transaction count in time window
    pub transaction_count: usize,

    /// Total value in time window
    pub total_value: u64,

    /// Unique submitters
    pub unique_submitters: usize,

    /// Average transaction size
    pub average_size: f64,
}

/// Suspicious activity tracking
#[derive(Debug)]
struct SuspiciousActivity {
    /// Activity type
    pub activity_type: String,

    /// Severity level
    pub severity: SecuritySeverity,

    /// Description
    pub description: String,

    /// Detection time
    pub detected_at: DateTime<Utc>,

    /// Related transaction IDs
    pub related_transactions: Vec<String>,
}

/// Rate limiting counter
#[derive(Debug, Default)]
struct RateLimitCounter {
    /// Count in current window
    pub count: u64,

    /// Window start time
    pub window_start: DateTime<Utc>,

    /// Window duration
    pub window_duration: Duration,
}

impl TimeDelayManager {
    /// Create new time delay manager
    pub async fn new(config: DelayConfig) -> Result<Self, DelayError> {
        info!("Initializing Time Delay Security Manager");

        // Validate configuration
        Self::validate_config(&config)?;

        let manager = TimeDelayManager {
            config,
            delayed_transactions: Arc::new(RwLock::new(HashMap::new())),
            emergency_paused: Arc::new(RwLock::new(false)),
            stats: Arc::new(Mutex::new(DelayStats::default())),
            security_monitor: Arc::new(Mutex::new(SecurityMonitor::default())),
        };

        // Start background tasks
        manager.start_background_tasks().await;

        info!("Time Delay Security Manager initialized successfully");
        Ok(manager)
    }

    /// Submit transaction for delayed execution
    #[instrument(skip(self, transaction_data), fields(transaction_id = %transaction_id, value = %value))]
    pub async fn submit_delayed_transaction(
        &self,
        transaction_id: String,
        transaction_data: Vec<u8>,
        value: u64,
        transaction_type: TransactionType,
        priority: TransactionPriority,
        submitter: TransactionSubmitter,
    ) -> Result<DelayedTransaction, DelayError> {
        info!("Submitting transaction for delayed execution: {}", transaction_id);

        // Check emergency pause
        if *self.emergency_paused.read().await {
            return Err(DelayError::EmergencyPauseActive);
        }

        // Validate transaction value
        if value > self.config.maximum_transaction_value {
            return Err(DelayError::ValueExceedsMaximum {
                value,
                maximum: self.config.maximum_transaction_value,
            });
        }

        // Check concurrent transaction limit
        {
            let delayed_transactions = self.delayed_transactions.read().await;
            if delayed_transactions.len() >= self.config.max_concurrent_delayed_transactions {
                return Err(DelayError::ConfigurationError(
                    "Maximum concurrent delayed transactions reached".to_string()
                ));
            }
        }

        // Calculate delay based on value and priority
        let delay_seconds = self.calculate_delay(value, &priority, &transaction_type).await;

        // Generate security flags
        let security_flags = self.generate_security_flags(
            value,
            &transaction_type,
            &priority,
            &submitter
        ).await;

        // Create delayed transaction
        let now = Utc::now();
        let delayed_transaction = DelayedTransaction {
            transaction_id: transaction_id.clone(),
            transaction_data,
            value,
            transaction_type,
            priority,
            status: DelayStatus::Pending,
            created_at: now,
            scheduled_execution: now + Duration::seconds(delay_seconds as i64),
            executed_at: None,
            submitter,
            early_authorizations: Vec::new(),
            security_flags,
            metadata: HashMap::new(),
        };

        // Store delayed transaction
        {
            let mut delayed_transactions = self.delayed_transactions.write().await;
            delayed_transactions.insert(transaction_id.clone(), delayed_transaction.clone());
        }

        // Update statistics
        self.update_stats_new_transaction(delay_seconds).await;

        // Update security monitoring
        self.update_security_monitoring(&delayed_transaction).await;

        info!("Transaction submitted for delayed execution: {} (delay: {}s)", 
              transaction_id, delay_seconds);

        Ok(delayed_transaction)
    }

    /// Request early execution authorization
    #[instrument(skip(self, signature), fields(transaction_id = %transaction_id, authorizer = %authorizer_key.to_string()))]
    pub async fn request_early_execution(
        &self,
        transaction_id: &str,
        authorizer_key: Pubkey,
        authorization_level: &str,
        signature: Vec<u8>,
        message: String,
    ) -> Result<DelayedTransaction, DelayError> {
        info!("Requesting early execution for transaction: {}", transaction_id);

        // Get authorization level configuration
        let auth_level = self.config.early_execution_authorization
            .iter()
            .find(|level| level.name == authorization_level)
            .ok_or_else(|| DelayError::ConfigurationError(
                format!("Unknown authorization level: {}", authorization_level)
            ))?;

        // Check if authorizer is authorized
        if !auth_level.authorized_keys.contains(&authorizer_key) {
            return Err(DelayError::InsufficientAuthorization);
        }

        // Get and update transaction
        let mut updated_transaction = None;
        {
            let mut delayed_transactions = self.delayed_transactions.write().await;
            if let Some(transaction) = delayed_transactions.get_mut(transaction_id) {
                // Check transaction status
                match transaction.status {
                    DelayStatus::Pending => {},
                    DelayStatus::Ready => {},
                    DelayStatus::Executed => {
                        return Err(DelayError::TransactionAlreadyExecuted {
                            transaction_id: transaction_id.to_string()
                        });
                    },
                    DelayStatus::Cancelled => {
                        return Err(DelayError::TransactionCancelled {
                            transaction_id: transaction_id.to_string()
                        });
                    },
                    DelayStatus::Expired => {
                        return Err(DelayError::TransactionExpired {
                            transaction_id: transaction_id.to_string()
                        });
                    },
                    DelayStatus::EmergencyPaused => {
                        return Err(DelayError::EmergencyPauseActive);
                    },
                }

                // Check value authorization limit
                if transaction.value > auth_level.max_authorized_value {
                    return Err(DelayError::InsufficientAuthorization);
                }

                // Add authorization
                let authorization = EarlyExecutionAuthorization {
                    authorization_id: Uuid::new_v4().to_string(),
                    authorizer_key,
                    authorization_level: authorization_level.to_string(),
                    authorized_at: Utc::now(),
                    signature,
                    message,
                };

                transaction.early_authorizations.push(authorization);

                // Check if sufficient authorizations received
                let current_auth_count = transaction.early_authorizations
                    .iter()
                    .filter(|auth| auth.authorization_level == authorization_level)
                    .count();

                if current_auth_count >= auth_level.required_signatures {
                    // Calculate new execution time with delay reduction
                    let delay_reduction = auth_level.delay_reduction_percent as f64 / 100.0;
                    let original_delay = (transaction.scheduled_execution - transaction.created_at).num_seconds() as f64;
                    let new_delay = original_delay * (1.0 - delay_reduction);

                    transaction.scheduled_execution = transaction.created_at + 
                        Duration::seconds(new_delay as i64);

                    info!("Early execution authorized for transaction: {} (new delay: {}s)", 
                          transaction_id, new_delay);
                }

                updated_transaction = Some(transaction.clone());
            } else {
                return Err(DelayError::TransactionNotFound {
                    transaction_id: transaction_id.to_string()
                });
            }
        }

        // Update statistics
        self.update_stats_early_execution().await;

        Ok(updated_transaction.unwrap())
    }

    /// Cancel a delayed transaction
    #[instrument(skip(self), fields(transaction_id = %transaction_id))]
    pub async fn cancel_transaction(
        &self,
        transaction_id: &str,
        canceller_key: Pubkey,
        reason: String,
    ) -> Result<(), DelayError> {
        info!("Cancelling delayed transaction: {} (reason: {})", transaction_id, reason);

        let mut delayed_transactions = self.delayed_transactions.write().await;
        if let Some(transaction) = delayed_transactions.get_mut(transaction_id) {
            // Check if cancellation is authorized
            // (In production, implement proper authorization logic)

            match transaction.status {
                DelayStatus::Pending | DelayStatus::Ready => {
                    transaction.status = DelayStatus::Cancelled;
                    transaction.metadata.insert("cancellation_reason".to_string(), reason);
                    transaction.metadata.insert("cancelled_by".to_string(), canceller_key.to_string());
                    transaction.metadata.insert("cancelled_at".to_string(), Utc::now().to_rfc3339());

                    // Update statistics
                    self.update_stats_cancellation().await;

                    info!("Transaction cancelled successfully: {}", transaction_id);
                    Ok(())
                },
                DelayStatus::Executed => {
                    Err(DelayError::TransactionAlreadyExecuted {
                        transaction_id: transaction_id.to_string()
                    })
                },
                DelayStatus::Cancelled => {
                    Err(DelayError::TransactionCancelled {
                        transaction_id: transaction_id.to_string()
                    })
                },
                DelayStatus::Expired => {
                    Err(DelayError::TransactionExpired {
                        transaction_id: transaction_id.to_string()
                    })
                },
                DelayStatus::EmergencyPaused => {
                    Err(DelayError::EmergencyPauseActive)
                },
            }
        } else {
            Err(DelayError::TransactionNotFound {
                transaction_id: transaction_id.to_string()
            })
        }
    }

    /// Get ready transactions for execution
    pub async fn get_ready_transactions(&self) -> Vec<DelayedTransaction> {
        let now = Utc::now();
        let delayed_transactions = self.delayed_transactions.read().await;

        delayed_transactions
            .values()
            .filter(|tx| {
                (tx.status == DelayStatus::Pending || tx.status == DelayStatus::Ready) &&
                now >= tx.scheduled_execution
            })
            .cloned()
            .collect()
    }

    /// Mark transaction as executed
    pub async fn mark_executed(&self, transaction_id: &str) -> Result<(), DelayError> {
        let mut delayed_transactions = self.delayed_transactions.write().await;
        if let Some(transaction) = delayed_transactions.get_mut(transaction_id) {
            transaction.status = DelayStatus::Executed;
            transaction.executed_at = Some(Utc::now());

            // Update statistics
            self.update_stats_execution().await;

            info!("Transaction marked as executed: {}", transaction_id);
            Ok(())
        } else {
            Err(DelayError::TransactionNotFound {
                transaction_id: transaction_id.to_string()
            })
        }
    }

    /// Get transaction status
    pub async fn get_transaction_status(&self, transaction_id: &str) -> Option<DelayedTransaction> {
        let delayed_transactions = self.delayed_transactions.read().await;
        delayed_transactions.get(transaction_id).cloned()
    }

    /// Emergency pause all transactions
    #[instrument(skip(self))]
    pub async fn emergency_pause(&self, reason: &str) -> Result<(), DelayError> {
        error!("EMERGENCY PAUSE TRIGGERED: {}", reason);

        // Set emergency pause flag
        *self.emergency_paused.write().await = true;

        // Mark all pending transactions as emergency paused
        let mut delayed_transactions = self.delayed_transactions.write().await;
        for transaction in delayed_transactions.values_mut() {
            if matches!(transaction.status, DelayStatus::Pending | DelayStatus::Ready) {
                transaction.status = DelayStatus::EmergencyPaused;
                transaction.metadata.insert("emergency_pause_reason".to_string(), reason.to_string());
                transaction.metadata.insert("paused_at".to_string(), Utc::now().to_rfc3339());
            }
        }

        // Update statistics
        self.update_stats_emergency_pause().await;

        error!("Emergency pause activated - all transactions paused");
        Ok(())
    }

    /// Resume from emergency pause
    pub async fn resume_from_emergency_pause(&self) -> Result<(), DelayError> {
        info!("Resuming from emergency pause");

        // Clear emergency pause flag
        *self.emergency_paused.write().await = false;

        // Resume paused transactions
        let mut delayed_transactions = self.delayed_transactions.write().await;
        for transaction in delayed_transactions.values_mut() {
            if transaction.status == DelayStatus::EmergencyPaused {
                transaction.status = DelayStatus::Pending;
                // Extend scheduled execution time to account for pause
                transaction.scheduled_execution = Utc::now() + Duration::seconds(60); // 1 minute grace period
            }
        }

        info!("Emergency pause lifted - transactions resumed");
        Ok(())
    }

    /// Get delay statistics
    pub async fn get_statistics(&self) -> DelayStats {
        self.stats.lock().await.clone()
    }

    // Private helper methods

    fn validate_config(config: &DelayConfig) -> Result<(), DelayError> {
        if config.default_delay_seconds == 0 {
            return Err(DelayError::InvalidDelayConfiguration(
                "Default delay must be greater than 0".to_string()
            ));
        }

        if config.high_value_threshold == 0 {
            return Err(DelayError::InvalidDelayConfiguration(
                "High value threshold must be greater than 0".to_string()
            ));
        }

        if config.maximum_transaction_value == 0 {
            return Err(DelayError::InvalidDelayConfiguration(
                "Maximum transaction value must be greater than 0".to_string()
            ));
        }

        Ok(())
    }

    async fn calculate_delay(
        &self,
        value: u64,
        priority: &TransactionPriority,
        transaction_type: &TransactionType,
    ) -> u64 {
        let base_delay = if value >= self.config.high_value_threshold {
            self.config.high_value_delay_seconds
        } else {
            self.config.default_delay_seconds
        };

        // Adjust for transaction type
        let type_multiplier = match transaction_type {
            TransactionType::Transfer => 1.0,
            TransactionType::CrossChainBridge => 
                self.config.cross_chain_delay_seconds as f64 / base_delay as f64,
            TransactionType::ContractExecution => 1.5,
            TransactionType::AccountCreation => 0.5,
            TransactionType::TokenOperation => 1.2,
            TransactionType::EmergencyOperation => 0.1,
            TransactionType::SystemAdmin => 2.0,
        };

        // Adjust for priority
        let priority_multiplier = match priority {
            TransactionPriority::Low => 2.0,
            TransactionPriority::Standard => 1.0,
            TransactionPriority::High => 0.5,
            TransactionPriority::Critical => 0.25,
            TransactionPriority::Emergency => 0.0, // Immediate execution (with proper auth)
        };

        let final_delay = (base_delay as f64 * type_multiplier * priority_multiplier) as u64;
        std::cmp::max(final_delay, 0) // Ensure non-negative
    }

    async fn generate_security_flags(
        &self,
        value: u64,
        transaction_type: &TransactionType,
        priority: &TransactionPriority,
        submitter: &TransactionSubmitter,
    ) -> Vec<SecurityFlag> {
        let mut flags = Vec::new();

        // High value flag
        if value >= self.config.high_value_threshold {
            flags.push(SecurityFlag {
                flag_type: SecurityFlagType::HighValue,
                severity: if value >= self.config.high_value_threshold * 10 {
                    SecuritySeverity::Critical
                } else {
                    SecuritySeverity::High
                },
                description: format!("High value transaction: {} lamports", value),
                detected_at: Utc::now(),
                source: "time_delay_manager".to_string(),
            });
        }

        // Emergency priority flag
        if *priority == TransactionPriority::Emergency {
            flags.push(SecurityFlag {
                flag_type: SecurityFlagType::KnownRisk,
                severity: SecuritySeverity::Critical,
                description: "Emergency priority transaction requires manual review".to_string(),
                detected_at: Utc::now(),
                source: "time_delay_manager".to_string(),
            });
        }

        // Cross-chain transaction flag
        if matches!(transaction_type, TransactionType::CrossChainBridge) {
            flags.push(SecurityFlag {
                flag_type: SecurityFlagType::KnownRisk,
                severity: SecuritySeverity::Medium,
                description: "Cross-chain transaction requires enhanced monitoring".to_string(),
                detected_at: Utc::now(),
                source: "time_delay_manager".to_string(),
            });
        }

        flags
    }

    async fn start_background_tasks(&self) {
        // Start transaction expiry monitor
        let delayed_transactions = Arc::clone(&self.delayed_transactions);
        let config = self.config.clone();
        tokio::spawn(async move {
            Self::transaction_expiry_monitor(delayed_transactions, config).await;
        });

        // Start security pattern monitor
        let security_monitor = Arc::clone(&self.security_monitor);
        let delayed_transactions = Arc::clone(&self.delayed_transactions);
        tokio::spawn(async move {
            Self::security_pattern_monitor(security_monitor, delayed_transactions).await;
        });
    }

    async fn transaction_expiry_monitor(
        delayed_transactions: Arc<RwLock<HashMap<String, DelayedTransaction>>>,
        config: DelayConfig,
    ) {
        let mut interval = tokio::time::interval(
            tokio::time::Duration::from_secs(60) // Check every minute
        );

        loop {
            interval.tick().await;

            let now = Utc::now();
            let mut to_expire = Vec::new();

            {
                let transactions = delayed_transactions.read().await;
                for (tx_id, transaction) in transactions.iter() {
                    // Auto-cancel after delay_time * auto_cancel_multiplier
                    let expiry_time = transaction.scheduled_execution + 
                        Duration::seconds(
                            (transaction.scheduled_execution - transaction.created_at).num_seconds() 
                            * config.auto_cancel_multiplier as i64
                        );

                    if now > expiry_time && 
                       matches!(transaction.status, DelayStatus::Pending | DelayStatus::Ready) {
                        to_expire.push(tx_id.clone());
                    }
                }
            }

            if !to_expire.is_empty() {
                let mut transactions = delayed_transactions.write().await;
                for tx_id in to_expire {
                    if let Some(transaction) = transactions.get_mut(&tx_id) {
                        transaction.status = DelayStatus::Expired;
                        warn!("Transaction expired: {}", tx_id);
                    }
                }
            }
        }
    }

    async fn security_pattern_monitor(
        _security_monitor: Arc<Mutex<SecurityMonitor>>,
        _delayed_transactions: Arc<RwLock<HashMap<String, DelayedTransaction>>>,
    ) {
        let mut interval = tokio::time::interval(
            tokio::time::Duration::from_secs(300) // Check every 5 minutes
        );

        loop {
            interval.tick().await;

            // Implement security pattern analysis
            // - Unusual transaction patterns
            // - Suspicious timing patterns
            // - Rate limiting violations
            // - Geographic anomalies

            // For now, just placeholder
        }
    }

    async fn update_stats_new_transaction(&self, delay_seconds: u64) {
        let mut stats = self.stats.lock().await;
        stats.total_transactions += 1;

        // Update average delay time
        let total = stats.total_transactions as f64;
        let current_avg = stats.average_delay_time;
        let new_delay = delay_seconds as f64;
        stats.average_delay_time = (current_avg * (total - 1.0) + new_delay) / total;
    }

    async fn update_stats_early_execution(&self) {
        let mut stats = self.stats.lock().await;
        stats.early_executions += 1;
    }

    async fn update_stats_cancellation(&self) {
        let mut stats = self.stats.lock().await;
        stats.cancelled_transactions += 1;
    }

    async fn update_stats_execution(&self) {
        let mut stats = self.stats.lock().await;
        stats.delayed_executions += 1;
    }

    async fn update_stats_emergency_pause(&self) {
        let mut stats = self.stats.lock().await;
        stats.emergency_pauses += 1;
    }

    async fn update_security_monitoring(&self, _transaction: &DelayedTransaction) {
        // Update security monitoring with new transaction patterns
        // This would analyze patterns, update rate limits, etc.
    }
}

impl Default for DelayConfig {
    fn default() -> Self {
        DelayConfig {
            default_delay_seconds: 300,      // 5 minutes
            high_value_threshold: 1_000_000_000, // 1 SOL
            high_value_delay_seconds: 86400, // 24 hours
            emergency_value_threshold: 100_000_000, // 0.1 SOL
            maximum_transaction_value: 10_000_000_000_000, // 10,000 SOL
            cross_chain_delay_seconds: 1800, // 30 minutes
            max_concurrent_delayed_transactions: 10000,
            early_execution_authorization: vec![
                AuthorizationLevel {
                    name: "admin".to_string(),
                    required_signatures: 2,
                    authorized_keys: vec![], // Would be populated with actual keys
                    max_authorized_value: 5_000_000_000, // 5 SOL
                    delay_reduction_percent: 50,
                },
                AuthorizationLevel {
                    name: "emergency".to_string(),
                    required_signatures: 3,
                    authorized_keys: vec![], // Would be populated with actual keys
                    max_authorized_value: u64::MAX, // No limit for emergency
                    delay_reduction_percent: 90,
                },
            ],
            emergency_pause_enabled: true,
            auto_cancel_multiplier: 2.0, // Cancel after 2x delay time
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[tokio::test]
    async fn test_time_delay_manager_creation() {
