    #[tokio::test]
    async fn test_high_value_transaction_delay() {
        let config = DelayConfig::default();
        let manager = TimeDelayManager::new(config.clone()).await.unwrap();

        let submitter = TransactionSubmitter {
            public_key: Pubkey::new_unique(),
            identifier: "test_user".to_string(),
            submitted_at: Utc::now(),
            client_info: None,
            ip_address: None,
        };

        // High value transaction
        let transaction = manager
            .submit_delayed_transaction(
                "high_value_tx".to_string(),
                b"high value transaction".to_vec(),
                2_000_000_000, // 2 SOL (above high_value_threshold)
                TransactionType::Transfer,
                TransactionPriority::Standard,
                submitter,
            )
            .await
            .unwrap();

        // Should have longer delay for high-value transaction
        let delay_duration = transaction.scheduled_execution - transaction.created_at;
        assert!(delay_duration.num_seconds() >= config.high_value_delay_seconds as i64);

        // Should have high value security flag
        assert!(transaction.security_flags.iter().any(|flag| 
            matches!(flag.flag_type, SecurityFlagType::HighValue)
        ));
    }

    #[tokio::test]
    async fn test_transaction_cancellation() {
        let config = DelayConfig::default();
        let manager = TimeDelayManager::new(config).await.unwrap();

        let submitter = TransactionSubmitter {
            public_key: Pubkey::new_unique(),
            identifier: "test_user".to_string(),
            submitted_at: Utc::now(),
            client_info: None,
            ip_address: None,
        };

        // Submit transaction
        let transaction = manager
            .submit_delayed_transaction(
                "cancel_test_tx".to_string(),
                b"test transaction".to_vec(),
                100_000_000,
                TransactionType::Transfer,
                TransactionPriority::Standard,
                submitter,
            )
            .await
            .unwrap();

        // Cancel transaction
        manager
            .cancel_transaction(
                &transaction.transaction_id,
                Pubkey::new_unique(),
                "Test cancellation".to_string(),
            )
            .await
            .unwrap();

        // Verify cancellation
        let status = manager
            .get_transaction_status(&transaction.transaction_id)
            .await
            .unwrap();
        assert_eq!(status.status, DelayStatus::Cancelled);
    }

    #[tokio::test]
    async fn test_emergency_pause() {
        let config = DelayConfig::default();
        let manager = TimeDelayManager::new(config).await.unwrap();

        let submitter = TransactionSubmitter {
            public_key: Pubkey::new_unique(),
            identifier: "test_user".to_string(),
            submitted_at: Utc::now(),
            client_info: None,
            ip_address: None,
        };

        // Submit transaction
        let _transaction = manager
            .submit_delayed_transaction(
                "emergency_test_tx".to_string(),
                b"test transaction".to_vec(),
                100_000_000,
                TransactionType::Transfer,
                TransactionPriority::Standard,
                submitter.clone(),
            )
            .await
            .unwrap();

        // Trigger emergency pause
        manager
            .emergency_pause("Test emergency")
            .await
            .unwrap();

        // Try to submit new transaction (should fail)
        let result = manager
            .submit_delayed_transaction(
                "should_fail_tx".to_string(),
                b"should fail".to_vec(),
                100_000_000,
                TransactionType::Transfer,
                TransactionPriority::Standard,
                submitter,
            )
            .await;

        assert!(matches!(result, Err(DelayError::EmergencyPauseActive)));
    }
}

