//! Transaction Monitoring and Anomaly Detection
//!
//! This module provides real-time transaction monitoring capabilities with advanced
//! anomaly detection algorithms to identify suspicious activities, potential attacks,
//! and unusual transaction patterns in the Solana L1 integration.

use std::collections::{HashMap, VecDeque};
use std::time::{Duration, SystemTime, UNIX_EPOCH};
use serde::{Deserialize, Serialize};
use thiserror::Error;
use tokio::sync::{mpsc, RwLock};
use std::sync::Arc;

/// Errors that can occur during transaction monitoring
#[derive(Error, Debug)]
pub enum MonitoringError {
    #[error("Configuration error: {0}")]
    Configuration(String),
    #[error("Analytics engine error: {0}")]
    Analytics(String),
    #[error("Alert system error: {0}")]
    AlertSystem(String),
    #[error("Data persistence error: {0}")]
    Persistence(String),
    #[error("System resource error: {0}")]
    SystemResource(String),
}

/// Transaction monitoring configuration
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct MonitoringConfig {
    /// Maximum transactions per second before triggering rate limit alerts
    pub max_tps_threshold: f64,
    /// Maximum transaction value before triggering high-value alerts (in lamports)
    pub max_value_threshold: u64,
    /// Time window for calculating transaction rates (seconds)
    pub rate_window_seconds: u64,
    /// Number of failed transactions before triggering failure alerts
    pub max_failure_threshold: u32,
    /// Minimum time between similar alerts (seconds)
    pub alert_cooldown_seconds: u64,
    /// Maximum number of transactions to keep in memory for analysis
    pub max_transaction_history: usize,
    /// Anomaly detection sensitivity (0.0 to 1.0, higher = more sensitive)
    pub anomaly_sensitivity: f64,
    /// Geographic location validation enabled
    pub geo_validation_enabled: bool,
    /// Advanced pattern detection enabled
    pub pattern_detection_enabled: bool,
}

impl Default for MonitoringConfig {
    fn default() -> Self {
        Self {
            max_tps_threshold: 100.0,
            max_value_threshold: 10_000_000_000, // 10 SOL in lamports
            rate_window_seconds: 300, // 5 minutes
            max_failure_threshold: 10,
            alert_cooldown_seconds: 60,
            max_transaction_history: 10_000,
            anomaly_sensitivity: 0.7,
            geo_validation_enabled: true,
            pattern_detection_enabled: true,
        }
    }
}

/// Transaction data for monitoring analysis
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct TransactionData {
    pub transaction_id: String,
    pub from_address: String,
    pub to_address: String,
    pub value: u64,
    pub timestamp: u64,
    pub success: bool,
    pub gas_used: u64,
    pub transaction_type: String,
    pub source_ip: Option<String>,
    pub user_agent: Option<String>,
    pub geographic_location: Option<String>,
    pub risk_score: f64,
}

/// Types of security alerts that can be generated
#[derive(Debug, Clone, Serialize, Deserialize)]
pub enum AlertSeverity {
    Low,
    Medium,
    High,
    Critical,
}

/// Security alert generated by the monitoring system
#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SecurityAlert {
    pub alert_id: String,
    pub severity: AlertSeverity,
    pub alert_type: String,
    pub description: String,
    pub transaction_ids: Vec<String>,
    pub timestamp: u64,
    pub metadata: HashMap<String, String>,
    pub recommended_actions: Vec<String>,
}

/// Advanced pattern detection for sophisticated attacks
#[derive(Debug)]
struct PatternDetector {
    /// Recent transaction patterns by address
    address_patterns: HashMap<String, VecDeque<TransactionData>>,
    /// Known attack signatures
    attack_signatures: Vec<AttackSignature>,
    /// Velocity tracking for addresses
    velocity_tracker: HashMap<String, VelocityStats>,
}

#[derive(Debug, Clone)]
struct AttackSignature {
    name: String,
    pattern_type: String,
    risk_indicators: Vec<String>,
    threshold_score: f64,
}

#[derive(Debug, Clone)]
struct VelocityStats {
    transaction_count: u32,
    total_value: u64,
    first_seen: u64,
    last_seen: u64,
    failure_rate: f64,
}

/// Real-time transaction monitoring and anomaly detection system
pub struct TransactionMonitor {
    config: MonitoringConfig,
    transaction_history: Arc<RwLock<VecDeque<TransactionData>>>,
    pattern_detector: Arc<RwLock<PatternDetector>>,
    alert_sender: mpsc::UnboundedSender<SecurityAlert>,
    alert_receiver: Arc<RwLock<Option<mpsc::UnboundedReceiver<SecurityAlert>>>>,
    statistics: Arc<RwLock<MonitoringStats>>,
    last_alert_times: Arc<RwLock<HashMap<String, u64>>>,
}

#[derive(Debug, Default)]
struct MonitoringStats {
    total_transactions_processed: u64,
    total_alerts_generated: u64,
    high_risk_transactions: u64,
    blocked_transactions: u64,
    false_positive_rate: f64,
    system_uptime_seconds: u64,
    average_processing_time_ms: f64,
}

impl TransactionMonitor {
    /// Create a new transaction monitoring system
    pub fn new(config: MonitoringConfig) -> Result<Self, MonitoringError> {
        let (alert_sender, alert_receiver) = mpsc::unbounded_channel();

        let pattern_detector = PatternDetector {
            address_patterns: HashMap::new(),
            attack_signatures: Self::load_attack_signatures(),
            velocity_tracker: HashMap::new(),
        };

        Ok(Self {
            config,
            transaction_history: Arc::new(RwLock::new(VecDeque::new())),
            pattern_detector: Arc::new(RwLock::new(pattern_detector)),
            alert_sender,
            alert_receiver: Arc::new(RwLock::new(Some(alert_receiver))),
            statistics: Arc::new(RwLock::new(MonitoringStats::default())),
            last_alert_times: Arc::new(RwLock::new(HashMap::new())),
        })
    }

    /// Process a new transaction through the monitoring system
    pub async fn process_transaction(&self, transaction: TransactionData) -> Result<bool, MonitoringError> {
        let start_time = std::time::Instant::now();

        // Calculate initial risk score
        let mut enhanced_transaction = transaction.clone();
        enhanced_transaction.risk_score = self.calculate_risk_score(&transaction).await?;

        // Store transaction in history
        {
            let mut history = self.transaction_history.write().await;
            history.push_back(enhanced_transaction.clone());

            // Maintain history size limit
            if history.len() > self.config.max_transaction_history {
                history.pop_front();
            }
        }

        // Update pattern detector with new transaction
        let should_block = self.analyze_transaction_patterns(&enhanced_transaction).await?;

        // Perform anomaly detection
        let anomaly_detected = self.detect_anomalies(&enhanced_transaction).await?;

        // Generate alerts if necessary
        if should_block || anomaly_detected {
            self.generate_alerts(&enhanced_transaction, should_block, anomaly_detected).await?;
        }

        // Update statistics
        {
            let mut stats = self.statistics.write().await;
            stats.total_transactions_processed += 1;
            if enhanced_transaction.risk_score > 0.7 {
                stats.high_risk_transactions += 1;
            }
            if should_block {
                stats.blocked_transactions += 1;
            }

            let processing_time = start_time.elapsed().as_millis() as f64;
            stats.average_processing_time_ms = 
                (stats.average_processing_time_ms * (stats.total_transactions_processed - 1) as f64 + processing_time) 
                / stats.total_transactions_processed as f64;
        }

        Ok(!should_block)
    }

    /// Calculate risk score for a transaction based on multiple factors
    async fn calculate_risk_score(&self, transaction: &TransactionData) -> Result<f64, MonitoringError> {
        let mut risk_score = 0.0;

        // High value transaction risk
        if transaction.value > self.config.max_value_threshold {
            risk_score += 0.3;
        }

        // Failed transaction risk
        if !transaction.success {
            risk_score += 0.2;
        }

        // Unusual gas usage patterns
        if transaction.gas_used > 1_000_000 {
            risk_score += 0.2;
        }

        // Geographic anomaly detection
        if self.config.geo_validation_enabled {
            if let Some(location) = &transaction.geographic_location {
                if self.is_high_risk_location(location) {
                    risk_score += 0.3;
                }
            }
        }

        // Velocity-based risk assessment
        let velocity_risk = self.calculate_velocity_risk(&transaction.from_address).await?;
        risk_score += velocity_risk;

        // Cap the risk score at 1.0
        Ok(risk_score.min(1.0))
    }

    /// Analyze transaction patterns for sophisticated attack detection
    async fn analyze_transaction_patterns(&self, transaction: &TransactionData) -> Result<bool, MonitoringError> {
        if !self.config.pattern_detection_enabled {
            return Ok(false);
        }

        let mut detector = self.pattern_detector.write().await;

        // Update address patterns
        let pattern_history = detector.address_patterns
            .entry(transaction.from_address.clone())
            .or_insert_with(VecDeque::new);

        pattern_history.push_back(transaction.clone());

        // Maintain pattern history size
        if pattern_history.len() > 100 {
            pattern_history.pop_front();
        }

        // Update velocity tracking
        let velocity = detector.velocity_tracker
            .entry(transaction.from_address.clone())
            .or_insert(VelocityStats {
                transaction_count: 0,
                total_value: 0,
                first_seen: transaction.timestamp,
                last_seen: transaction.timestamp,
                failure_rate: 0.0,
            });

        velocity.transaction_count += 1;
        velocity.total_value += transaction.value;
        velocity.last_seen = transaction.timestamp;

        // Calculate failure rate
        let failed_count = pattern_history.iter().filter(|t| !t.success).count();
        velocity.failure_rate = failed_count as f64 / pattern_history.len() as f64;

        // Check for attack patterns
        for signature in &detector.attack_signatures {
            if self.matches_attack_signature(transaction, signature, pattern_history) {
                return Ok(true);
            }
        }

        // Rate limiting check
        let time_window_start = transaction.timestamp.saturating_sub(self.config.rate_window_seconds);
        let recent_transactions = pattern_history.iter()
            .filter(|t| t.timestamp >= time_window_start)
            .count();

        let rate = recent_transactions as f64 / self.config.rate_window_seconds as f64;
        if rate > self.config.max_tps_threshold {
            return Ok(true);
        }

        Ok(false)
    }

    /// Detect statistical anomalies in transaction data
    async fn detect_anomalies(&self, transaction: &TransactionData) -> Result<bool, MonitoringError> {
        let history = self.transaction_history.read().await;

        if history.len() < 100 {
            return Ok(false); // Need sufficient data for anomaly detection
        }

        // Calculate statistical baselines
        let values: Vec<u64> = history.iter().map(|t| t.value).collect();
        let gas_usage: Vec<u64> = history.iter().map(|t| t.gas_used).collect();

        let value_mean = values.iter().sum::<u64>() as f64 / values.len() as f64;
        let gas_mean = gas_usage.iter().sum::<u64>() as f64 / gas_usage.len() as f64;

        // Simple standard deviation calculation
        let value_variance = values.iter()
            .map(|v| (*v as f64 - value_mean).powi(2))
            .sum::<f64>() / values.len() as f64;
        let value_stddev = value_variance.sqrt();

        let gas_variance = gas_usage.iter()
            .map(|g| (*g as f64 - gas_mean).powi(2))
            .sum::<f64>() / gas_usage.len() as f64;
        let gas_stddev = gas_variance.sqrt();

        // Check for value anomalies (3-sigma rule adjusted by sensitivity)
        let value_z_score = (transaction.value as f64 - value_mean).abs() / value_stddev;
        let gas_z_score = (transaction.gas_used as f64 - gas_mean).abs() / gas_stddev;

        let threshold = 3.0 - (self.config.anomaly_sensitivity * 2.0); // Range: 1.0 to 3.0

        Ok(value_z_score > threshold || gas_z_score > threshold)
    }

    /// Generate security alerts based on analysis results
    async fn generate_alerts(
        &self, 
        transaction: &TransactionData, 
        should_block: bool, 
        anomaly_detected: bool
    ) -> Result<(), MonitoringError> {
        let current_time = SystemTime::now()
            .duration_since(UNIX_EPOCH)
            .map_err(|_| MonitoringError::SystemResource("Time calculation failed".to_string()))?
            .as_secs();

        // Check alert cooldown
        let alert_type = if should_block { "BLOCKED_TRANSACTION" } else { "ANOMALY_DETECTED" };

        {
            let last_alerts = self.last_alert_times.read().await;
            if let Some(&last_time) = last_alerts.get(alert_type) {
                if current_time - last_time < self.config.alert_cooldown_seconds {
                    return Ok(());
                }
            }
        }

        let severity = if should_block { 
            AlertSeverity::Critical 
        } else if transaction.risk_score > 0.8 {
            AlertSeverity::High
        } else if transaction.risk_score > 0.6 {
            AlertSeverity::Medium
        } else {
            AlertSeverity::Low
        };

        let mut description = format!(
            "Transaction monitoring alert for transaction {}. Risk score: {:.2}",
            transaction.transaction_id, transaction.risk_score
        );

        if should_block {
            description.push_str(". Transaction blocked due to pattern analysis.");
        }
        if anomaly_detected {
            description.push_str(". Statistical anomaly detected.");
        }

        let alert = SecurityAlert {
            alert_id: format!("MON-{}-{}", current_time, transaction.transaction_id),
            severity,
            alert_type: alert_type.to_string(),
            description,
            transaction_ids: vec![transaction.transaction_id.clone()],
            timestamp: current_time,
            metadata: self.build_alert_metadata(transaction),
            recommended_actions: self.get_recommended_actions(should_block, transaction.risk_score),
        };

        // Send alert
        self.alert_sender.send(alert)
            .map_err(|_| MonitoringError::AlertSystem("Failed to send alert".to_string()))?;

        // Update alert timing
        {
            let mut last_alerts = self.last_alert_times.write().await;
            last_alerts.insert(alert_type.to_string(), current_time);
        }

        // Update statistics
        {
            let mut stats = self.statistics.write().await;
            stats.total_alerts_generated += 1;
        }

        Ok(())
    }

    /// Build metadata for alerts
    fn build_alert_metadata(&self, transaction: &TransactionData) -> HashMap<String, String> {
        let mut metadata = HashMap::new();
        metadata.insert("from_address".to_string(), transaction.from_address.clone());
        metadata.insert("to_address".to_string(), transaction.to_address.clone());
        metadata.insert("value".to_string(), transaction.value.to_string());
        metadata.insert("gas_used".to_string(), transaction.gas_used.to_string());
        metadata.insert("transaction_type".to_string(), transaction.transaction_type.clone());

        if let Some(ip) = &transaction.source_ip {
            metadata.insert("source_ip".to_string(), ip.clone());
        }
        if let Some(location) = &transaction.geographic_location {
            metadata.insert("geographic_location".to_string(), location.clone());
        }

        metadata
    }

    /// Get recommended actions based on alert type and risk score
    fn get_recommended_actions(&self, blocked: bool, risk_score: f64) -> Vec<String> {
        let mut actions = Vec::new();

        if blocked {
            actions.push("Transaction has been automatically blocked".to_string());
            actions.push("Review transaction patterns for the involved addresses".to_string());
            actions.push("Consider temporarily restricting address if pattern continues".to_string());
        }

        if risk_score > 0.8 {
            actions.push("Investigate transaction source and destination addresses".to_string());
            actions.push("Verify transaction legitimacy with user if possible".to_string());
        }

        if risk_score > 0.6 {
            actions.push("Monitor address for continued suspicious activity".to_string());
            actions.push("Consider enhanced verification for future transactions".to_string());
        }

        actions.push("Update threat intelligence with new patterns if confirmed malicious".to_string());

        actions
    }

    /// Calculate velocity-based risk for an address
    async fn calculate_velocity_risk(&self, address: &str) -> Result<f64, MonitoringError> {
        let detector = self.pattern_detector.read().await;

        if let Some(velocity) = detector.velocity_tracker.get(address) {
            let time_span = velocity.last_seen - velocity.first_seen;
            if time_span == 0 {
                return Ok(0.0);
            }

            let tx_rate = velocity.transaction_count as f64 / time_span as f64;
            let value_rate = velocity.total_value as f64 / time_span as f64;

            // Higher rates indicate higher risk
            let rate_risk = (tx_rate / 10.0).min(0.3); // Max 0.3 risk from rate
            let value_risk = (value_rate / 1_000_000_000.0).min(0.2); // Max 0.2 risk from value
            let failure_risk = velocity.failure_rate * 0.3; // Max 0.3 risk from failures

            Ok(rate_risk + value_risk + failure_risk)
        } else {
            Ok(0.0)
        }
    }

    /// Check if a location is considered high-risk
    fn is_high_risk_location(&self, location: &str) -> bool {
        // Simple implementation - in practice, this would check against threat intelligence
        let high_risk_locations = ["Unknown", "TOR", "VPN", "Sanctioned"];
        high_risk_locations.iter().any(|&risk_loc| location.contains(risk_loc))
    }

    /// Check if transaction matches known attack signature
    fn matches_attack_signature(
        &self, 
        transaction: &TransactionData, 
        signature: &AttackSignature, 
        history: &VecDeque<TransactionData>
    ) -> bool {
        match signature.pattern_type.as_str() {
            "rapid_small_transactions" => {
                let recent_small = history.iter()
                    .rev()
                    .take(10)
                    .filter(|t| t.value < 1_000_000) // Less than 0.001 SOL
                    .count();
                recent_small >= 8
            },
            "duplicate_amounts" => {
                let duplicate_count = history.iter()
                    .filter(|t| t.value == transaction.value)
                    .count();
                duplicate_count >= 5
            },
            "round_robin" => {
                // Check for transactions cycling between same addresses
                let unique_addresses: std::collections::HashSet<_> = history.iter()
                    .map(|t| (&t.from_address, &t.to_address))
                    .collect();
                unique_addresses.len() < history.len() / 3
            },
            _ => false,
        }
    }

    /// Load known attack signatures
    fn load_attack_signatures() -> Vec<AttackSignature> {
        vec![
            AttackSignature {
                name: "Rapid Micro Transactions".to_string(),
                pattern_type: "rapid_small_transactions".to_string(),
                risk_indicators: vec!["high_frequency".to_string(), "small_amounts".to_string()],
                threshold_score: 0.8,
            },
            AttackSignature {
                name: "Duplicate Amount Attack".to_string(),
                pattern_type: "duplicate_amounts".to_string(),
                risk_indicators: vec!["repeated_amounts".to_string(), "automation_likely".to_string()],
                threshold_score: 0.7,
            },
            AttackSignature {
                name: "Round Robin Mixing".to_string(),
                pattern_type: "round_robin".to_string(),
                risk_indicators: vec!["address_cycling".to_string(), "obfuscation_attempt".to_string()],
                threshold_score: 0.9,
            },
        ]
    }

    /// Get the alert receiver for external consumption
    pub async fn take_alert_receiver(&self) -> Option<mpsc::UnboundedReceiver<SecurityAlert>> {
        self.alert_receiver.write().await.take()
    }

    /// Get current monitoring statistics
    pub async fn get_statistics(&self) -> MonitoringStats {
        self.statistics.read().await.clone()
    }

    /// Perform emergency shutdown of monitoring system
    pub async fn emergency_shutdown(&self) -> Result<(), MonitoringError> {
        println!("üö® EMERGENCY SHUTDOWN: Transaction monitoring system halted");

        // Clear sensitive data
        self.transaction_history.write().await.clear();
        self.pattern_detector.write().await.address_patterns.clear();

        // Generate emergency alert
        let emergency_alert = SecurityAlert {
            alert_id: format!("EMERGENCY-{}", 
                SystemTime::now()
                    .duration_since(UNIX_EPOCH)
                    .unwrap_or_default()
                    .as_secs()
            ),
            severity: AlertSeverity::Critical,
            alert_type: "SYSTEM_EMERGENCY_SHUTDOWN".to_string(),
            description: "Transaction monitoring system has executed emergency shutdown".to_string(),
            transaction_ids: vec![],
            timestamp: SystemTime::now()
                .duration_since(UNIX_EPOCH)
                .unwrap_or_default()
                .as_secs(),
            metadata: HashMap::new(),
            recommended_actions: vec![
                "Investigate cause of emergency shutdown".to_string(),
                "Verify system integrity before restart".to_string(),
                "Review recent alerts and transaction patterns".to_string(),
            ],
        };

        let _ = self.alert_sender.send(emergency_alert);

        Ok(())
    }

    /// Check system health and performance
    pub async fn health_check(&self) -> Result<HashMap<String, String>, MonitoringError> {
        let mut health = HashMap::new();
        let stats = self.statistics.read().await;

        health.insert("status".to_string(), "operational".to_string());
        health.insert("transactions_processed".to_string(), stats.total_transactions_processed.to_string());
        health.insert("alerts_generated".to_string(), stats.total_alerts_generated.to_string());
        health.insert("avg_processing_time_ms".to_string(), format!("{:.2}", stats.average_processing_time_ms));
        health.insert("high_risk_transactions".to_string(), stats.high_risk_transactions.to_string());
        health.insert("blocked_transactions".to_string(), stats.blocked_transactions.to_string());

        let history_size = self.transaction_history.read().await.len();
        health.insert("transaction_history_size".to_string(), history_size.to_string());

        let pattern_addresses = self.pattern_detector.read().await.address_patterns.len();
        health.insert("monitored_addresses".to_string(), pattern_addresses.to_string());

        Ok(health)
    }
}

impl Clone for MonitoringStats {
    fn clone(&self) -> Self {
        Self {
            total_transactions_processed: self.total_transactions_processed,
            total_alerts_generated: self.total_alerts_generated,
            high_risk_transactions: self.high_risk_transactions,
            blocked_transactions: self.blocked_transactions,
            false_positive_rate: self.false_positive_rate,
            system_uptime_seconds: self.system_uptime_seconds,
            average_processing_time_ms: self.average_processing_time_ms,
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    #[tokio::test]
    async fn test_transaction_monitoring_creation() {
        let config = MonitoringConfig::default();
        let monitor = TransactionMonitor::new(config).unwrap();

        let health = monitor.health_check().await.unwrap();
        assert_eq!(health.get("status").unwrap(), "operational");
    }

    #[tokio::test]
    async fn test_risk_score_calculation() {
        let config = MonitoringConfig::default();
        let monitor = TransactionMonitor::new(config).unwrap();

        let high_value_tx = TransactionData {
            transaction_id: "test-high-value".to_string(),
            from_address: "addr1".to_string(),
            to_address: "addr2".to_string(),
            value: 50_000_000_000, // 50 SOL
            timestamp: 1000000000,
            success: true,
            gas_used: 5000,
            transaction_type: "transfer".to_string(),
            source_ip: None,
            user_agent: None,
            geographic_location: None,
            risk_score: 0.0,
        };

        let risk_score = monitor.calculate_risk_score(&high_value_tx).await.unwrap();
        assert!(risk_score > 0.0, "High value transaction should have risk score > 0");
    }

    #[tokio::test]
    async fn test_pattern_detection() {
        let config = MonitoringConfig::default();
        let monitor = TransactionMonitor::new(config).unwrap();

        // Simulate rapid small transactions
        for i in 0..10 {
            let tx = TransactionData {
                transaction_id: format!("rapid-tx-{}", i),
                from_address: "rapid-sender".to_string(),
                to_address: format!("addr-{}", i),
                value: 100_000, // 0.0001 SOL
                timestamp: 1000000000 + i,
                success: true,
                gas_used: 5000,
                transaction_type: "transfer".to_string(),
                source_ip: None,
                user_agent: None,
                geographic_location: None,
                risk_score: 0.0,
            };

            let result = monitor.process_transaction(tx).await.unwrap();
            if i >= 7 {
                // Should start blocking after pattern is detected
                assert!(!result, "Should block transaction after pattern detection");
                break;
            }
        }
    }
}

