//! This module provides comprehensive security infrastructure for the APEchain
//! Solana L1 integration, including HSM integration, multi-signature validation,
//! transaction monitoring, and emergency controls.

pub mod hsm_integration;
pub mod multisig_validation;
pub mod time_delay_security;
pub mod transaction_monitoring;
pub mod circuit_breaker;
pub mod audit_logger;

// Re-export main types for convenience
pub use hsm_integration::{
    HSMManager, HSMConfig, KeyMetadata, KeyPurpose, KeyStatus,
    HSMSignature, SignatureAlgorithm, HSMError as HSMError,
    HSMHealthStatus,
};

pub use multisig_validation::{
    MultiSigValidator, MultiSigConfig, ValidatorInfo, SignatureValidation,
