async fn start_security_validation_pipeline(
    &self,
    request: SecureTransactionRequest,
) -> Result<(), SolanaL1Error> {
    let request_id = request.request_id.clone();

    // Clone necessary components for the async task
    let hsm_manager = Arc::clone(&self.hsm_manager);
    let multisig_validator = Arc::clone(&self.multisig_validator);
    let time_delay_manager = Arc::clone(&self.time_delay_manager);
    let svm_runtime = Arc::clone(&self.svm_runtime);
    let pending_transactions = Arc::clone(&self.pending_transactions);
    let completed_transactions = Arc::clone(&self.completed_transactions);
    let stats = Arc::clone(&self.stats);

    // Start validation pipeline in background
    tokio::spawn(async move {
        let result = Self::execute_security_validation_pipeline(
            request,
            hsm_manager,
            multisig_validator,
            time_delay_manager,
            svm_runtime,
            pending_transactions.clone(),
            stats,
        ).await;

        // Store result
        let mut completed = completed_transactions.write().await;
        completed.insert(request_id.clone(), result);

        // Remove from pending
        let mut pending = pending_transactions.write().await;
        pending.remove(&request_id);
    });

    Ok(())
}

