    async fn security_alert_monitoring_task(
        alert_receiver: Arc<RwLock<Option<tokio::sync::mpsc::UnboundedReceiver<SecurityAlert>>>>,
        emergency_shutdown: Arc<RwLock<bool>>,
    ) {
        // Extract the receiver from the Arc<RwLock<Option<_>>>
        let mut receiver_opt = alert_receiver.write().await;
        if let Some(mut receiver) = receiver_opt.take() {
            drop(receiver_opt); // Release the lock

            info!("Starting security alert monitoring task");

            while let Some(alert) = receiver.recv().await {
                warn!("üö® Security Alert Received: {}", alert.alert_type);
                info!("Alert ID: {}, Severity: {:?}", alert.alert_id, alert.severity);
                info!("Description: {}", alert.description);

                // Handle critical alerts that may require emergency shutdown
                match alert.severity {
                    AlertSeverity::Critical => {
                        error!("üî¥ CRITICAL SECURITY ALERT: {}", alert.description);

                        // Check if this alert type should trigger emergency shutdown
                        if alert.alert_type.contains("BLOCKED_TRANSACTION") 
                            || alert.alert_type.contains("SYSTEM_EMERGENCY") {
                            warn!("Initiating emergency shutdown due to critical security alert");
                            *emergency_shutdown.write().await = true;
                        }

                        // Log to audit trail
                        Self::log_security_event(&alert).await;

                        // Send notifications (webhook, email, etc.)
                        Self::send_alert_notifications(&alert).await;
                    },
                    AlertSeverity::High => {
                        warn!("üü† HIGH SECURITY ALERT: {}", alert.description);
                        Self::log_security_event(&alert).await;
                        Self::send_alert_notifications(&alert).await;
                    },
                    AlertSeverity::Medium => {
                        info!("üü° MEDIUM SECURITY ALERT: {}", alert.description);
                        Self::log_security_event(&alert).await;
                    },
                    AlertSeverity::Low => {
                        info!("üü¢ LOW SECURITY ALERT: {}", alert.description);
                        // Just log low-severity alerts
                    }
                }

                // Execute recommended actions for critical alerts
                if matches!(alert.severity, AlertSeverity::Critical | AlertSeverity::High) {
                    for action in &alert.recommended_actions {
                        info!("Recommended Action: {}", action);
                        // Could implement automated responses here
                    }
                }
            }

            warn!("Security alert monitoring task ended - receiver closed");
        } else {
            error!("Failed to initialize security alert monitoring - no receiver available");
        }
    }

    async fn log_security_event(alert: &SecurityAlert) {
        // In a production environment, this would write to a secure audit log
        // integrated with SIEM systems and compliance frameworks
        info!("AUDIT: Security event logged - Alert ID: {}, Type: {}, Severity: {:?}", 
            alert.alert_id, alert.alert_type, alert.severity);
    }

    async fn send_alert_notifications(alert: &SecurityAlert) {
        // In a production environment, this would send notifications via:
        // - Webhook endpoints
        // - Email alerts
        // - SMS for critical alerts
        // - Integration with incident response systems
        info!("NOTIFICATION: Alert {} notification sent to configured endpoints", alert.alert_id);
    }
}

