use anyhow::{anyhow, Result};
use std::path::PathBuf;
use std::process::Command;
use std::sync::Arc;
use tokio::sync::RwLock;
use uuid::Uuid;

use cryptobox_nif::sandbox::exec_in_sandbox_secure;

use crate::compute_backend::{ComputeBackend, ComputeMode};
use crate::sandbox_manager::SandboxManager;

pub struct HvmExecutor;

impl HvmExecutor {
    /// Execute HVM code in a sandbox with the specified compute mode
    pub async fn execute_in_sandbox(
        sandbox_id: Uuid,
        hvm_code: String,
        mode: ComputeMode,
    ) -> Result<String> {
        // Verify sandbox is ready
        if !SandboxManager::is_sandbox_ready(sandbox_id).await? {
            return Err(anyhow!("Sandbox is not in running state"));
        }

        // Determine the actual compute mode to use
        let actual_mode = if mode == ComputeMode::Auto {
            Self::detect_optimal_mode(&hvm_code).await?
        } else {
            mode
        };

        // Set the compute mode for the sandbox
        SandboxManager::set_compute_mode(sandbox_id, actual_mode).await?;

        // Prepare HVM execution based on compute mode
        let execution_result = match actual_mode {
            ComputeMode::Cuda => Self::execute_cuda(sandbox_id, hvm_code).await,
            ComputeMode::Wasm => Self::execute_wasm(sandbox_id, hvm_code).await,
            ComputeMode::Cpu => Self::execute_cpu(sandbox_id, hvm_code).await,
            ComputeMode::Auto => unreachable!("Auto mode should be resolved by now"),
        };

        execution_result
    }

    /// Detect the optimal compute mode based on the HVM code characteristics
    async fn detect_optimal_mode(hvm_code: &str) -> Result<ComputeMode> {
        // Analyze the HVM code to determine the best execution mode
        let lines = hvm_code.lines().count();
        let has_parallel_patterns = hvm_code.contains("dup") || hvm_code.contains("par");
        let has_large_data = lines > 1000;

        // Check hardware availability
        let cuda_available = Self::is_cuda_available();
        let wasm_preferred = !has_large_data && lines < 100;

        if cuda_available && (has_parallel_patterns || has_large_data) {
            Ok(ComputeMode::Cuda)
        } else if wasm_preferred {
            Ok(ComputeMode::Wasm)
        } else {
            Ok(ComputeMode::Cpu)
        }
    }

    /// Execute HVM code using CUDA backend
    async fn execute_cuda(sandbox_id: Uuid, hvm_code: String) -> Result<String> {
        #[cfg(feature = "cuda")]
        {
            // Write HVM code to temporary file in sandbox
            let hvm_file = format!("/tmp/hvm_{}.hvm", sandbox_id);
            let write_cmd = format!("echo '{}' > {}", hvm_code, hvm_file);
            exec_in_sandbox_secure(sandbox_id, "sh".to_string(), vec!["-c".to_string(), write_cmd]).await?;

            // Execute with CUDA backend
            let result = exec_in_sandbox_secure(
                sandbox_id,
                "hvm".to_string(),
                vec!["run-cu".to_string(), hvm_file],
            ).await?;

            // Clean up
            exec_in_sandbox_secure(
                sandbox_id,
                "rm".to_string(),
                vec![hvm_file],
            ).await?;

            Ok(result)
        }

        #[cfg(not(feature = "cuda"))]
        {
            // Fallback to CPU if CUDA not available
            Self::execute_cpu(sandbox_id, hvm_code).await
        }
    }

    /// Execute HVM code using WASM backend
    async fn execute_wasm(sandbox_id: Uuid, hvm_code: String) -> Result<String> {
        use wasmtime::*;
        use wasmtime_wasi::WasiCtxBuilder;

        // First, compile HVM to WASM (this would need HVM's WASM target)
        let wasm_module = Self::compile_hvm_to_wasm(&hvm_code).await?;

        // Create WASM runtime
        let engine = Engine::default();
        let mut linker = Linker::new(&engine);
        wasmtime_wasi::add_to_linker(&mut linker, |s| s)?;

        let wasi = WasiCtxBuilder::new()
            .inherit_stdio()
            .inherit_args()?
            .build();

        let mut store = Store::new(&engine, wasi);

        // Load and instantiate the WASM module
        let module = Module::from_binary(&engine, &wasm_module)?;
        let instance = linker.instantiate(&mut store, &module)?;

        // Execute the main function
        let main = instance.get_typed_func::<(), ()>(&mut store, "_start")?;
        main.call(&mut store, ())?;

        // Capture output (this would need proper output handling)
        Ok("WASM execution completed".to_string())
    }

    /// Execute HVM code using CPU backend
    async fn execute_cpu(sandbox_id: Uuid, hvm_code: String) -> Result<String> {
        // Write HVM code to temporary file in sandbox
        let hvm_file = format!("/tmp/hvm_{}.hvm", sandbox_id);
        let write_cmd = format!("echo '{}' > {}", hvm_code, hvm_file);
        exec_in_sandbox_secure(sandbox_id, "sh".to_string(), vec!["-c".to_string(), write_cmd]).await?;

        // Execute with C backend (fastest CPU option)
        let result = exec_in_sandbox_secure(
            sandbox_id,
            "hvm".to_string(),
            vec!["run-c".to_string(), hvm_file],
        ).await?;

        // Clean up
        exec_in_sandbox_secure(
            sandbox_id,
            "rm".to_string(),
            vec![hvm_file],
        ).await?;

        Ok(result)
    }

    /// Compile HVM code to WASM
    async fn compile_hvm_to_wasm(hvm_code: &str) -> Result<Vec<u8>> {
        // This would integrate with HVM's compilation pipeline
        // For now, return a placeholder
        // In production, this would:
        // 1. Parse HVM code
        // 2. Generate WASM bytecode
        // 3. Optimize for WASM execution
        Ok(vec![])
    }

    /// Check if CUDA is available on the system
    fn is_cuda_available() -> bool {
        #[cfg(feature = "cuda")]
        {
            // Check for CUDA runtime
            Command::new("nvidia-smi")
                .output()
                .map(|output| output.status.success())
                .unwrap_or(false)
        }

        #[cfg(not(feature = "cuda"))]
        {
            false
        }
    }
}

