import { assertEquals, assertExists } from "https://deno.land/std@0.224.0/assert/mod.ts";
import {
  getGlobalConfig,
  getCurrentProjectConfig,
  DEFAULT_GLOBAL_CONFIG,
  DEFAULT_PROJECT_CONFIG,
} from "./config.ts";

Deno.test("getGlobalConfig returns default config when no config exists", () => {
  const config = getGlobalConfig();
  assertExists(config);
  assertEquals(config.theme, DEFAULT_GLOBAL_CONFIG.theme);
});

Deno.test("getCurrentProjectConfig returns default config when no config exists", () => {
  const config = getCurrentProjectConfig();
  assertExists(config);
  assertEquals(config.hasCompletedProjectOnboarding, DEFAULT_PROJECT_CONFIG.hasCompletedProjectOnboarding);
});

Deno.test("config should have required properties", () => {
  const globalConfig = getGlobalConfig();
  assertExists(globalConfig.theme);
  assertExists(globalConfig.providers);

  const projectConfig = getCurrentProjectConfig();
  assertExists(projectConfig.hasCompletedProjectOnboarding);
});

Deno.test({
  name: "config persistence test",
  permissions: { read: true, write: true },
  fn: () => {
    // This test would verify that config can be saved and loaded
    // In a real implementation, you'd mock the file system operations
    const config = getGlobalConfig();
    assertExists(config);
  },
});

