/**
 * Wasmer Runtime for WebAssembly
 * Provides high-performance WebAssembly execution with native compatibility
 */

import { instantiate } from "./wasmer_wasm.js"

export interface WasmerConfig {
  memory?: {
    initial?: number
    maximum?: number
    shared?: boolean
  }
  imports?: Record<string, any>
  env?: Record<string, string>
  preopens?: Record<string, string>
}

export interface WasmerInstance {
  exports: WebAssembly.Exports
  memory: WebAssembly.Memory
  table?: WebAssembly.Table
}

export class WasmerRuntime {
  private wasmModule: WebAssembly.Module | null = null
  private instance: WasmerInstance | null = null
  private config: WasmerConfig

  constructor(config: WasmerConfig = {}) {
    this.config = {
      memory: {
        initial: 256,
        maximum: 16384,
        shared: false,
        ...config.memory,
      },
      imports: config.imports || {},
      env: config.env || {},
      preopens: config.preopens || {},
    }
  }

  /**
   * Load and compile WebAssembly module
   */
  async loadModule(source: BufferSource | Response | string): Promise<void> {
    let wasmBytes: BufferSource

    if (typeof source === "string") {
      // Load from file path
      const response = await fetch(source)
      wasmBytes = await response.arrayBuffer()
    } else if (source instanceof Response) {
      wasmBytes = await source.arrayBuffer()
    } else {
      wasmBytes = source
    }

    // Compile the WebAssembly module
    this.wasmModule = await WebAssembly.compile(wasmBytes)
  }

  /**
   * Instantiate the WebAssembly module with imports
   */
  async instantiate(): Promise<WasmerInstance> {
    if (!this.wasmModule) {
      throw new Error("No WebAssembly module loaded. Call loadModule() first.")
    }

    // Create memory instance
    const memory = new WebAssembly.Memory({
      initial: this.config.memory!.initial,
      maximum: this.config.memory!.maximum,
      shared: this.config.memory!.shared,
    })

    // Prepare WASI imports
    const wasiImports = this.createWASIImports(memory)

    // Merge custom imports
    const imports = {
      wasi_snapshot_preview1: wasiImports,
      env: {
        memory,
        ...this.config.imports,
      },
    }

    // Instantiate the module
    const wasmInstance = await WebAssembly.instantiate(this.wasmModule, imports)

    this.instance = {
      exports: wasmInstance.exports,
      memory,
      table: wasmInstance.exports.table as WebAssembly.Table | undefined,
    }

    // Initialize WASI if _start is exported
    if (typeof this.instance.exports._start === "function") {
      ;(this.instance.exports._start as Function)()
    }

    return this.instance
  }

  /**
   * Create WASI imports for WebAssembly module
   */
  private createWASIImports(memory: WebAssembly.Memory): Record<string, Function> {
    const decoder = new TextDecoder()
    const encoder = new TextEncoder()

    return {
      fd_write: (fd: number, iovs: number, iovsLen: number, nwritten: number) => {
        const view = new DataView(memory.buffer)
        let written = 0

        for (let i = 0; i < iovsLen; i++) {
          const ptr = iovs + i * 8
          const buf = view.getUint32(ptr, true)
          const bufLen = view.getUint32(ptr + 4, true)

          const bytes = new Uint8Array(memory.buffer, buf, bufLen)
          const text = decoder.decode(bytes)

          if (fd === 1) {
            // stdout
            console.log(text)
          } else if (fd === 2) {
            // stderr
            console.error(text)
          }

          written += bufLen
        }

        view.setUint32(nwritten, written, true)
        return 0
      },

      fd_read: (fd: number, iovs: number, iovsLen: number, nread: number) => {
        // Simplified implementation for demonstration
        const view = new DataView(memory.buffer)
        view.setUint32(nread, 0, true)
        return 0
      },

      fd_close: (fd: number) => {
        return 0
      },

      fd_seek: (fd: number, offset: bigint, whence: number, newOffset: number) => {
        return 0
      },

      environ_sizes_get: (environCount: number, environBufSize: number) => {
        const view = new DataView(memory.buffer)
        const envVars = Object.entries(this.config.env!)

        view.setUint32(environCount, envVars.length, true)

        let totalSize = 0
        for (const [key, value] of envVars) {
          totalSize += encoder.encode(`${key}=${value}\0`).length
        }
        view.setUint32(environBufSize, totalSize, true)

        return 0
      },

      environ_get: (environ: number, environBuf: number) => {
        const view = new DataView(memory.buffer)
        const envVars = Object.entries(this.config.env!)

        let bufOffset = environBuf
        let ptrOffset = environ

        for (const [key, value] of envVars) {
          view.setUint32(ptrOffset, bufOffset, true)

          const envStr = `${key}=${value}\0`
          const bytes = encoder.encode(envStr)
          new Uint8Array(memory.buffer, bufOffset, bytes.length).set(bytes)

          bufOffset += bytes.length
          ptrOffset += 4
        }

        return 0
      },

      proc_exit: (code: number) => {
        throw new Error(`WASI process exited with code ${code}`)
      },

      clock_time_get: (id: number, precision: bigint, time: number) => {
        const view = new DataView(memory.buffer)
        const now = BigInt(Date.now()) * 1000000n // Convert to nanoseconds
        view.setBigUint64(time, now, true)
        return 0
      },

      random_get: (buf: number, bufLen: number) => {
        const bytes = new Uint8Array(memory.buffer, buf, bufLen)
        crypto.getRandomValues(bytes)
        return 0
      },
    }
  }

  /**
   * Call an exported function from the WebAssembly module
   */
  call(name: string, ...args: any[]): any {
    if (!this.instance) {
      throw new Error("WebAssembly module not instantiated. Call instantiate() first.")
    }

    const func = this.instance.exports[name]
    if (typeof func !== "function") {
      throw new Error(`Export '${name}' is not a function`)
    }

    return func(...args)
  }

  /**
   * Get memory view for direct memory access
   */
  getMemoryView(): DataView {
    if (!this.instance) {
      throw new Error("WebAssembly module not instantiated")
    }
    return new DataView(this.instance.memory.buffer)
  }

  /**
   * Read string from memory
   */
  readString(ptr: number, len: number): string {
    if (!this.instance) {
      throw new Error("WebAssembly module not instantiated")
    }
    const bytes = new Uint8Array(this.instance.memory.buffer, ptr, len)
    return new TextDecoder().decode(bytes)
  }

  /**
   * Write string to memory
   */
  writeString(ptr: number, str: string): number {
    if (!this.instance) {
      throw new Error("WebAssembly module not instantiated")
    }
    const bytes = new TextEncoder().encode(str)
    new Uint8Array(this.instance.memory.buffer, ptr, bytes.length).set(bytes)
    return bytes.length
  }

  /**
   * Allocate memory in the WebAssembly module
   */
  allocate(size: number): number {
    if (!this.instance) {
      throw new Error("WebAssembly module not instantiated")
    }

    // Call malloc if available
    const malloc = this.instance.exports.malloc
    if (typeof malloc === "function") {
      return malloc(size) as number
    }

    throw new Error("Memory allocation not supported (malloc not exported)")
  }

  /**
   * Free allocated memory
   */
  free(ptr: number): void {
    if (!this.instance) {
      throw new Error("WebAssembly module not instantiated")
    }

    const free = this.instance.exports.free
    if (typeof free === "function") {
      free(ptr)
    }
  }

  /**
   * Get all exported functions
   */
  getExports(): WebAssembly.Exports {
    if (!this.instance) {
      throw new Error("WebAssembly module not instantiated")
    }
    return this.instance.exports
  }

  /**
   * Reset the runtime instance
   */
  reset(): void {
    this.instance = null
    this.wasmModule = null
  }
}

// Export factory function for easy instantiation
export async function createWasmerRuntime(
  wasmSource: BufferSource | Response | string,
  config?: WasmerConfig,
): Promise<WasmerRuntime> {
  const runtime = new WasmerRuntime(config)
  await runtime.loadModule(wasmSource)
  await runtime.instantiate()
  return runtime
}

