use wasm_bindgen::prelude::*;
use serde::{Deserialize, Serialize};
use web_sys::console;
use std::sync::Arc;
use parking_lot::RwLock;
use dashmap::DashMap;
use uuid::Uuid;

#[cfg(feature = "wee_alloc")]
#[global_allocator]
static ALLOC: wee_alloc::WeeAlloc = wee_alloc::WeeAlloc::INIT;

#[wasm_bindgen]
extern "C" {
    #[wasm_bindgen(js_namespace = console)]
    fn log(s: &str);

    #[wasm_bindgen(js_namespace = Math)]
    fn random() -> f64;
}

macro_rules! console_log {
    ($($t:tt)*) => {
        log(&format_args!($($t)*).to_string())
    };
}

/// Elixir Process representation in WASM
#[wasm_bindgen]
#[derive(Clone)]
pub struct ElixirProcess {
    id: String,
    mailbox: Arc<RwLock<Vec<JsValue>>>,
    state: Arc<RwLock<ProcessState>>,
    linked_processes: Arc<DashMap<String, ElixirProcess>>,
}

#[derive(Clone, Debug, Serialize, Deserialize)]
struct ProcessState {
    status: ProcessStatus,
    priority: Priority,
    reductions: u64,
    memory: usize,
}

#[derive(Clone, Debug, Serialize, Deserialize)]
enum ProcessStatus {
    Running,
    Waiting,
    Suspended,
    Exiting,
}

#[derive(Clone, Debug, Serialize, Deserialize)]
enum Priority {
    Low,
    Normal,
    High,
    Max,
}

#[wasm_bindgen]
impl ElixirProcess {
    /// Create a new Elixir process
    #[wasm_bindgen(constructor)]
    pub fn new() -> Self {
        let id = Uuid::new_v4().to_string();
        console_log!("Creating new Elixir process: {}", id);

        Self {
            id: id.clone(),
            mailbox: Arc::new(RwLock::new(Vec::new())),
            state: Arc::new(RwLock::new(ProcessState {
                status: ProcessStatus::Running,
                priority: Priority::Normal,
                reductions: 0,
                memory: 0,
            })),
            linked_processes: Arc::new(DashMap::new()),
        }
    }

    /// Send a message to this process
    #[wasm_bindgen]
    pub fn send(&self, message: JsValue) {
        let mut mailbox = self.mailbox.write();
        mailbox.push(message);
        console_log!("Message sent to process {}", self.id);
    }

    /// Receive a message from the mailbox
    #[wasm_bindgen]
    pub fn receive(&self) -> Option<JsValue> {
        let mut mailbox = self.mailbox.write();
        mailbox.pop()
    }

    /// Link this process to another
    #[wasm_bindgen]
    pub fn link(&self, other: &ElixirProcess) {
        self.linked_processes.insert(other.id.clone(), other.clone());
        other.linked_processes.insert(self.id.clone(), self.clone());
        console_log!("Linked processes {} and {}", self.id, other.id);
    }

    /// Get process ID
    #[wasm_bindgen(getter)]
    pub fn pid(&self) -> String {
        self.id.clone()
    }

    /// Check if mailbox has messages
    #[wasm_bindgen]
    pub fn has_messages(&self) -> bool {
        !self.mailbox.read().is_empty()
    }
}

/// Phoenix Channel implementation for WASM
#[wasm_bindgen]
pub struct PhoenixChannel {
    topic: String,
    params: JsValue,
    socket: Option<web_sys::WebSocket>,
    callbacks: Arc<DashMap<String, js_sys::Function>>,
    ref_counter: Arc<RwLock<u64>>,
}

#[wasm_bindgen]
impl PhoenixChannel {
    /// Create a new Phoenix channel
    #[wasm_bindgen(constructor)]
    pub fn new(topic: String, params: JsValue) -> Self {
        console_log!("Creating Phoenix channel for topic: {}", topic);

        Self {
            topic,
            params,
            socket: None,
            callbacks: Arc::new(DashMap::new()),
            ref_counter: Arc::new(RwLock::new(0)),
        }
    }

    /// Connect to Phoenix WebSocket
    #[wasm_bindgen]
    pub async fn connect(&mut self, endpoint: &str) -> Result<(), JsValue> {
        use web_sys::{WebSocket, MessageEvent, ErrorEvent};
        use wasm_bindgen::JsCast;
        use wasm_bindgen_futures::spawn_local;

        let ws = WebSocket::new(endpoint)?;

        // Set up WebSocket callbacks
        let onmessage_callback = Closure::<dyn FnMut(_)>::new(move |e: MessageEvent| {
            if let Ok(txt) = e.data().dyn_into::<js_sys::JsString>() {
                console_log!("Received: {}", String::from(txt));
            }
        });

        let onerror_callback = Closure::<dyn FnMut(_)>::new(move |e: ErrorEvent| {
            console_log!("WebSocket error: {}", e.message());
        });

        let onopen_callback = Closure::<dyn FnMut()>::new(move || {
            console_log!("WebSocket connected");
        });

        ws.set_onmessage(Some(onmessage_callback.as_ref().unchecked_ref()));
        ws.set_onerror(Some(onerror_callback.as_ref().unchecked_ref()));
        ws.set_onopen(Some(onopen_callback.as_ref().unchecked_ref()));

        onmessage_callback.forget();
        onerror_callback.forget();
        onopen_callback.forget();

        self.socket = Some(ws);
        Ok(())
    }

    /// Join the channel
    #[wasm_bindgen]
    pub fn join(&self) -> Result<JsValue, JsValue> {
        if let Some(socket) = &self.socket {
            let msg = serde_json::json!({
                "topic": self.topic,
                "event": "phx_join",
                "payload": {},
                "ref": self.make_ref()
            });

            let msg_str = serde_json::to_string(&msg)
                .map_err(|e| JsValue::from_str(&e.to_string()))?;

            socket.send_with_str(&msg_str)?;
            Ok(JsValue::TRUE)
        } else {
            Err(JsValue::from_str("Not connected"))
        }
    }

    /// Push a message to the channel
    #[wasm_bindgen]
    pub fn push(&self, event: &str, payload: JsValue) -> Result<(), JsValue> {
        if let Some(socket) = &self.socket {
            let msg = serde_json::json!({
                "topic": self.topic,
                "event": event,
                "payload": payload,
                "ref": self.make_ref()
            });

            let msg_str = serde_json::to_string(&msg)
                .map_err(|e| JsValue::from_str(&e.to_string()))?;

            socket.send_with_str(&msg_str)?;
            Ok(())
        } else {
            Err(JsValue::from_str("Not connected"))
        }
    }

    /// Register a callback for an event
    #[wasm_bindgen]
    pub fn on(&self, event: String, callback: js_sys::Function) {
        self.callbacks.insert(event.clone(), callback);
        console_log!("Registered callback for event: {}", event);
    }

    fn make_ref(&self) -> u64 {
        let mut counter = self.ref_counter.write();
        *counter += 1;
        *counter
    }
}

/// WebAssembly Runtime Manager
#[wasm_bindgen]
pub struct WasmRuntime {
    processes: Arc<DashMap<String, ElixirProcess>>,
    channels: Arc<DashMap<String, PhoenixChannel>>,
    memory_pool: Arc<RwLock<MemoryPool>>,
}

struct MemoryPool {
    allocated: usize,
    max_size: usize,
    chunks: Vec<MemoryChunk>,
}

struct MemoryChunk {
    ptr: *mut u8,
    size: usize,
    in_use: bool,
}

#[wasm_bindgen]
impl WasmRuntime {
    /// Initialize the WASM runtime
    #[wasm_bindgen(constructor)]
    pub fn new(max_memory_mb: usize) -> Self {
        console_log!("Initializing WASM runtime with {}MB max memory", max_memory_mb);

        Self {
            processes: Arc::new(DashMap::new()),
            channels: Arc::new(DashMap::new()),
            memory_pool: Arc::new(RwLock::new(MemoryPool {
                allocated: 0,
                max_size: max_memory_mb * 1024 * 1024,
                chunks: Vec::new(),
            })),
        }
    }

    /// Spawn a new Elixir process
    #[wasm_bindgen]
    pub fn spawn_process(&self) -> ElixirProcess {
        let process = ElixirProcess::new();
        self.processes.insert(process.id.clone(), process.clone());
        process
    }

    /// Create a new Phoenix channel
    #[wasm_bindgen]
    pub fn create_channel(&self, topic: String, params: JsValue) -> PhoenixChannel {
        let channel = PhoenixChannel::new(topic.clone(), params);
        self.channels.insert(topic, channel.clone());
        channel
    }

    /// Get runtime statistics
    #[wasm_bindgen]
    pub fn stats(&self) -> JsValue {
        let stats = serde_json::json!({
            "processes": self.processes.len(),
            "channels": self.channels.len(),
            "memory": {
                "allocated": self.memory_pool.read().allocated,
                "max": self.memory_pool.read().max_size,
            }
        });

        serde_wasm_bindgen::to_value(&stats).unwrap_or(JsValue::NULL)
    }

    /// Garbage collect unused processes
    #[wasm_bindgen]
    pub fn gc(&self) {
        console_log!("Running garbage collection...");

        let mut to_remove = Vec::new();

        for entry in self.processes.iter() {
            let state = entry.value().state.read();
            if matches!(state.status, ProcessStatus::Exiting) {
                to_remove.push(entry.key().clone());
            }
        }

        for key in to_remove {
            self.processes.remove(&key);
            console_log!("Removed process: {}", key);
        }
    }
}

/// Initialize the WASM module
#[wasm_bindgen(start)]
pub fn init() {
    #[cfg(feature = "console_error_panic_hook")]
    console_error_panic_hook::set_once();

    #[cfg(feature = "console_log")]
    console_log::init_with_level(log::Level::Debug).unwrap_or(());

    console_log!("Elixir WASM Runtime initialized");
}

/// Export version information
#[wasm_bindgen]
pub fn version() -> String {
    env!("CARGO_PKG_VERSION").to_string()
}

/// Advanced memory operations
#[wasm_bindgen]
pub fn allocate(size: usize) -> *mut u8 {
    let mut vec = Vec::with_capacity(size);
    let ptr = vec.as_mut_ptr();
    std::mem::forget(vec);
    ptr
}

#[wasm_bindgen]
pub fn deallocate(ptr: *mut u8, size: usize) {
    unsafe {
        let _ = Vec::from_raw_parts(ptr, size, size);
    }
}

#[cfg(test)]
mod tests {
    use super::*;
    use wasm_bindgen_test::*;

    #[wasm_bindgen_test]
    fn test_process_creation() {
        let process = ElixirProcess::new();
        assert!(!process.pid().is_empty());
    }

    #[wasm_bindgen_test]
    fn test_message_passing() {
        let process = ElixirProcess::new();
        process.send(JsValue::from_str("test"));
        assert!(process.has_messages());

        let msg = process.receive();
        assert!(msg.is_some());
    }
}

