/**
 * Elixir WASM Runtime for Deno
 * High-performance WebAssembly runtime with Elixir/Phoenix integration
 */

import { instantiate } from "./pkg/elixir_wasm_runtime.js"

export interface ElixirProcessOptions {
  priority?: "low" | "normal" | "high" | "max"
  reductions?: number
  memory?: number
}

export interface PhoenixChannelOptions {
  params?: Record<string, any>
  timeout?: number
  rejoinAfterMs?: number
  reconnectAfterMs?: number
}

export interface RuntimeConfig {
  maxMemoryMb?: number
  gcInterval?: number
  processLimit?: number
  channelLimit?: number
}

/**
 * Elixir Process wrapper for TypeScript
 */
export class ElixirProcess {
  private process: any

  constructor(runtime: ElixirWasmRuntime) {
    this.process = runtime.spawnProcess()
  }

  get pid(): string {
    return this.process.pid
  }

  send(message: any): void {
    this.process.send(message)
  }

  receive(): any {
    return this.process.receive()
  }

  async receiveAsync(timeout?: number): Promise<any> {
    const startTime = Date.now()
    const checkInterval = 10 // ms

    while (true) {
      const msg = this.receive()
      if (msg !== undefined) {
        return msg
      }

      if (timeout && Date.now() - startTime > timeout) {
        throw new Error("Receive timeout")
      }

      await new Promise(resolve => setTimeout(resolve, checkInterval))
    }
  }

  link(other: ElixirProcess): void {
    this.process.link(other.process)
  }

  hasMessages(): boolean {
    return this.process.has_messages()
  }
}

/**
 * Phoenix Channel wrapper for TypeScript
 */
export class PhoenixChannel {
  private channel: any
  private callbacks: Map<string, Function> = new Map()

  constructor(
    private runtime: ElixirWasmRuntime,
    public topic: string,
    options?: PhoenixChannelOptions
  ) {
    this.channel = runtime.createChannel(topic, options?.params || {})
  }

  async connect(endpoint: string): Promise<void> {
    await this.channel.connect(endpoint)
  }

  async join(): Promise<void> {
    return this.channel.join()
  }

  push(event: string, payload: any): void {
    this.channel.push(event, payload)
  }

  on(event: string, callback: (payload: any) => void): void {
    this.callbacks.set(event, callback)
    this.channel.on(event, callback)
  }

  off(event: string): void {
    this.callbacks.delete(event)
  }
}

/**
 * Main Elixir WASM Runtime class
 */
export class ElixirWasmRuntime {
  private runtime: any
  private processes: Map<string, ElixirProcess> = new Map()
  private channels: Map<string, PhoenixChannel> = new Map()
  private gcTimer?: number

  constructor(private config: RuntimeConfig = {}) {
    this.config = {
      maxMemoryMb: 256,
      gcInterval: 60000, // 1 minute
      processLimit: 10000,
      channelLimit: 1000,
      ...config
    }
  }

  /**
   * Initialize the runtime with WASM module
   */
  async init(wasmPath?: string): Promise<void> {
    const wasmModule = wasmPath 
      ? await Deno.readFile(wasmPath)
      : await this.fetchDefaultWasm()

    const { WasmRuntime } = await instantiate(wasmModule)
    this.runtime = new WasmRuntime(this.config.maxMemoryMb!)

    // Start garbage collection timer
    if (this.config.gcInterval! > 0) {
      this.gcTimer = setInterval(() => {
        this.gc()
      }, this.config.gcInterval!)
    }
  }

  /**
   * Spawn a new Elixir process
   */
  spawn(fn?: (process: ElixirProcess) => void): ElixirProcess {
    if (this.processes.size >= this.config.processLimit!) {
      throw new Error(`Process limit (${this.config.processLimit}) reached`)
    }

    const process = new ElixirProcess(this)
    this.processes.set(process.pid, process)

    if (fn) {
      fn(process)
    }

    return process
  }

  /**
   * Create a Phoenix channel
   */
  channel(topic: string, options?: PhoenixChannelOptions): PhoenixChannel {
    if (this.channels.size >= this.config.channelLimit!) {
      throw new Error(`Channel limit (${this.config.channelLimit}) reached`)
    }

    const channel = new PhoenixChannel(this, topic, options)
    this.channels.set(topic, channel)
    return channel
  }

  /**
   * Internal: Spawn process (called by ElixirProcess constructor)
   */
  spawnProcess(): any {
    return this.runtime.spawn_process()
  }

  /**
   * Internal: Create channel (called by PhoenixChannel constructor)
   */
  createChannel(topic: string, params: any): any {
    return this.runtime.create_channel(topic, params)
  }

  /**
   * Get runtime statistics
   */
  stats(): any {
    return this.runtime.stats()
  }

  /**
   * Run garbage collection
   */
  gc(): void {
    this.runtime.gc()

    // Clean up local references
    for (const [pid, process] of this.processes.entries()) {
      if (!process.hasMessages()) {
        // Check if process is still active
        // Remove if inactive
      }
    }
  }

  /**
   * Shutdown the runtime
   */
  shutdown(): void {
    if (this.gcTimer) {
      clearInterval(this.gcTimer)
    }

    this.processes.clear()
    this.channels.clear()
  }

  /**
   * Fetch default WASM module
   */
  private async fetchDefaultWasm(): Promise<Uint8Array> {
    const response = await fetch(
      new URL("./pkg/elixir_wasm_runtime_bg.wasm", import.meta.url)
    )
    return new Uint8Array(await response.arrayBuffer())
  }
}

/**
 * GenServer-like behavior for processes
 */
export abstract class GenServer {
  protected process: ElixirProcess

  constructor(protected runtime: ElixirWasmRuntime) {
    this.process = runtime.spawn()
    this.start()
  }

  private async start(): Promise<void> {
    await this.init()
    this.loop()
  }

  private async loop(): Promise<void> {
    while (true) {
      try {
        const message = await this.process.receiveAsync(1000)
        if (message) {
          await this.handleMessage(message)
        }
      } catch (e) {
        // Timeout - continue loop
      }
    }
  }

  abstract init(): Promise<void>
  abstract handleMessage(message: any): Promise<void>

  cast(message: any): void {
    this.process.send({ type: "cast", payload: message })
  }

  call(message: any): Promise<any> {
    return new Promise((resolve) => {
      const ref = Math.random().toString(36)
      this.process.send({ type: "call", payload: message, ref })

      // Set up response handler
      const checkResponse = setInterval(() => {
        const msg = this.process.receive()
        if (msg && msg.ref === ref) {
          clearInterval(checkResponse)
          resolve(msg.response)
        }
      }, 10)
    })
  }
}

/**
 * Supervisor-like behavior for process supervision
 */
export class Supervisor {
  private children: Map<string, ElixirProcess> = new Map()

  constructor(
    private runtime: ElixirWasmRuntime,
    private strategy: "one_for_one" | "one_for_all" | "rest_for_one" = "one_for_one"
  ) {}

  /**
   * Start a child process under supervision
   */
  startChild(
    id: string,
    startFn: () => ElixirProcess,
    restart: "permanent" | "temporary" | "transient" = "permanent"
  ): ElixirProcess {
    const process = startFn()
    this.children.set(id, process)

    // Monitor process and restart if needed
    this.monitor(id, process, restart)

    return process
  }

  private monitor(
    id: string,
    process: ElixirProcess,
    restart: "permanent" | "temporary" | "transient"
  ): void {
    // Implementation would monitor process health
    // and restart according to strategy
  }

  /**
   * Stop a child process
   */
  stopChild(id: string): void {
    this.children.delete(id)
  }

  /**
   * Restart all children
   */
  restartAll(): void {
    // Implementation based on strategy
  }
}

// Export factory function
export async function createElixirRuntime(
  config?: RuntimeConfig
): Promise<ElixirWasmRuntime> {
  const runtime = new ElixirWasmRuntime(config)
  await runtime.init()
  return runtime
}

