/**
 * Deno Deploy Entrypoint
 * Main entry for Deno Deploy with WASM runtime support
 */

import { serve } from "https://deno.land/std@0.224.0/http/server.ts"
import { ElixirWasmRuntime, createElixirRuntime } from "@packages/elixir-wasm-runtime/mod.ts"
import { WasmerRuntime } from "@packages/wasmer-runtime/mod.ts"

// Initialize runtimes
let elixirRuntime: ElixirWasmRuntime
let wasmerRuntime: WasmerRuntime

/**
 * Initialize WASM runtimes
 */
async function initializeRuntimes() {
  console.log("Initializing WASM runtimes...")

  // Initialize Elixir WASM runtime
  elixirRuntime = await createElixirRuntime({
    maxMemoryMb: 512,
    gcInterval: 30000,
    processLimit: 5000,
    channelLimit: 500
  })

  // Initialize Wasmer runtime for general WASM
  wasmerRuntime = new WasmerRuntime({
    memory: {
      initial: 256,
      maximum: 4096,
      shared: true
    }
  })

  console.log("WASM runtimes initialized successfully")
}

/**
 * Handle HTTP requests
 */
async function handler(req: Request): Promise<Response> {
  const url = new URL(req.url)

  // Health check endpoint
  if (url.pathname === "/health") {
    return new Response(JSON.stringify({
      status: "healthy",
      runtimes: {
        elixir: elixirRuntime ? "initialized" : "not initialized",
        wasmer: wasmerRuntime ? "initialized" : "not initialized"
      },
      stats: elixirRuntime?.stats()
    }), {
      headers: { "content-type": "application/json" }
    })
  }

  // WebSocket endpoint for Phoenix channels
  if (url.pathname === "/socket" && req.headers.get("upgrade") === "websocket") {
    const { socket, response } = Deno.upgradeWebSocket(req)

    socket.onopen = () => {
      console.log("WebSocket connection opened")
    }

    socket.onmessage = async (event) => {
      try {
        const data = JSON.parse(event.data)

        // Handle Phoenix channel messages
        if (data.topic && data.event) {
          const channel = elixirRuntime.channel(data.topic)
          await channel.connect(url.origin + "/socket")
          channel.push(data.event, data.payload)
        }
      } catch (error) {
        console.error("WebSocket message error:", error)
      }
    }

    socket.onclose = () => {
      console.log("WebSocket connection closed")
    }

    return response
  }

  // API endpoint for running WASM functions
  if (url.pathname.startsWith("/api/wasm/")) {
    const functionName = url.pathname.replace("/api/wasm/", "")

    try {
      const body = await req.json()

      // Execute WASM function
      const result = await executeWasmFunction(functionName, body)

      return new Response(JSON.stringify({
        success: true,
        result
      }), {
        headers: { "content-type": "application/json" }
      })
    } catch (error) {
      return new Response(JSON.stringify({
        success: false,
        error: error.message
      }), {
        status: 500,
        headers: { "content-type": "application/json" }
      })
    }
  }

  // Spawn Elixir process endpoint
  if (url.pathname === "/api/spawn" && req.method === "POST") {
    try {
      const { code, args } = await req.json()

      const process = elixirRuntime.spawn((proc) => {
        // Execute user code in process context
        proc.send({ type: "execute", code, args })
      })

      return new Response(JSON.stringify({
        success: true,
        pid: process.pid
      }), {
        headers: { "content-type": "application/json" }
      })
    } catch (error) {
      return new Response(JSON.stringify({
        success: false,
        error: error.message
      }), {
        status: 500,
        headers: { "content-type": "application/json" }
      })
    }
  }

  // Message passing endpoint
  if (url.pathname === "/api/send" && req.method === "POST") {
    try {
      const { pid, message } = await req.json()

      // Send message to process
      // Note: In real implementation, we'd need to track processes by PID

      return new Response(JSON.stringify({
        success: true,
        message: "Message sent"
      }), {
        headers: { "content-type": "application/json" }
      })
    } catch (error) {
      return new Response(JSON.stringify({
        success: false,
        error: error.message
      }), {
        status: 500,
        headers: { "content-type": "application/json" }
      })
    }
  }

  // Default response
  return new Response(`
    <!DOCTYPE html>
    <html>
      <head>
        <title>Autonogrammer AI - Deno Deploy</title>
        <style>
          body {
            font-family: system-ui, -apple-system, sans-serif;
            max-width: 800px;
            margin: 50px auto;
            padding: 20px;
            background: #0a0a0a;
            color: #e0e0e0;
          }
          h1 { color: #4ade80; }
          .endpoints {
            background: #1a1a1a;
            padding: 20px;
            border-radius: 8px;
            margin-top: 20px;
          }
          .endpoint {
            margin: 10px 0;
            padding: 10px;
            background: #262626;
            border-radius: 4px;
          }
          code {
            background: #333;
            padding: 2px 6px;
            border-radius: 3px;
            color: #4ade80;
          }
        </style>
      </head>
      <body>
        <h1> Autonogrammer AI - Deno Deploy</h1>
        <p>WebAssembly Runtime with Elixir/Phoenix Support</p>

        <div class="endpoints">
          <h2>Available Endpoints:</h2>

          <div class="endpoint">
            <strong>GET /health</strong>
            <p>Health check and runtime statistics</p>
          </div>

          <div class="endpoint">
            <strong>WS /socket</strong>
            <p>WebSocket endpoint for Phoenix channels</p>
          </div>

          <div class="endpoint">
            <strong>POST /api/spawn</strong>
            <p>Spawn a new Elixir process</p>
            <code>{"code": "...", "args": {...}}</code>
          </div>

          <div class="endpoint">
            <strong>POST /api/send</strong>
            <p>Send message to process</p>
            <code>{"pid": "...", "message": {...}}</code>
          </div>

          <div class="endpoint">
            <strong>POST /api/wasm/:function</strong>
            <p>Execute WASM function</p>
          </div>
        </div>

        <div class="endpoints">
          <h2>Runtime Status:</h2>
          <pre id="status">Loading...</pre>
        </div>

        <script>
          async function updateStatus() {
            const res = await fetch('/health');
            const data = await res.json();
            document.getElementById('status').textContent = JSON.stringify(data, null, 2);
          }

          updateStatus();
          setInterval(updateStatus, 5000);
        </script>
      </body>
    </html>
  `, {
    headers: { "content-type": "text/html" }
  })
}

/**
 * Execute a WASM function
 */
async function executeWasmFunction(name: string, args: any): Promise<any> {
  // This would execute functions from loaded WASM modules
  // Implementation depends on specific WASM modules loaded

  if (name === "test") {
    return { message: "Test function executed", args }
  }

  throw new Error(`Unknown function: ${name}`)
}

// Initialize runtimes on startup
await initializeRuntimes()

// Start the server
console.log("Starting server on http://localhost:8000")
await serve(handler, { port: 8000 })

