.PHONY: help install build test deploy clean dev wasm

# Default target
help:
	@echo "Autonogrammer AI - Deno + WASM Build System"
	@echo ""
	@echo "Available targets:"
	@echo "  make install    - Install dependencies and setup environment"
	@echo "  make build      - Build all modules including WASM"
	@echo "  make wasm       - Build only WASM modules"
	@echo "  make test       - Run all tests"
	@echo "  make dev        - Start development server"
	@echo "  make deploy     - Deploy to Deno Deploy"
	@echo "  make clean      - Clean build artifacts"
	@echo ""

# Install dependencies
install:
	@echo " Installing dependencies..."
	@deno cache --reload src/entrypoints/deno-cli.tsx
	@echo " Installing Rust toolchain..."
	@rustup target add wasm32-unknown-unknown
	@cargo install wasm-pack wasm-bindgen-cli wasm-opt
	@echo " Installation complete"

# Build everything
build: wasm
	@echo " Building Deno application..."
	@deno task build
	@echo " Build complete"

# Build WASM modules only
wasm:
	@echo " Building WASM modules..."
	@./scripts/build-wasm.sh
	@echo " WASM build complete"

# Run tests
test:
	@echo " Running tests..."
	@echo "Testing Deno modules..."
	@deno test --allow-all
	@echo "Testing Rust modules..."
	@cd packages/elixir-wasm-runtime && cargo test
	@cd packages/wasmer-runtime && cargo test
	@echo " All tests passed"

# Development server
dev:
	@echo " Starting development server..."
	@deno task dev

# Deploy to Deno Deploy
deploy: build
	@echo " Deploying to Deno Deploy..."
	@deployctl deploy \
		--project=autonogrammer-ai \
		--entrypoint=src/entrypoints/deno-deploy.ts \
		--include=src,packages,import_map.json,deno.json \
		--exclude=node_modules,target,pkg
	@echo " Deployment complete"

# Clean build artifacts
clean:
	@echo " Cleaning build artifacts..."
	@rm -rf packages/*/target
	@rm -rf packages/*/pkg
	@rm -rf .deno_cache
	@rm -rf dist
	@rm -f cli.js
	@rm -f wasm-manifest.json
	@echo " Clean complete"

# Development with hot reload
watch:
	@echo " Starting development with hot reload..."
	@deno task dev --watch

# Lint code
lint:
	@echo " Linting code..."
	@biome check --apply src/
	@deno lint src/
	@cargo clippy --all-targets --all-features -- -D warnings
	@echo " Linting complete"

# Format code
format:
	@echo " Formatting code..."
	@biome format --write src/
	@deno fmt
	@cargo fmt --all
	@echo " Formatting complete"

# Check types
typecheck:
	@echo " Type checking..."
	@deno task typecheck
	@cargo check --all
	@echo " Type checking complete"

# Build for production
production: clean
	@echo " Building for production..."
	@NODE_ENV=production make build
	@echo " Build sizes:"
	@ls -lh packages/*/pkg/*.wasm
	@echo " Production build complete"

# Run benchmarks
bench:
	@echo " Running benchmarks..."
	@deno bench --allow-all
	@cargo bench
	@echo " Benchmarks complete"

# Update dependencies
update:
	@echo " Updating dependencies..."
	@deno cache --reload src/entrypoints/deno-cli.tsx
	@cargo update
	@echo " Dependencies updated"

# Docker build (if needed)
docker:
	@echo " Building Docker image..."
	@docker build -t autonogrammer-ai:latest .
	@echo " Docker build complete"

# CI/CD pipeline
ci: install lint typecheck test build
	@echo " CI pipeline complete"

