     1/**
     2 * XRCanvas - Core WebXR Canvas Component
     3 *
     4 * Provides the foundational WebXR context and rendering environment
     5 * for immersive VR/AR experiences across all supported platforms
     6 */
     7
     8import { AdaptiveDpr, AdaptiveEvents, Environment, Stats } from '@react-three/drei';
     9import { Canvas, type CanvasProps } from '@react-three/fiber';
    10import { XR, XRStore, createXRStore } from '@react-three/xr';
    11import type React from 'react';
    12import { Suspense, useEffect, useMemo } from 'react';
    13import { ErrorBoundary } from 'react-error-boundary';
    14import { XRPerformanceMonitor } from '../optimization/PerformanceMonitor';
    15import { useXRStore } from '../stores/xr-store';
    16import { SpatialAudioProvider } from './SpatialAudio';
    17
    18export interface XRCanvasProps extends Omit<CanvasProps, 'children'> {
    19  children: React.ReactNode;
    20
    21  // XR Configuration
    22  enableHandTracking?: boolean;
    23  enableEyeTracking?: boolean;
    24  enablePassthrough?: boolean;
    25  frameRate?: 90 | 120 | 72;
    26  foveatedRendering?: boolean;
    27
    28  // Platform Detection
    29  platform?: 'auto' | 'quest' | 'visionpro' | 'web';
    30  fallbackMode?: '2d' | 'disabled';
    31
    32  // Performance Settings
    33  adaptiveQuality?: boolean;
    34  targetFPS?: number;
    35  enableLOD?: boolean;
    36  enableCulling?: boolean;
    37
    38  // Audio
    39  spatialAudio?: boolean;
    40  audioContext?: AudioContext;
    41
    42  // Development
    43  showStats?: boolean;
    44  showInspector?: boolean;
    45  enableDebugMode?: boolean;
    46
    47  // Event Handlers
    48  onXRSessionStart?: (session: XRSession) => void;
    49  onXRSessionEnd?: () => void;
    50  onPerformanceChange?: (metrics: PerformanceMetrics) => void;
    51}
    52
    53interface PerformanceMetrics {
    54  fps: number;
    55  frameTime: number;
    56  memoryUsage: number;
    57  drawCalls: number;
    58}
    59
    60export const XRCanvas: React.FC<XRCanvasProps> = ({
    61  children,
    62  enableHandTracking = true,
    63  enableEyeTracking = false,
    64  enablePassthrough = false,
    65  frameRate = 90,
    66  foveatedRendering = true,
    67  platform = 'auto',
    68  fallbackMode = '2d',
    69  adaptiveQuality = true,
    70  targetFPS = 90,
    71  enableLOD = true,
    72  enableCulling = true,
    73  spatialAudio = true,
    74  showStats = false,
    75  showInspector = false,
    76  enableDebugMode = false,
    77  onXRSessionStart,
    78  onXRSessionEnd,
    79  onPerformanceChange,
    80  ...canvasProps
    81}) => {
    82  const { enterXR, exitXR, updateSpatialMode } = useXRStore();
    83
    84  // Create XR store with platform-specific configuration
    85  const xrStore = useMemo(() => {
    86    const features: XRSessionInit['requiredFeatures'] = [];
    87    const optionalFeatures: XRSessionInit['optionalFeatures'] = [];
    88
    89    if (enableHandTracking) {
    90      optionalFeatures.push('hand-tracking');
    91    }
    92
    93    if (enableEyeTracking) {
    94      optionalFeatures.push('eye-tracking');
    95    }
    96
    97    if (enablePassthrough) {
    98      optionalFeatures.push('camera-access');
    99    }
   100
   101    return createXRStore({
   102      frameRate,
   103      foveation: foveatedRendering ? 1 : 0,
   104      referenceSpace: 'local-floor',
   105      sessionInit: {
   106        optionalFeatures,
   107        requiredFeatures: features,
   108      },
   109    });
   110  }, [enableHandTracking, enableEyeTracking, enablePassthrough, frameRate, foveatedRendering]);
   111
   112  // Platform detection and optimization
   113  const platformConfig = useMemo(() => {
   114    if (platform !== 'auto') return { platform };
   115
   116    // Auto-detect platform based on user agent
   117    const userAgent = navigator.userAgent.toLowerCase();
   118
   119    if (userAgent.includes('quest')) {
   120      return { platform: 'quest', optimizations: ['aggressive-batching', 'reduced-effects'] };
   121    }
   122
   123    if (userAgent.includes('visionos')) {
   124      return { platform: 'visionpro', optimizations: ['high-fidelity', 'spatial-computing'] };
   125    }
   126
   127    return { platform: 'web', optimizations: ['adaptive-quality', 'fallback-ready'] };
   128  }, [platform]);
   129
   130  // XR session event handlers
   131  useEffect(() => {
   132    const handleSessionStart = (session: XRSession) => {
   133      enterXR(platformConfig.platform);
   134      updateSpatialMode('3d');
   135      onXRSessionStart?.(session);
   136    };
   137
   138    const handleSessionEnd = () => {
   139      exitXR();
   140      updateSpatialMode('2d');
   141      onXRSessionEnd?.();
   142    };
   143
   144    // Subscribe to XR store events
   145    const unsubscribe = xrStore.subscribe(
   146      (state) => state.session,
   147      (session) => {
   148        if (session) {
   149          handleSessionStart(session);
   150        } else {
   151          handleSessionEnd();
   152        }
   153      }
   154    );
   155
   156    return unsubscribe;
   157  }, [
   158    xrStore,
   159    enterXR,
   160    exitXR,
   161    updateSpatialMode,
   162    onXRSessionStart,
   163    onXRSessionEnd,
   164    platformConfig.platform,
   165  ]);
   166
   167  // Error fallback component
   168  const XRErrorFallback = ({ error, resetErrorBoundary }: any) => (
   169    <div className="xr-error-fallback p-4 bg-red-100 border border-red-400 rounded">
   170      <h2 className="text-lg font-bold text-red-800 mb-2">XR Error</h2>
   171      <p className="text-red-700 mb-4">{error.message}</p>
   172      <button
   173        onClick={resetErrorBoundary}
   174        className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700"
   175      >
   176        Retry XR Session
   177      </button>
   178    </div>
   179  );
   180
   181  // Performance monitoring callback
   182  const handlePerformanceUpdate = (metrics: PerformanceMetrics) => {
   183    onPerformanceChange?.(metrics);
   184
   185    // Auto-adjust quality based on performance
   186    if (adaptiveQuality && metrics.fps < targetFPS * 0.8) {
   187      // Reduce quality to maintain target FPS
   188      console.log('XR: Reducing quality to maintain performance');
   189    }
   190  };
   191
   192  return (
   193    <ErrorBoundary FallbackComponent={XRErrorFallback}>
   194      <Canvas
   195        {...canvasProps}
   196        gl={{
   197          antialias: true,
   198          alpha: true,
   199          powerPreference: 'high-performance',
   200          failIfMajorPerformanceCaveat: false,
   201        }}
   202        camera={{
   203          position: [0, 1.6, 3],
   204          fov: 50,
   205          near: 0.1,
   206          far: 1000,
   207        }}
   208        dpr={adaptiveQuality ? [1, 2] : 1}
   209      >
   210        <XR store={xrStore}>
   211          {/* Adaptive Performance Components */}
   212          {adaptiveQuality && <AdaptiveDpr pixelated />}
   213          {adaptiveQuality && <AdaptiveEvents />}
   214
   215          {/* Performance Monitoring */}
   216          <XRPerformanceMonitor
   217            onUpdate={handlePerformanceUpdate}
   218            targetFPS={targetFPS}
   219            enabled={enableDebugMode}
   220          />
   221
   222          {/* Spatial Audio Context */}
   223          {spatialAudio && (
   224            <SpatialAudioProvider>
   225              {/* Audio will be handled by child components */}
   226            </SpatialAudioProvider>
   227          )}
   228
   229          {/* Default Lighting */}
   230          <ambientLight intensity={0.4} />
   231          <directionalLight
   232            position={[5, 5, 5]}
   233            intensity={0.6}
   234            castShadow
   235            shadow-mapSize-width={2048}
   236            shadow-mapSize-height={2048}
   237          />
   238
   239          {/* Environment */}
   240          <Environment
   241            preset={platformConfig.platform === 'visionpro' ? 'studio' : 'city'}
   242            background={!enablePassthrough}
   243          />
   244
   245          {/* XR Content */}
   246          <Suspense fallback={<XRLoadingFallback />}>{children}</Suspense>
   247
   248          {/* Development Tools */}
   249          {showStats && <Stats />}
   250
   251          {/* Platform-Specific Optimizations */}
   252          <PlatformOptimizations platform={platformConfig.platform} />
   253        </XR>
   254      </Canvas>
   255    </ErrorBoundary>
   256  );
   257};
