      displayName: 'Autonogrammer AI',
      version: '2.0.0',
      buildNumber: 200,
      minimumOSVersion: '13.0',
      capabilities: ['push-notifications', 'camera', 'microphone']
    },
    android: {
      packageName: 'com.autonogrammer.ai',
