    type: 'primjs', // TikTok's optimized QuickJS-based engine
    options: {
      enableJIT: true,
      memoryLimit: '512MB',
      gcThreshold: '256MB',
      asyncSupport: true
    }
  },

  // Performance optimizations
  performance: {
    enableRustOptimization: true,
    dualThreading: true,
    lazyLoading: true,
    bundleSplitting: true,
    imageOptimization: {
      webp: true,
      lazy: true,
      placeholder: 'blur'
    },
    codeOptimization: {
