    preprocessor: 'postcss',
    features: {
      animations: true,
      transitions: true,
      flexbox: true,
      grid: true,
      customProperties: true
    },
    tailwind: {
      enabled: true,
      config: './tailwind.config.js'
    }
  },

  // Module federation for micro-frontends
