    name: 'autonogrammer_ai',
    filename: 'remoteEntry.js',
    exposes: {
      './AIAssistant': './src/components/AIAssistant',
      './ChatInterface': './src/components/ChatInterface',
      './CodeEditor': './src/components/CodeEditor'
    },
    shared: {
      react: { singleton: true, requiredVersion: '^18.3.1' },
      'react-dom': { singleton: true, requiredVersion: '^18.3.1' },
      '@lynx-family/react-lynx': { singleton: true }
    }
  },

  // Development configuration
  development: {
    hot: true,
    sourceMaps: true,
    overlay: true,
    stats: 'minimal',
    proxy: {
      '/api': {
        target: 'http://localhost:8000',
        changeOrigin: true
      }
    }
  },

  // Build configuration
  build: {
    outDir: 'dist',
    assetsDir: 'assets',
    sourcemap: false,
    chunkSizeWarning: 500,
    rollupOptions: {
      output: {
        manualChunks: {
          vendor: ['react', 'react-dom'],
          lynx: ['@lynx-family/core', '@lynx-family/react-lynx'],
          ai: ['openai', '@anthropic-ai/sdk']
        }
      }
    }
  },

  // Plugin system
  plugins: [
    '@lynx-family/plugin-pwa',
    '@lynx-family/plugin-analytics',
    '@lynx-family/plugin-sentry',
    '@lynx-family/plugin-push-notifications'
  ],

  // Native module bindings
  nativeModules: {
    ios: ['./ios/AIAssistantModule'],
    android: ['./android/AIAssistantModule']
  },

  // Environment variables
  env: {
    API_URL: process.env.VITE_API_URL || 'https://api.autonogrammer.ai',
    WS_URL: process.env.VITE_WS_URL || 'wss://ws.autonogrammer.ai',
    SENTRY_DSN: process.env.VITE_SENTRY_DSN
  }
};

export default config;

