import React, { Suspense, lazy } from 'react';
import { 
  LynxProvider, 
  createLynxApp, 
  useLynxNavigation,
  useLynxPlatform,
  useLynxPerformance 
} from '@lynx-family/react-lynx';
import { PrimJS } from '@lynx-family/primjs';
import { AIProvider } from './providers/AIProvider';
import { ThemeProvider } from './providers/ThemeProvider';
import { ErrorBoundary } from './components/ErrorBoundary';
import { LoadingScreen } from './components/LoadingScreen';

// Lazy load heavy components for better performance
const AIAssistant = lazy(() => import('./components/AIAssistant'));
const ChatInterface = lazy(() => import('./components/ChatInterface'));
const CodeEditor = lazy(() => import('./components/CodeEditor'));

// Create Lynx application instance with PrimJS engine
const lynxApp = createLynxApp({
  engine: new PrimJS({
    enableJIT: true,
    asyncSupport: true,
    memoryLimit: '512MB'
  }),

  // Platform-specific configurations
  platforms: {
    ios: {
      bundleId: 'com.autonogrammer.ai',
      statusBar: {
        style: 'light-content',
        backgroundColor: '#0a0a0a'
      }
    },
    android: {
      packageName: 'com.autonogrammer.ai',
      navigationBar: {
        color: '#0a0a0a',
        dividerColor: '#1a1a1a'
      }
    },
    web: {
      publicPath: '/',
      viewport: {
        width: 'device-width',
        initialScale: 1,
        userScalable: false
      }
    }
  },

  // Performance monitoring
  performance: {
    trackRenderTime: true,
    trackNavigationTime: true,
    trackAPILatency: true,
    reportingEndpoint: '/api/performance'
  },

  // Error tracking
  errorBoundary: {
    fallback: ErrorBoundary,
    onError: (error, errorInfo) => {
      console.error('Lynx App Error:', error, errorInfo);
      // Send to error tracking service
    }
  }
});

// Root component
export function App() {
  const platform = useLynxPlatform();
  const navigation = useLynxNavigation();
  const performance = useLynxPerformance();

  // Track app launch performance
  React.useEffect(() => {
    performance.mark('app-interactive');
    const launchTime = performance.measure('launch', 'navigation-start', 'app-interactive');
    console.log(`App launched in ${launchTime}ms on ${platform.name}`);
  }, [performance, platform]);

  return (
    <LynxProvider app={lynxApp}>
      <ThemeProvider>
        <AIProvider>
          <ErrorBoundary>
            <Suspense fallback={<LoadingScreen />}>
              <AppContent platform={platform} />
            </Suspense>
          </ErrorBoundary>
        </AIProvider>
      </ThemeProvider>
    </LynxProvider>
  );
}

// Platform-adaptive content
function AppContent({ platform }: { platform: any }) {
  const navigation = useLynxNavigation();

  // Render different layouts based on platform
  if (platform.type === 'mobile') {
    return <MobileLayout />;
  } else if (platform.type === 'tablet') {
    return <TabletLayout />;
  } else {
    return <WebLayout />;
  }
}

// Mobile-optimized layout
function MobileLayout() {
  return (
    <div className="flex flex-col h-screen bg-gray-900">
      <header className="flex-shrink-0 p-4 bg-gray-800 border-b border-gray-700">
        <h1 className="text-xl font-bold text-white">Autonogrammer AI</h1>
      </header>

      <main className="flex-1 overflow-hidden">
        <ChatInterface fullscreen />
      </main>

