import React, { useState, useRef, useEffect } from 'react';
import { useLynxPlatform, useLynxNavigation, useLynxHaptics } from '@lynx-family/react-lynx';

interface ChatProps {
  fullscreen?: boolean;
}

interface ChatMessage {
  id: string;
  text: string;
  sender: 'user' | 'ai';
  timestamp: Date;
  code?: string;
  language?: string;
}

export function ChatInterface({ fullscreen = false }: ChatProps) {
  const platform = useLynxPlatform();
  const navigation = useLynxNavigation();
  const haptics = useLynxHaptics();

  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const inputRef = useRef<HTMLTextAreaElement>(null);
  const scrollRef = useRef<HTMLDivElement>(null);

  // Auto-scroll to bottom
  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  // Focus input on mount
  useEffect(() => {
    if (platform.type === 'web' && inputRef.current) {
      inputRef.current.focus();
    }
  }, [platform]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    // Haptic feedback on mobile
    if (platform.type === 'mobile') {
      haptics.impact('light');
    }

    const userMessage: ChatMessage = {
      id: `msg-${Date.now()}`,
      text: input,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsTyping(true);

    // Simulate AI response
    setTimeout(() => {
      const aiMessage: ChatMessage = {
        id: `msg-${Date.now() + 1}`,
        text: processUserInput(input),
        sender: 'ai',
        timestamp: new Date(),
        code: extractCode(input),
        language: detectLanguage(input)
      };

      setMessages(prev => [...prev, aiMessage]);
      setIsTyping(false);

      if (platform.type === 'mobile') {
        haptics.notification('success');
      }
    }, 1000 + Math.random() * 1000);
  };

  const processUserInput = (text: string): string => {
    // Simple response generation
    if (text.toLowerCase().includes('hello')) {
      return "Hello! I'm your AI assistant. How can I help you today?";
    }
    if (text.toLowerCase().includes('code')) {
      return "I can help you with coding! What language are you working with?";
    }
    if (text.toLowerCase().includes('debug')) {
      return "Let me help you debug that issue. Can you share the error message?";
    }
    return "I understand. Let me analyze that for you...";
  };

  const extractCode = (text: string): string | undefined => {
    const codeMatch = text.match(/```[\s\S]*?```/);
    if (codeMatch) {
      return codeMatch[0].replace(/```/g, '').trim();
    }
    return undefined;
  };

  const detectLanguage = (text: string): string | undefined => {
    if (text.includes('javascript') || text.includes('js')) return 'javascript';
    if (text.includes('python') || text.includes('py')) return 'python';
    if (text.includes('rust') || text.includes('rs')) return 'rust';
    if (text.includes('typescript') || text.includes('ts')) return 'typescript';
    return undefined;
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const containerClass = fullscreen
    ? 'flex flex-col h-full bg-gray-900'
    : 'flex flex-col h-full bg-gray-850';

  return (
    <div className={containerClass}>
      {/* Messages Area */}
      <div
        ref={scrollRef}
        className="flex-1 overflow-y-auto p-4 space-y-3"
      >
        {messages.length === 0 && (
          <div className="text-center text-gray-500 mt-8">
            <p className="text-lg mb-2">Welcome to Autonogrammer AI Chat</p>
            <p className="text-sm">Start a conversation to begin...</p>
          </div>
