              <div className="flex items-center space-x-2">
                <div className="typing-indicator">
                  <span></span>
                  <span></span>
                  <span></span>
                </div>
                <span className="text-sm text-gray-400">AI is typing...</span>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Input Area */}
