import React, { useState, useRef, useEffect, useCallback } from 'react';
import { useLynxPlatform, useLynxStorage, useLynxPerformance } from '@lynx-family/react-lynx';

interface CodeFile {
  id: string;
  name: string;
  language: string;
  content: string;
  modified: boolean;
}

export function CodeEditor() {
  const platform = useLynxPlatform();
  const storage = useLynxStorage();
  const performance = useLynxPerformance();

  const [files, setFiles] = useState<CodeFile[]>([
    {
      id: 'main',
      name: 'main.ts',
      language: 'typescript',
      content: `// Welcome to Autonogrammer AI Code Editor
// Powered by Lynx Framework

import { createLynxApp } from '@lynx-family/core';

const app = createLynxApp({
  name: 'MyApp',
  version: '1.0.0'
});

app.run();`,
      modified: false
    }
  ]);

  const [activeFileId, setActiveFileId] = useState('main');
  const [fontSize, setFontSize] = useState(14);
  const [theme, setTheme] = useState<'dark' | 'light'>('dark');
  const editorRef = useRef<HTMLTextAreaElement>(null);

  const activeFile = files.find(f => f.id === activeFileId);

  // Load saved files
  useEffect(() => {
    const loadFiles = async () => {
      const savedFiles = await storage.getItem('editor-files');
      if (savedFiles) {
        setFiles(JSON.parse(savedFiles));
      }
    };
    loadFiles();
  }, [storage]);

  // Save files on change
  useEffect(() => {
    const saveFiles = async () => {
      await storage.setItem('editor-files', JSON.stringify(files));
    };
    saveFiles();
  }, [files, storage]);

  const updateFileContent = useCallback((content: string) => {
    performance.mark('editor-update-start');

    setFiles(prev => prev.map(file => 
      file.id === activeFileId
        ? { ...file, content, modified: true }
        : file
    ));

    performance.mark('editor-update-end');
    performance.measure('editor-update', 'editor-update-start', 'editor-update-end');
  }, [activeFileId, performance]);

  const createNewFile = () => {
    const newFile: CodeFile = {
      id: `file-${Date.now()}`,
      name: `untitled-${files.length + 1}.ts`,
      language: 'typescript',
      content: '',
      modified: false
    };
    setFiles(prev => [...prev, newFile]);
    setActiveFileId(newFile.id);
  };

  const deleteFile = (fileId: string) => {
    if (files.length === 1) return; // Keep at least one file

    setFiles(prev => prev.filter(f => f.id !== fileId));
    if (activeFileId === fileId) {
      setActiveFileId(files[0].id);
    }
  };

  const saveFile = (fileId: string) => {
    setFiles(prev => prev.map(file => 
      file.id === fileId
        ? { ...file, modified: false }
        : file
    ));
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    // Handle keyboard shortcuts
    if ((e.ctrlKey || e.metaKey) && e.key === 's') {
      e.preventDefault();
      saveFile(activeFileId);
    }

    // Handle tab key
    if (e.key === 'Tab') {
      e.preventDefault();
      const textarea = editorRef.current;
      if (textarea) {
        const start = textarea.selectionStart;
        const end = textarea.selectionEnd;
        const value = textarea.value;
        const newValue = value.substring(0, start) + '  ' + value.substring(end);
        updateFileContent(newValue);

        // Restore cursor position
        setTimeout(() => {
          textarea.selectionStart = textarea.selectionEnd = start + 2;
        }, 0);
      }
    }
  };

  const getLanguageColor = (language: string) => {
    switch (language) {
      case 'typescript': return 'text-blue-400';
      case 'javascript': return 'text-yellow-400';
      case 'python': return 'text-green-400';
      case 'rust': return 'text-orange-400';
      default: return 'text-gray-400';
    }
  };

  return (
    <div className="flex flex-col h-full bg-gray-900">
      {/* Toolbar */}
      <div className="flex items-center justify-between px-4 py-2 bg-gray-800 border-b border-gray-700">
        <div className="flex items-center gap-2">
          <button
            onClick={createNewFile}
            className="p-1 text-gray-400 hover:text-white"
            title="New File"
          >
            <NewFileIcon />
          </button>
          <button
            onClick={() => saveFile(activeFileId)}
            className="p-1 text-gray-400 hover:text-white"
            title="Save (Ctrl+S)"
          >
            <SaveIcon />
          </button>
          <div className="h-4 w-px bg-gray-600 mx-1" />
          <button
            onClick={() => setFontSize(Math.max(10, fontSize - 2))}
            className="p-1 text-gray-400 hover:text-white"
            title="Decrease Font Size"
          >
            <MinusIcon />
          </button>
          <span className="text-sm text-gray-400 mx-1">{fontSize}px</span>
          <button
            onClick={() => setFontSize(Math.min(24, fontSize + 2))}
            className="p-1 text-gray-400 hover:text-white"
            title="Increase Font Size"
          >
            <PlusIcon />
          </button>
          <div className="h-4 w-px bg-gray-600 mx-1" />
          <button
            onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')}
            className="p-1 text-gray-400 hover:text-white"
            title="Toggle Theme"
          >
            {theme === 'dark' ? <SunIcon /> : <MoonIcon />}
          </button>
        </div>

        {activeFile && (
          <div className="text-sm text-gray-400">
            <span className={getLanguageColor(activeFile.language)}>
              {activeFile.language}
            </span>
            {activeFile.modified && <span className="ml-2 text-yellow-500"></span>}
          </div>
        )}
      </div>

      {/* File Tabs */}
      <div className="flex bg-gray-850 border-b border-gray-700 overflow-x-auto">
        {files.map(file => (
          <div
            key={file.id}
            className={`flex items-center gap-2 px-3 py-2 text-sm cursor-pointer border-r border-gray-700 ${
              file.id === activeFileId
                ? 'bg-gray-900 text-white'
                : 'text-gray-400 hover:text-white hover:bg-gray-800'
            }`}
            onClick={() => setActiveFileId(file.id)}
          >
            <span>{file.name}</span>
            {file.modified && <span className="text-yellow-500"></span>}
            {files.length > 1 && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  deleteFile(file.id);
                }}
                className="ml-1 text-gray-500 hover:text-red-400"
              >

              </button>
            )}
          </div>
        ))}
      </div>

      {/* Editor */}
      {activeFile && (
        <div className="flex-1 relative">
          <textarea
            ref={editorRef}
            value={activeFile.content}
            onChange={(e) => updateFileContent(e.target.value)}
            onKeyDown={handleKeyDown}
            className={`absolute inset-0 w-full h-full p-4 font-mono resize-none focus:outline-none ${
              theme === 'dark'
                ? 'bg-gray-900 text-gray-100'
                : 'bg-white text-gray-900'
            }`}
            style={{ fontSize: `${fontSize}px` }}
            spellCheck={false}
            placeholder="Start typing..."
          />

          {/* Line numbers overlay (simplified) */}
          <div
            className="absolute left-0 top-0 p-4 pointer-events-none select-none"
            style={{ fontSize: `${fontSize}px` }}
          >
            {activeFile.content.split('\n').map((_, index) => (
              <div
                key={index}
                className="text-gray-600 font-mono"
                style={{ height: `${fontSize * 1.5}px` }}
              >
                {index + 1}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Status Bar */}
      <div className="flex items-center justify-between px-4 py-1 bg-gray-800 border-t border-gray-700 text-xs text-gray-400">
        <div className="flex items-center gap-4">
          <span>Lynx Editor</span>
          {activeFile && (
            <>
              <span>Lines: {activeFile.content.split('\n').length}</span>
              <span>Chars: {activeFile.content.length}</span>
            </>
          )}
        </div>
        <div className="flex items-center gap-4">
          <span>Platform: {platform.name}</span>
          <span>UTF-8</span>
        </div>
      </div>
    </div>
  );
}

// Icon Components
function NewFileIcon() {
  return (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
    </svg>
  );
}

function SaveIcon() {
  return (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 7H5a2 2 0 00-2 2v9a2 2 0 002 2h14a2 2 0 002-2V9a2 2 0 00-2-2h-3m-1 4l-3 3m0 0l-3-3m3 3V2" />
    </svg>
  );
}

function MinusIcon() {
  return (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20 12H4" />
    </svg>
  );
}

function PlusIcon() {
  return (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
    </svg>
  );
}

function SunIcon() {
  return (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
    </svg>
  );
}

function MoonIcon() {
  return (
    <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
    </svg>
  );
}

export default CodeEditor;

